_a-0000ebb3-5308-8000-9cab-011c48011c48_.sail_code:
a!localVariables(
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: cons!CMGT_INT_BATCH_SIZE_MEDIUM,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: recordType!CMGT_Document.createdOn,
      ascending: false()
    )
  ),
  local!documents: rule!CMGT_QR_GetDocument(
    caseId: ri!caseId,
    additionalFilters: {
      /*Fetching only the public documents of the case*/
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_Document.isPrivate,
        operator: "=",
        value: false()
      )
    },
    returnType: "DATA_SUBSET",
    triggerRefresh: ri!triggerRefresh,
    pagingInfo: local!pagingInfo,
    executeWhen: a!isNotNullOrEmpty(ri!caseId)
  ),
  #"SYSTEM_SYSRULES_sectionLayout_v1"(
    label: "",
    labelSize: "SMALL",
    labelColor: "STANDARD",
    contents: {
      /*Documents Header*/
      rule!CMGT_UTIL_HeaderField(
        label: #"urn:appian:translation-string:v1:21e05523-30c7-4cb5-b438-242415ba02e3",
        labelColor: ri!brandingMap.StandardTextColor,
        divider: "NONE",
        rightSideContents: {
          /*Action to add documents */
          #"SYSTEM_SYSRULES_recordActionField_v1"(
            actions: #"SYSTEM_SYSRULES_recordActionItem"(
              action: #"urn:appian:record-action:v1:d81cdd0e-c54d-4b8e-beec-b7ae37fefe7f/bbf49e3b-d924-46b4-9e34-cb4275fbaf98",
              identifier: ri!caseId
            ),
            style: "LINKS",
            display: "LABEL_AND_ICON",
            align: "END"
          )
        },
        brandingMap: ri!brandingMap,
        marginBelow: "NONE"
      ),
      /*List of Documents*/
      #"SYSTEM_SYSRULES_forEach"(
        items: local!documents.data,
        expression: rule!CMGT_LA_Document_Sub_DocumentCard(
          document: fv!item,
          brandingMap: ri!brandingMap
        )
      ),
      /*Paging*/
      #"SYSTEM_SYSRULES_sectionLayout_v1"(
        contents: rule!CMGT_UTIL_CustomPaging(
          pagingInfo: local!pagingInfo,
