          local!activeTasks[recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskUuid],
          local!activeTasks,
          null
        ),
        local!taskAfterBranch: displayvalue(
          fv!item[recordType!CMGT_WFL_CFG_TaskBlockTaskBranch.afterTaskBlockTaskUuid],
          local!activeTasks[recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskUuid],
          local!activeTasks,
          null
        ),
        a!map(
          from: local!taskBeforeBranch[recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskUuid],
          to: local!taskAfterBranch[recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskUuid],
          label: rule!CMGT_UTIL_TruncateText(
            text: fv!item[recordType!CMGT_WFL_CFG_TaskBlockTaskBranch.taskResponseOption.label],
            maxLength: 15
          ),
          title: fv!item[recordType!CMGT_WFL_CFG_TaskBlockTaskBranch.taskResponseOption.label],
          color: ri!brandingMap.BlackColor,
          dashes: if(
            fv!item[recordType!CMGT_WFL_CFG_TaskBlockTaskBranch.isRequired],
            false,
            true
          ),
          font: a!map(
            multi: "html",
            align: "horizontal",
            color: ri!brandingMap.BlackColor,
            bold: a!map(mod: "bold", size: 15)
          )
        )
      )
    )
  ),
  /*Build edges for all tasks that connect to their milestone start node*/
  local!milestoneToTaskEdges: #"SYSTEM_SYSRULES_forEach"(
    items: local!activeTasks,
    expression: a!map(
      from: local!startMilestoneReferenceMilestoneId,
      to: fv!item[recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskUuid],
      color: ri!brandingMap.BlackColor,
      /*- Connect to start if starts by default is true*/
      showWhen: fv!item[recordType!CMGT_WFL_CFG_TaskBlockTask.startsByDefault],
      dashes: false,
      font: a!map()
    )
  ),
  /*Build edges for all tasks that connect to the next milestone or the "End" node if in last milestone*/
  local!taskToMilestoneEdges: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!activeTasks,
      expression: a!localVariables(
        local!taskDependentBranches: rule!CMGT_UTIL_Filter(
          records: local!activeTaskBranches,
          field: recordType!CMGT_WFL_CFG_TaskBlockTaskBranch.beforeTaskBlockTaskUuid,
          value: fv!item[recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskUuid]
        ),
        local!taskPrecedentBranches: rule!CMGT_UTIL_Filter(
          records: local!activeTaskBranches,
          field: recordType!CMGT_WFL_CFG_TaskBlockTaskBranch.afterTaskBlockTaskUuid,
          value: fv!item[recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskUuid]
        ),
        local!responseOptionToEnd: rule!CMGT_UTIL_Filter(
          records: fv!item[recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskResponseOptions],
          field: recordType!CMGT_WFL_CFG_TaskBlockTaskResponseOption.taskBlockTaskResponseUuid,
          value: local!taskDependentBranches[recordType!CMGT_WFL_CFG_TaskBlockTaskBranch.taskBlockTaskResponseOptionUuid],
          filterType: "REJECT"
        ),
        local!item: fv!item,
        if(
          and(
            toboolean(
              local!item[recordType!CMGT_WFL_CFG_TaskBlockTask.startsByDefault]
            ) = false,
            rule!CMGT_UTIL_IsBlank(local!taskPrecedentBranches)
          ),
          null,
          {
            /* Constructing edge for each response option */
            #"SYSTEM_SYSRULES_forEach"(
              items: local!responseOptionToEnd,
              expression: a!map(
                from: local!item[recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskUuid],
                to: "m-" & length(local!activeTasks) + 1,
                color: ri!brandingMap.BlackColor,
                label: rule!CMGT_UTIL_TruncateText(
                  text: fv!item[recordType!CMGT_WFL_CFG_TaskBlockTaskResponseOption.label],
                  maxLength: 15
                ),
                title: fv!item[recordType!CMGT_WFL_CFG_TaskBlockTaskResponseOption.label],
                dashes: false,
                font: a!map(
                  multi: "html",
                  align: "horizontal",
                  color: ri!brandingMap.BlackColor,
                  bold: a!map(mod: "bold", size: 15)
                )
              )
            ),
            a!map(
              from: fv!item[recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskUuid],
              to: "m-" & length(local!activeTasks) + 1,
              color: ri!brandingMap.BlackColor,
              label: "",
              title: "",
              /*- Connect to end if has nothing after it*/
              showWhen: and(
                rule!CMGT_UTIL_IsBlank(local!taskDependentBranches),
                rule!CMGT_UTIL_IsBlank(local!responseOptionToEnd)
              ),
              dashes: false,
              font: a!map()
            )
          }
        )
      )
    )
  ),
  /*Combine all edges before passing to component*/
  /*Set the arrows parameter on the individual edges since we may need to swap these around to optimize rendering*/
  local!allEdges: rule!CMGT_UTIL_Filter(
    records: #"SYSTEM_SYSRULES_forEach"(
      items: rule!CMGT_UTIL_RejectBlank(
        {
          local!taskToTaskEdges,
          local!milestoneToTaskEdges,
          local!taskToMilestoneEdges
        }
      ),
      expression: #"urn:appian:function:v1:a:update"(
        fv!item,
        "arrows",
        rule!CMGT_NetworkChartField_StandardFormatting(brandingMap: ri!brandingMap).defaultArrow
      )
    ),
    field: "showWhen",
    value: false,
    filterType: "REJECT"
  ),
  local!fixNodesEdgesOrder: if(
    a!isNullOrEmpty(local!activeTasks),
    a!map(nodes: {}, edges: {}),
    rule!CMGT_NetworkChartField_FixNodeEdgeOrder_Logic(
      nodes: local!milestoneNodes,
      edges: local!allEdges
    )
  ),
  /*
  -------
  local variables that refreshes after load based on selection
  -------
  */
  /*Logic for node selection*/
  local!selectedTask: rule!CMGT_UTIL_cast(
    type: recordType!CMGT_WFL_CFG_TaskBlockTask,
    value: if(
      a!isNullOrEmpty(ri!selectedTaskBlockTaskUuid),
      null,
      rule!CMGT_UTIL_Filter(
        records: local!activeTasks,
        field: recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskUuid,
        value: ri!selectedTaskBlockTaskUuid
      )
    )
  ),
  local!finalNodesToDisplay: if(
    a!isNullOrEmpty(ri!selectedTaskBlockTaskUuid),
    /*display as it is, if no task node is selected*/
    local!fixNodesEdgesOrder.nodes,
    /*Update selected task's border and unrelated task nodes*/
    #"SYSTEM_SYSRULES_forEach"(
      items: local!fixNodesEdgesOrder.nodes,
      expression: a!localVariables(
        local!currentTask: rule!CMGT_UTIL_cast(
          type: recordType!CMGT_WFL_CFG_TaskBlockTask,
          value: rule!CMGT_UTIL_Filter(
            records: local!activeTasks,
            field: recordType!CMGT_WFL_CFG_TaskBlockTask.taskBlockTaskUuid,
            value: fv!item.id
          )
        ),
        local!isSelected: touniformstring(ri!selectedTaskBlockTaskUuid) = touniformstring(fv!item.id),
        local!isRelatedToSelected: or(
          contains(
            local!currentTask[recordType!CMGT_WFL_CFG_TaskBlockTask.beforeTaskBlockTaskBranches.beforeTaskBlockTaskUuid],
            touniformstring(ri!selectedTaskBlockTaskUuid)
          ),
          contains(
            local!selectedTask[recordType!CMGT_WFL_CFG_TaskBlockTask.beforeTaskBlockTaskBranches.beforeTaskBlockTaskUuid],
            touniformstring(fv!item.id)
          )
        ),
        #"urn:appian:function:v1:a:update"(
          fv!item,
          { "font", "color", "shadow" },
          {
            #"urn:appian:function:v1:a:update"(
              fv!item.font,
              "color",
              if(
                or(
                  local!isSelected,
                  local!isRelatedToSelected
                ),
                { ri!brandingMap.BlackColor },
                { ri!brandingMap.SecondaryTextColor }
              )
            ),
            #"urn:appian:function:v1:a:update"(
              fv!item.color,
              { "background", "border" },
              {
                if(
                  or(
                    local!isSelected,
                    local!isRelatedToSelected
                  ),
                  { fv!item.color.background },
                  { ri!brandingMap.WhiteColor }
                ),
                if(
                  local!isSelected,
                  ri!brandingMap.BlackColor,
                  ri!brandingMap.DividerColor
                )
              }
            ),
            #"urn:appian:function:v1:a:update"(
              fv!item.shadow,
              "color",
              if(
                or(
                  local!isSelected,
                  local!isRelatedToSelected
                ),
                { fv!item.shadow.color },
                { ri!brandingMap.WhiteColor }
              )
            )
          }
        )
      )
    )
  ),
  local!finalEdgesToDisplay: if(
