          recordType!CMGT_SendEmailTask.templateName: ri!task[recordType!CMGT_Task.sendEmailTask.templateName],
          recordType!CMGT_SendEmailTask.subject: ri!task[recordType!CMGT_Task.sendEmailTask.subject],
