_a-0000ed24-7935-8000-9d2d-011c48011c48_.sail_code:
/* 
1.If there are no open branches then the task will be marked as not needed.
2.If there are open branches from chosen(traversed) path then those task should not be marked as not needed.
So any task that is dependent on it will also be removed from mark as needed
3.If there are open branches only on the current unchosen or untravesed branch which will be evaluated 
after the current task,  Then the task will be initially marked as not needed which will be removed later if
needed based on the dependent task.

*/
a!localVariables(
  /*Branches of the currentTaskInUntraversedPath with the beforeTaskInstance's status as 'Ready', 'In Progress' or 'Queued'*/
  local!openTaskBranches: rule!CMGT_UTIL_Filter(
    records: a!flatten(
      ri!currentTaskInUntraversedPath[recordType!CMGT_Task.beforeTaskInstanceBranches]
    ),
    field: recordType!CMGT_TaskBranch.beforeTaskInstance.statusId,
    value: {
      cons!CMGT_REFID_STATUS_TASK_READY,
      cons!CMGT_REFID_STATUS_CASE_IN_PROGRESS,
      cons!CMGT_REFID_STATUS_TASK_QUEUED
    }
  ),
  /*Check if the task branch is present in the task that will be opened in current flow*/
  local!taskBranchesInToBeOpenedTasks: rule!CMGT_UTIL_Filter(
    records: a!flatten(
      ri!currentTaskInUntraversedPath[recordType!CMGT_Task.beforeTaskInstanceBranches]
    ),
    field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
    value: rule!CMGT_UTIL_RejectBlank(array: ri!tasksToOpenUuids)
  ),
  /*Filter the task not in untraversed path of both current task and the 
  tasks that will be completed in the current flow*/
  local!taskBranchesNotInUntraversedPath: a!flatten(
    rule!CMGT_UTIL_Filter(
      records: local!openTaskBranches,
      fieldValuePairs: {
        a!map(
          field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
          value: ri!tasksInUntraversedPathForCurrentTask[recordType!CMGT_Task.taskInstanceUuid]
        ),
        a!map(
          field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
          value: ri!tasksInUntraversedPathForOtherTasks[recordType!CMGT_Task.taskInstanceUuid]
        )
      },
      matchingCriteria: "ANY",
      filterType: "REJECT"
    )
  ),
  /*Filter the task in current task Untraversed path*/
  local!taskBranchesInCurrentTaskUntraversedPath: a!flatten(
    rule!CMGT_UTIL_Filter(
      records: local!openTaskBranches,
      fieldValuePairs: {
        a!map(
          field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
          value: ri!tasksInUntraversedPathForCurrentTask[recordType!CMGT_Task.taskInstanceUuid]
        )
      },

    )
  ),
  /*Check if any of the task is removed from task not needed.
  This logic is if the dependent task in untraveresed is removed from
  not needed task the current task that is processing will not be marked
  as not needed*/
  local!isTaskRemovedFromNotNeeded: if(
    rule!CMGT_UTIL_IsNotBlank(
      local!taskBranchesInCurrentTaskUntraversedPath[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid]
    ),
    or(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!taskBranchesInCurrentTaskUntraversedPath[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid],
        expression: contains(
          ri!finalMap.taskUuidsRemovedFromNotNeeded,
          fv!item
        )
      )
    ),
    false
  ),
  /*Filter the task branches in other completed tasks' untraversed path*/
  local!taskBranchesInOtherTasksUntraversedPath: a!flatten(
    rule!CMGT_UTIL_Filter(
      records: local!openTaskBranches,
      fieldValuePairs: {
        a!map(
          field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
          value: ri!tasksInUntraversedPathForOtherTasks[recordType!CMGT_Task.taskInstanceUuid]
        )
      },

    )
  ),
  if(
    rule!CMGT_UTIL_IsBlank(local!openTaskBranches),
    /*If the task is not having any branches return the map by appending
    current task as not needed*/
    a!map(
      notNeededtaskUUIDs: a!flatten(
        append(
          ri!finalMap.notNeededtaskUUIDs,
          ri!currentTaskInUntraversedPath[recordType!CMGT_Task.taskInstanceUuid]
        )
      ),
      dependentTaskMap: ri!finalMap.dependentTaskMap,
      taskUuidsRemovedFromNotNeeded: ri!finalMap.taskUuidsRemovedFromNotNeeded
    ),
    /*The task will not be marked as not needed when :
    1. It has open branches from outside our current untraversed path
    2. The task branch is the task that will be opened on current flow
    3. If any of the task in the current untraversed path has been moved from not needed tasks*/
    if(
      or(
        rule!CMGT_UTIL_IsNotBlank(local!taskBranchesNotInUntraversedPath),
        rule!CMGT_UTIL_IsNotBlank(local!taskBranchesInToBeOpenedTasks),
        local!isTaskRemovedFromNotNeeded = true
      ),
      /*Along with removing the current task the chain of dependent will also
    be removed that is waiting for this task*/
      rule!CMGT_Task_GetTasksToBeRemovedFromNotNeededStatus_Logic(
        taskInstanceUUID: ri!currentTaskInUntraversedPath[recordType!CMGT_Task.taskInstanceUuid],
        finalMap: ri!finalMap
      ),
      /*If the task has dependent on the untraversed path of current task
      or the tasks that will be completed as path of this flow initially lets assume 
      it to be not needed which will be updated at the later part of workflow if needed based on
      the dependent task evaluation*/
      if(
        or(
          rule!CMGT_UTIL_IsNotBlank(
            local!taskBranchesInCurrentTaskUntraversedPath
          ),
          rule!CMGT_UTIL_IsNotBlank(
            local!taskBranchesInOtherTasksUntraversedPath
          )
        ),
        a!localVariables(
          local!dependentTaskMap: #"SYSTEM_SYSRULES_forEach"(
            items: rule!CMGT_UTIL_Distinct(
              rule!CMGT_UTIL_RejectBlank(
                {
                  local!taskBranchesInCurrentTaskUntraversedPath,
                  local!taskBranchesInOtherTasksUntraversedPath
                }
              )
            ),
            expression: a!map(
              currentTask: ri!currentTaskInUntraversedPath[recordType!CMGT_Task.taskInstanceUuid],
              dependentTask: fv!item[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid]
            )
          ),
          a!map(
            notNeededtaskUUIDs: a!flatten(
              append(
                ri!finalMap.notNeededtaskUUIDs,
                ri!currentTaskInUntraversedPath[recordType!CMGT_Task.taskInstanceUuid]
              )
            ),
            dependentTaskMap: a!flatten(
              append(
                ri!finalMap.dependentTaskMap,
                local!dependentTaskMap
              )
            ),
            taskUuidsRemovedFromNotNeeded: ri!finalMap.taskUuidsRemovedFromNotNeeded,

          )
        ),
        ri!finalMap
      )
    )
  )
)

