a!localVariables(
  local!activeTasks: rule!CMGT_UTIL_Filter(
    records: ri!tasks,
    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.isActive,
    value: true
  ),
  local!activeTaskBranches: rule!CMGT_UTIL_Filter(
    records: a!flatten(
      ri!tasks[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.beforeCaseTypeWorkflowTaskBranches]
    ),
    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.isActive,
    value: true
  ),
  local!startMilestoneReferenceMilestoneId: a!defaultValue(ri!startTaskUuid, "m-0"),
  /*
  -------
  Build all nodes
  -------
  */
  /*Start with parent milestone nodes for each distinct milestone*/
  /*we should rework this to remove the milstone concept entirely*/
  local!milestoneNodes: #"SYSTEM_SYSRULES_forEach"(
    items: {
      a!map(
        referenceMilestone: a!map(
          referenceMilestoneId: local!startMilestoneReferenceMilestoneId,
          label: #"urn:appian:translation-string:v1:ca8737d3-bee3-452c-9475-419cf1366475"
        ),
        tasks: local!activeTasks
      ),
      a!map(
        referenceMilestone: a!map(
          referenceMilestoneId: "m-" & if(
            rule!CMGT_UTIL_IsBlank(local!activeTasks),
            1,
            length(local!activeTasks) + 1
          ),
          label: #"urn:appian:translation-string:v1:c9dcd0d5-2e16-4d10-b544-3e3445922c79"
        )
      )
    },
    expression: a!map(
      id: fv!item.referenceMilestone.referenceMilestoneId,
      group: "terminal",
      label: concat(
        "<b>",
        substitute(
          upper(fv!item.referenceMilestone.label),
          char(10),
          "</b>" & char(10) & "<b>"
        ),
        "</b>"
      ),
      font: a!map(
        multi: "html",
        align: "left",
        size: 12,
        vadjust: 2,
        color: ri!brandingMap.BlackColor,
        bold: a!map(size: 14, vadjust: 0, mod: "bold")
      ),
      color: a!map(
        border: ri!brandingMap.DividerColor,
        background: ri!brandingMap.WhiteColor
      ),
      shadow: a!map(),
      /*For each milestone, create the children nodes within that milestone*/
      children: #"SYSTEM_SYSRULES_forEach"(
        items: fv!item.tasks,
        expression: a!localVariables(
          if(
            fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.taskBehaviorType.isAutomated],
            /*Automated Tasks */
            a!map(
              id: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
              group: "task",
              label: if(
                tostring(ri!selectedCaseTypeWorkflowTaskUuid) = fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
                concat(
                  "<b>" & rule!CMGT_UTIL_TruncateText(
                    text: a!defaultValue(
                      fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.label],
                      #"urn:appian:translation-string:v1:f1a2114c-0453-4665-b358-be59650c55bc"
                    ),
                    maxLength: 44
                  ) & "</b>"
                ),
                rule!CMGT_UTIL_TruncateText(
                  text: a!defaultValue(
                    fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.label],
                    #"urn:appian:translation-string:v1:f1a2114c-0453-4665-b358-be59650c55bc"
                  ),
                  maxLength: 44
                )
              ),
              font: a!map(
                multi: "html",
                align: "left",
                size: 12,
                vadjust: 2,
                color: ri!brandingMap.BlackColor,
                bold: a!map(size: 14, vadjust: - 4, mod: "bold")
              ),
              shape: "image",
              image: rule!CMGT_WFL_Automation_returnImageURL(
                taskBehaviorType: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.taskBehaviorType],
                taskAction: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction],
                isSelected: tostring(ri!selectedCaseTypeWorkflowTaskUuid) = fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
              ),
              color: a!map(
                border: ri!brandingMap.DividerColor,
                background: null
              ),
              shadow: a!map()
            ),
            a!map(
              id: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
              group: "task",
              label: concat(
                "<b>",
                /*Trim the title to 44 characters at the next space to prevent wrapping text*/
                substitute(
                  rule!CMGT_UTIL_TruncateText(
                    text: a!defaultValue(
                      fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.label],
                      #"urn:appian:translation-string:v1:f1a2114c-0453-4665-b358-be59650c55bc"
                    ),
                    maxLength: 44
                  ),
                  char(10),
                  "</b>" & char(10) & "<b>"
                ),
                "</b>",
                char(10),
                fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.taskBehaviorType.label]
              ),
              font: a!map(
                multi: "html",
                align: "left",
                size: 12,
                vadjust: 2,
                color: ri!brandingMap.BlackColor,
                bold: a!map(size: 14, vadjust: - 4, mod: "bold")
              ),
              color: a!map(
                border: ri!brandingMap.DividerColor,
                background: ri!brandingMap.WhiteColor
              ),
              shadow: a!map(
                enabled: true,
                color: rule!CMGT_UTIL_DisplayBranding(
                  brandingMap: ri!brandingMap,
                  key: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.referenceMilestone.milestoneColor]
                ),
                size: 0,
                x: 0,
                y: - 5
              )
            )
          )
        )
      )
    )
  ),
  /*
  -------
  Build all edges
  -------
  */
  /*Build edges for all VALID branches between tasks*/
  /*Note that branches can become inActive through SAIL manipulation in forms, so we hide them*/
  local!taskToTaskEdges: if(
    rule!CMGT_UTIL_IsBlank(local!activeTasks),
    {},
    #"SYSTEM_SYSRULES_forEach"(
      items: rule!CMGT_UTIL_DistinctRecords(
        records: local!activeTaskBranches,
        fields: {
          recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
          recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.afterCaseTypeWorkflowTaskUuid,
          recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.taskResponseOption.caseTypeWorkflowTaskResponseOptionUuid,

        }
      ),
      expression: a!localVariables(
        local!taskBeforeBranch: displayvalue(
          fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid],
          local!activeTasks[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
          local!activeTasks,
          null
        ),
        local!taskAfterBranch: displayvalue(
          fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.afterCaseTypeWorkflowTaskUuid],
          local!activeTasks[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
          local!activeTasks,
          null
        ),
        a!map(
          from: local!taskBeforeBranch[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
          to: local!taskAfterBranch[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
          label: rule!CMGT_UTIL_TruncateText(
            text: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.taskResponseOption.label],
            maxLength: 15
          ),
          title: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.taskResponseOption.label],
          color: ri!brandingMap.BlackColor,
          dashes: if(
            fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.isRequired],
            false,
            true
          ),
          font: a!map(
            multi: "html",
            align: "horizontal",
            color: ri!brandingMap.BlackColor,
            bold: a!map(mod: "bold", size: 15)
          )
        )
      )
    )
  ),
  /*Build edges for all tasks that connect to their milestone start node*/
  local!milestoneToTaskEdges: #"SYSTEM_SYSRULES_forEach"(
    items: local!activeTasks,
    expression: a!map(
      from: local!startMilestoneReferenceMilestoneId,
      to: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
      color: ri!brandingMap.BlackColor,
      showWhen: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.startsByDefault],
      dashes: false,
      font: a!map()
    )
  ),
  /*Build edges for all tasks that connect to the next milestone or the "End" node if in last milestone*/
  local!taskToMilestoneEdges: rule!CMGT_UTIL_RejectBlank(
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!activeTasks,
        expression: a!localVariables(
          local!taskDependentBranches: rule!CMGT_UTIL_Filter(
            records: local!activeTaskBranches,
            field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
            value: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
          ),
          local!taskPrecedentBranches: rule!CMGT_UTIL_Filter(
            records: local!activeTaskBranches,
            field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.afterCaseTypeWorkflowTaskUuid,
            value: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
          ),
          local!responseOptionToEnd: rule!CMGT_UTIL_Filter(
            records: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskResponseOptions],
            field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskResponseOption.caseTypeWorkflowTaskResponseOptionUuid,
            value: local!taskDependentBranches[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.caseTypeTaskResponseOptionUuid],
            filterType: "REJECT"
          ),
          /* The below 2 locals are specific to automation */
          local!currentTaskActiveConditionSets: cast(
            #"urn:appian:function:v1:a:listtype?okey=a!listType"(
              recordType!CMGT_CFG_ConditionSet
            ),
            rule!CMGT_UTIL_Filter(
              records: a!flatten(
                rule!CMGT_UTIL_Filter(
                  records: ri!conditionSets,
                  field: recordType!CMGT_CFG_ConditionSet.caseTypeWorkflowTaskUuid,
                  value: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
                )[recordType!CMGT_CFG_ConditionSet.childConditionSet]
              ),
              field: recordType!CMGT_CFG_ConditionSet.isActive,
              value: true
            )
          ),
          local!currentTaskActiveConditionSetCount: length(
            rule!CMGT_UTIL_DistinctRecords(
              records: rule!CMGT_UTIL_Filter(
                records: a!flatten(
                  local!currentTaskActiveConditionSets[recordType!CMGT_CFG_ConditionSet.condition]
                ),
                field: recordType!CMGT_CFG_Condition.isActive,
                value: true
              ),
              fields: recordType!CMGT_CFG_Condition.conditionSetUuid
            )
          ),
          /* This local is used to drive logic to connect activities with End */
          local!hasDefaultEdgeToEnd: a!match(
            value: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.taskBehaviorTypeId],
            whenTrue: and(
              fv!value = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
              fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction.actionCode] = cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING
            ),
            /* Checking if the after task branch count is less than active condition set count(with conditions)+1 (If no rule met go to) */
            then: length(
              rule!CMGT_UTIL_DistinctRecords(
                records: rule!CMGT_UTIL_Filter(
                  records: local!activeTaskBranches,
                  field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
                  value: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],

                ),
                fields: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.conditionSetUuid
              )
            ) < local!currentTaskActiveConditionSetCount + 1,
            whenTrue: and(
              rule!CMGT_UTIL_IsBlank(local!taskDependentBranches),
              rule!CMGT_UTIL_IsBlank(local!responseOptionToEnd)
            ),
            then: true,
            /* add additional logics here */
            default: false
          ),
          local!item: fv!item,
          if(
            and(
              local!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.startsByDefault] = false,
              rule!CMGT_UTIL_IsBlank(local!taskPrecedentBranches)
            ),
            null,
            {
              /* Constructing edge for each response option */
              #"SYSTEM_SYSRULES_forEach"(
                items: local!responseOptionToEnd,
                expression: a!map(
                  from: local!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
                  to: "m-" & length(local!activeTasks) + 1,
                  color: ri!brandingMap.BlackColor,
                  label: rule!CMGT_UTIL_TruncateText(
                    text: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskResponseOption.label],
                    maxLength: 15
                  ),
                  title: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskResponseOption.label],
                  dashes: false,
                  font: a!map(
                    multi: "html",
                    align: "horizontal",
                    color: ri!brandingMap.BlackColor,
                    bold: a!map(mod: "bold", size: 15)
                  )
                )
              ),
              a!map(
                from: local!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
                to: "m-" & length(local!activeTasks) + 1,
                color: ri!brandingMap.BlackColor,
                label: "",
                title: "",
                /*- Connect to end if has default edge to end*/
                showWhen: local!hasDefaultEdgeToEnd,
                dashes: false,
                font: a!map(
                  multi: "html",
                  align: "horizontal",
                  color: ri!brandingMap.BlackColor,
                  bold: a!map(mod: "bold", size: 15)
                )
              )
            }
          )
        )
      )
    )
  ),
  /*Combine all edges before passing to component*/
  /*Set the arrows parameter on the individual edges since we may need to swap these around to optimize rendering*/
  local!allEdges: rule!CMGT_UTIL_Filter(
    records: #"SYSTEM_SYSRULES_forEach"(
      items: {
        local!taskToTaskEdges,
        local!milestoneToTaskEdges,
        local!taskToMilestoneEdges
      },
      expression: #"urn:appian:function:v1:a:update"(
        fv!item,
        "arrows",
        rule!CMGT_NetworkChartField_StandardFormatting(brandingMap: ri!brandingMap).defaultArrow
      )
    ),
    field: "showWhen",
    value: false,
    filterType: "REJECT"
  ),
  local!fixNodesEdgesOrder: /*a!map(nodes: {}, edges: {}),*/
  if(
    a!isNullOrEmpty(local!activeTasks),
    a!map(nodes: {}, edges: {}),
    rule!CMGT_NetworkChartField_FixNodeEdgeOrder_Logic(
      nodes: local!milestoneNodes,
      edges: local!allEdges
    )
  ),
  /*-------*/
  /*local variables that refreshes after load based on selection*/
  /*-------*/
  /**/
  /*Logic for node selection*/
  /*ri!selectedCaseTypeWorkflowTaskUuid: if(*/
  /*a!isNotNullOrEmpty(ri!selectedCaseTypeWorkflowTaskUuid),*/
  /*ri!selectedCaseTypeWorkflowTaskUuid,*/
  /*""*/
  /*),*/
  local!selectedTask: rule!CMGT_UTIL_cast(
    type: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask,
    value: if(
      a!isNullOrEmpty(ri!selectedCaseTypeWorkflowTaskUuid),
      null,
      rule!CMGT_UTIL_Filter(
        records: local!activeTasks,
        field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid,
        value: ri!selectedCaseTypeWorkflowTaskUuid
      )
    )
  ),
  local!finalNodesToDisplay: if(
    a!isNullOrEmpty(ri!selectedCaseTypeWorkflowTaskUuid),
    /*display as it is, if no task node is selected*/
    local!fixNodesEdgesOrder.nodes,
    /*Update selected task's border and unrelated task nodes*/
    #"SYSTEM_SYSRULES_forEach"(
      items: local!fixNodesEdgesOrder.nodes,
      expression: a!localVariables(
        local!currentTask: rule!CMGT_UTIL_cast(
          type: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask,
          value: rule!CMGT_UTIL_Filter(
            records: local!activeTasks,
            field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid,
            value: fv!item.id
          )
        ),
        local!isSelected: touniformstring(ri!selectedCaseTypeWorkflowTaskUuid) = touniformstring(fv!item.id),
        local!isRelatedToSelected: or(
          contains(
            local!currentTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.beforeCaseTypeWorkflowTaskBranches.beforeCaseTypeWorkflowTaskUuid],
            touniformstring(ri!selectedCaseTypeWorkflowTaskUuid)
          ),
          contains(
            local!selectedTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.beforeCaseTypeWorkflowTaskBranches.beforeCaseTypeWorkflowTaskUuid],
            touniformstring(fv!item.id)
          )
        ),
        #"urn:appian:function:v1:a:update"(
          fv!item,
          { "font", "color", "shadow" },
          {
            #"urn:appian:function:v1:a:update"(
              fv!item.font,
              "color",
              if(
                or(
                  local!isSelected,
                  local!isRelatedToSelected
                ),
                { ri!brandingMap.BlackColor },
                { ri!brandingMap.SecondaryTextColor }
              )
            ),
            #"urn:appian:function:v1:a:update"(
              fv!item.color,
              { "background", "border" },
              {
                if(
                  or(
                    local!isSelected,
                    local!isRelatedToSelected
                  ),
                  { fv!item.color.background },
                  { ri!brandingMap.WhiteColor }
                ),
                if(
                  local!isSelected,
                  ri!brandingMap.BlackColor,
                  ri!brandingMap.DividerColor
                )
              }
            ),
            #"urn:appian:function:v1:a:update"(
              fv!item.shadow,
              "color",
              if(
                or(
                  local!isSelected,
                  local!isRelatedToSelected
                ),
                { fv!item.shadow.color },
                { ri!brandingMap.WhiteColor }
              )
            )
          }
        )
      )
    )
  ),
  local!finalEdgesToDisplay: if(
    a!isNullOrEmpty(ri!selectedCaseTypeWorkflowTaskUuid),
    /*display as it is if no task node is selected*/
    local!fixNodesEdgesOrder.edges,
    /*Update edges to selected and related nodes to be black in color*/
    #"SYSTEM_SYSRULES_forEach"(
      items: local!fixNodesEdgesOrder.edges,
      expression: if(
        /*if it is a edge to selected or related node - update color*/
        contains(
          touniformstring({ fv!item.from, fv!item.to }),
          touniformstring(ri!selectedCaseTypeWorkflowTaskUuid)
        ),
        fv!item,
        {
          #"urn:appian:function:v1:a:update"(
            data: fv!item,
            index: { "font", "color" },
            value: {
              #"urn:appian:function:v1:a:update"(
                data: fv!item.font,
                index: "color",
                value: ri!brandingMap.SecondaryTextColor
              ),
              ri!brandingMap.DividerColor
            }
          )
        }
      )
    )
  ),
  local!validations: a!localVariables(
    local!isEndNodePresentAndConnected: and(
      contains(
        index(local!finalNodesToDisplay, "label", ""),
        "<b>END</b>"
      ),
      contains(
        touniformstring(
          index(local!finalEdgesToDisplay, "to", "")
        ),
        "m-" & if(
          rule!CMGT_UTIL_IsBlank(local!activeTasks),
          1,
          length(local!activeTasks) + 1
        )
      )
    ),
    {
      #"SYSTEM_SYSRULES_validationMessage"(
        message: #"urn:appian:translation-string:v1:b1c8fa44-5ad7-4c3d-adab-2b29d020ce6f"(
          #"urn:appian:translation-variable:v1:7202908f-19dc-44a8-a741-9d1f47227cbe": ri!additionalValidationConditions.taskNamesInViolation
        ),
        validateAfter: "SUBMIT",
        showWhen: rule!CMGT_UTIL_IsNotBlank(
          ri!additionalValidationConditions.taskNamesInViolation
        )
      ),
      /*display validation message if there are no tasks mapped to START*/
      #"SYSTEM_SYSRULES_validationMessage"(
        message: #"urn:appian:translation-string:v1:459be6ff-55b0-47f3-81fc-6c7386747126",
        validateAfter: "SUBMIT",
        showWhen: and(
          rule!CMGT_UTIL_IsBlank(
            ri!additionalValidationConditions.tasksMappedtoStart
          ),
          ri!additionalValidationConditions.doesNonStartTasksExist
        )
      ),
      /*display validation message if there is not end node*/
      #"SYSTEM_SYSRULES_validationMessage"(
        message: #"urn:appian:translation-string:v1:2c2e7b04-c771-4f76-94e6-06f65f4c162f",
        validateAfter: "SUBMIT",
        showWhen: and(
          not(local!isEndNodePresentAndConnected),
          count(
            index(local!finalNodesToDisplay, "label", null)
          ) > 1
        )
      ),
      /*display validation message if there are tasks with no required precedents*/
      #"SYSTEM_SYSRULES_validationMessage"(
        message: #"urn:appian:translation-string:v1:cb8b665a-6708-48a7-a046-0581dfad9bdd"(
          #"urn:appian:translation-variable:v1:5f1e4161-3b84-4604-a027-ed934750ece6": joinarray(
            index(
              ri!additionalValidationConditions,
              "tasksWithNoPrecedents",
              null
            )[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.label],
            ", "
          )
        ),
        validateAfter: "SUBMIT",
        showWhen: rule!CMGT_UTIL_IsNotBlank(
          index(
            ri!additionalValidationConditions,
            "tasksWithNoPrecedents",
            null
          )
        )
      ),
      #"SYSTEM_SYSRULES_validationMessage"(
        message: #"urn:appian:translation-string:v1:4fab94dc-fcb1-427a-af9f-1239079b2897"(
          #"urn:appian:translation-variable:v1:3e31b6d5-df20-4d17-80a7-81f3c83ff370": #"urn:appian:translation-string:v1:e1677170-3ff7-4041-bdf6-16f33d92d9ed"
        ),
        validateAfter: "SUBMIT",
        showWhen: rule!CMGT_UTIL_IsNotBlank(
          index(
            ri!additionalValidationConditions,
            "taskWithoutName",
            null
          )
        )
      ),
      /*display validation message if there are tasks with invalid basic details*/
      #"SYSTEM_SYSRULES_validationMessage"(
        message: #"urn:appian:translation-string:v1:1954697c-c9b5-4ac7-b349-d5d298d0f929"(
          #"urn:appian:translation-variable:v1:f8f512c7-0b6c-4d3d-9aaf-482aa193d182": joinarray(
            index(
              index(
                ri!additionalValidationConditions,
                "tasksWithInvalidBasicDetails",
                {}
              ),
              recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.label,
              {}
            ),
            ", "
          )
        ),
        validateAfter: "SUBMIT",
        showWhen: rule!CMGT_UTIL_IsNotBlank(
          index(
            ri!additionalValidationConditions,
            "tasksWithInvalidBasicDetails",
            null
          )
        )
      )
    }
  ),
  {
    #"SYSTEM_SYSRULES_sectionLayout_v1"(
      showWhen: rule!CMGT_UTIL_IsNotBlank(local!validations),
      validations: local!validations,
      validationGroup: ri!validationGroup
    ),
    #"SYSTEM_SYSRULES_cardLayout"(
      contents: rule!CMGT_PLUGIN_NetworkChartField(
        nodes: local!finalNodesToDisplay,
        edges: local!finalEdgesToDisplay,
        selectedNodeUuid: ri!selectedCaseTypeWorkflowTaskUuid,
        height: a!defaultValue(
          ri!height,
          if(
            rule!CMGT_UTIL_IsMobileScreenSize(),
            "MEDIUM",
            "TALL"
          )
        ),
        selectable: ri!selectable,
        brandingMap: ri!brandingMap,
        onNodeSelection: a!save(
          ri!selectedCaseTypeWorkflowTaskUuid,
          rule!CMGT_UTIL_cast(
            typeOf: { "" },
            value: if(
              touniformstring(ri!selectedCaseTypeWorkflowTaskUuid) = touniformstring(save!value),
              {},
              save!value
            )
          )
        )
      ),
      style: a!defaultValue(ri!backgroundStyle,ri!brandingMap.CardBackgroundColor),
      shape: ri!brandingMap.CardShape,
      padding: "EVEN_LESS",
      showBorder: false,
      showShadow: false
    )
  }
)

_a-0000ed20-1ca4-8000-9d2b-011c48011c48_.sail_code:
if(
  rule!CMGT_UTIL_IsBlank(ri!automationTasks),
  {},
  a!localVariables(
    local!data: rule!CMGT_EVAL_returnDataForConditionEvaluation(case: ri!case, task: ri!task),
    local!caseType: rule!CMGT_QR_GetCaseType(
      returnType: "SINGLE_OBJECT",
      caseTypeUuid: ri!case[recordType!CMGT_Case.caseTypeUuid],
      fields: {
        recordType!CMGT_CFG_CaseType.parentCaseType
      }
    ),
    /*Need to return the available field paths in case the paths have changed.  */
    local!allAvailableFields: rule!CMGT_returnAvailableFields(
      scope: if(
        rule!CMGT_UTIL_IsBlank(ri!task),
        "CASE",
        "TASK"
      ),
      caseTypeRecordTypeUuid: local!caseType[recordType!CMGT_CFG_CaseType.uuid],
      parentCaseTypeRecordTypeUuid: local!caseType[recordType!CMGT_CFG_CaseType.parentUuid]
    ),
    local!filteredAvailableFields: rule!CMGT_UTIL_Filter(
      records: local!allAvailableFields,
      fieldValuePairs: {
        a!map(
          field: "uuid",
          value: rule!CMGT_ENUM_Automation_ootbCaseRecordFieldsToReject(context: null)
        )
      },
      filterType: "REJECT"
    ),
    local!rulesWithPassingConditionSets: ri!automationTasks[where(
      /*loops through each task (automation rule) and checks whether or not it should be run based on the conditions*/
      a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!automationTasks, expression: a!localVariables(
            /* Only want to execute if the condition set is active         */
            local!parentConditionSet: index(
              rule!CMGT_UTIL_Filter(
                records: a!flatten(
                  fv!item[recordType!CMGT_Task.conditionSet]
                ),
                field: recordType!CMGT_ConditionSet.isActive,
                value: true()
              ),
              1,
              null
            ),
            /*loops through each child condition set and checks if the child condition sets associated with each one pass*/
            local!parentConditionSetEvaluation: #"SYSTEM_SYSRULES_forEach"(
              items: local!parentConditionSet[recordType!CMGT_ConditionSet.childConditionSet],
              expression: a!localVariables(
                local!childConditionSet: fv!item,
                /*loops through each condition on the child condition set and checks if the conditions associated pass*/
                #"SYSTEM_SYSRULES_forEach"(
                  items: local!childConditionSet[recordType!CMGT_ConditionSet.condition],
                  expression: rule!CMGT_EVAL_EvaluateConditionSetDynamic(
                    conditionSet: local!childConditionSet,
                    data: local!data,
                    availableFields: local!filteredAvailableFields
                  ).result
                )
              )
            ),
            if(
              a!isNullOrEmpty(local!parentConditionSet),
              true,
              /*checks if all the child condition sets pass depending on the operator selected*/
              a!match(
                value: tostring(
                  local!parentConditionSet[recordType!CMGT_ConditionSet.operatorCode]
                ),
                equals: "OR",
                then: or(local!parentConditionSetEvaluation),
                equals: "AND",
                then: and(local!parentConditionSetEvaluation),
                default: 0
              )
            )
          )
        )
      )
    )],
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!rulesWithPassingConditionSets[recordType!CMGT_Task.automationTaskAction],
        expression: #"urn:appian:function:v1:a:update"(
          data: fv!item,
          index: recordType!CMGT_TaskActions.automationTaskInstance,
          value: rule!CMGT_UTIL_Filter(
            records: ri!automationTasks,
            field: recordType!CMGT_Task.taskInstanceUuid,
            value: fv!item[recordType!CMGT_TaskActions.taskInstanceUuid]
          )
        )
      )
    )
  )
)

_a-0000e99c-1217-8000-9ba9-011c48011c48_.sail_code:
a!localVariables(
  local!caseWatchers: rule!CMGT_QR_GetRole(
    caseId: ri!case[recordType!CMGT_Case.caseId],
    triggerRefresh: ri!triggerRefresh,
    roleTypeId: cons!CMGT_REFID_ROLE_TYPE_WATCHER
  ),
  #"SYSTEM_SYSRULES_sectionLayout_v1"(
    label: "",
    labelSize: "MEDIUM",
    labelHeadingTag: "H2",
    labelColor: ri!brandingMap.SecondaryTextColor,
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
