a!localVariables(
  local!activeConditionSets: rule!CMGT_UTIL_Filter(
    records: ri!caseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.conditionSet],
    field: recordType!CMGT_CFG_ConditionSet.isActive,
    value: true
  ),
  local!containsMultipleChildConditionSets: length(
    a!defaultValue(
      local!activeConditionSets,
      0
    )
  ) > 1,
  #"SYSTEM_SYSRULES_forEach"(
    items: local!activeConditionSets,
    expression: {
      a!localVariables(
        local!currentChildConditionSet: fv!item,
        local!activeConditions:rule!CMGT_UTIL_Filter(
          records:local!currentChildConditionSet[recordType!CMGT_CFG_ConditionSet.condition],
          field: recordType!CMGT_CFG_Condition.isActive,
          value: true
        ),
        local!numberOfConditions: length(
          local!activeConditions
        ),
        local!containsMultipleConditions: local!numberOfConditions > 1,
        {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: "(",
            size: a!defaultValue(ri!textSize,"MEDIUM"),
            showWhen: and(
              local!containsMultipleChildConditionSets,
              local!containsMultipleConditions
            ),
            color: a!defaultValue(ri!textColor, "STANDARD")
          ),
          #"SYSTEM_SYSRULES_forEach"(
            items: local!activeConditions,
            expression: a!localVariables(
              {
                rule!CMGT_Automation_FormatCondition(
                  fieldDetails:ri!availableFields,
                  fieldPathJson:fv!item[recordType!CMGT_CFG_Condition.fieldPathJson],
                  textSize: a!defaultValue(ri!textSize,"MEDIUM"),
                  textColor:ri!textColor,
                  valueJson: fv!item[recordType!CMGT_CFG_Condition.valueJson],
                  operator: fv!item[recordType!CMGT_CFG_Condition.operator]
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: " ",
                  showWhen: and(
                    fv!index < local!numberOfConditions,
                  ),
                  size:a!defaultValue(ri!textSize,"MEDIUM"),
                  color: a!defaultValue(ri!textColor, "STANDARD")
                ),
                /*only show the in between opeartor codes if it is not the last condition within a specific condition set*/
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: UPPER(
                    local!currentChildConditionSet[recordType!CMGT_CFG_ConditionSet.operatorCode]
                  ) & " ",
                  showWhen: fv!index < local!numberOfConditions,
                  size: a!defaultValue(ri!textSize,"MEDIUM"),
                  color: a!defaultValue(ri!textColor, "STANDARD")
                )
              }
            )
          ),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: ")",
            size: a!defaultValue(ri!textSize,"MEDIUM"),
            showWhen: and(
              local!containsMultipleChildConditionSets,
              local!containsMultipleConditions
            ),
            color: a!defaultValue(ri!textColor, "STANDARD")
          ),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: " ",
            size: a!defaultValue(ri!textSize,"MEDIUM"),
            showWhen:
            or(
              and(
                local!containsMultipleChildConditionSets,
                local!containsMultipleConditions
              ),
              fv!index < length(
                a!defaultValue(
                  local!activeConditionSets,
                  {}
                )
              )
            ),
            color: a!defaultValue(ri!textColor, "STANDARD")
          ),
          /*only show the operator code if the current condition set is not the last one*/
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: UPPER(
              ri!caseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.conditionSet.operatorCode]
            ) & " ",
            showWhen: fv!index < length(
              a!defaultValue(
                local!activeConditionSets,
                {}
              )
            ),
            size: a!defaultValue(ri!textSize,"MEDIUM"),
            color: a!defaultValue(ri!textColor, "STANDARD")
          )
        }
      )
    }
  )
)

_a-0000ea2f-ac11-8000-9bf4-011c48011c48_.sail_code:
if(
  a!isNullOrEmpty(ri!roleTypeIds),
  null,
  a!localVariables(
    local!newUserRoles: rule!CMGT_UTIL_Filter(
      records: ri!roles,
      fieldValuePairs: {
        a!map(
          field: recordType!CMGT_Role.roleId,
          value: null()
        ),
        a!map(
          field: recordType!CMGT_Role.roleTypeId,
          value: if(
            ri!roleTypeIds = cons!CMGT_REFID_ROLE_TYPE_WATCHER,
            cons!CMGT_REFID_ROLE_TYPE_WATCHER,
            cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE
          )
        )
      }
    ),
    local!currentUserRoles: difference(
      rule!CMGT_UTIL_Filter(
        records: ri!roles,
        fieldValuePairs: {
          a!map(
            field: recordType!CMGT_Role.roleTypeId,
            value: cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE
          )
        }
      ),
      local!newUserRoles
    ),
    a!match(
      value: ri!roleTypeIds,
      equals: cons!CMGT_REFID_ROLE_TYPE_WATCHER,
      /*Send email to new watchers*/
      then: local!newUserRoles,
      default: a!defaultValue(
        /*Sending Email to new user assignee*/
        value: local!newUserRoles,
        /*
    If there is no current user assignee and if the group assignee is updated, email is sent
    to all group members. If there is a current user assignee, and if the group assignee is updated, the 
    email won't be sent to new group members
    */
        default: if(
          a!isNullOrEmpty(local!currentUserRoles),
          rule!CMGT_UTIL_Filter(
            records: ri!roles,
            fieldValuePairs: {
              a!map(
                field: recordType!CMGT_Role.roleId,
                value: null()
              ),
              a!map(
                field: recordType!CMGT_Role.roleTypeId,
                value: cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE
              )
            }
          ),
          null()
        )
      )
    )
  )
)

_a-0000eaba-1e20-8000-9c2a-011c48011c48_.sail_code:
if(
  or(
    isnull(ri!colorSchemeValue),
    contains(rule!CMGT_ENUM_BRAND_STANDARD_COLOR_SCHEMES().COLOR_SCHEME.colorSchemes.key, ri!colorSchemeValue)
  ),

  /*Is a standard color scheme (e.g. MIDNIGHT)*/
  if(
    ri!returnRawHexCodes,
    a!flatten(displayvalue(
      ri!colorSchemeValue,
      rule!CMGT_ENUM_BRAND_STANDARD_COLOR_SCHEMES().COLOR_SCHEME.colorSchemes.key,
      rule!CMGT_ENUM_BRAND_STANDARD_COLOR_SCHEMES().COLOR_SCHEME.colorSchemes.hexCodes,
      {}
    )),
    ri!colorSchemeValue
  ),

  /*Is a custom color scheme*/
  if(
