a!localVariables(
  /*Get the sender*/
  local!sender: rule!CMGT_UTIL_Filter(
    records: ri!latestMessageRecipients,
    field: recordType!CMGT_EML_TRI_MessageRecipient.isSender,
    value: true
  ),
  /*Get all recipients of latest message excluding system main box*/
  local!latestMessageRecipients: rule!CMGT_UTIL_Filter(
    records: ri!latestMessageRecipients,
    field: recordType!CMGT_EML_TRI_MessageRecipient.recipientEmail,
    value: cons!CMGT_EML_TRI_MAILBOX_EMAIL_ADDRESS,
    filterType: "REJECT"
  ),
  if(
    ri!action = "REPLY",
    /*If the latest sender if from system, then reply to will be first recipient else the latest sender*/
    if(
      local!sender[recordType!CMGT_EML_TRI_MessageRecipient.recipientEmail] = cons!CMGT_EML_TRI_MAILBOX_EMAIL_ADDRESS,
      index(
        rule!CMGT_UTIL_Filter(
          records: local!latestMessageRecipients,
          field: recordType!CMGT_EML_TRI_MessageRecipient.isSender,
          value: false
        ),
        1,
        null
      ),
      local!sender
    ),
    /*For reply all fetch all the recipients of the latest message excluding system mailbox*/
    local!latestMessageRecipients
  )
)

