        ri!data[recordType!CMGT_EventHistory_Task.details]
      ),
      #"SYSTEM_SYSRULES_fromJson_v1"(
        ri!data[recordType!CMGT_EventHistory_Task.details]
      ),
      null
    )
  ),
  a!match(
    value: ri!data[recordType!CMGT_EventHistory_Task.taskEventTypeId],
    whenTrue: and(
      contains(
        {
          cons!CMGT_REFID_EVENT_TYPE_TASK_COMPLETE
        },
        fv!value
      ),
      ri!data[recordType!CMGT_EventHistory_Task.task.taskBehaviorTypeId] = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
    ),
    then: concat(
      #"urn:appian:translation-string:v1:6ddd8757-400b-4b3e-831d-104bf494c1de"(
        #"urn:appian:translation-variable:v1:6577e335-2c33-4e71-a854-63d183c0ef0d": local!details.automationType
      ),
      if(
        ri!data[recordType!CMGT_EventHistory_Task.task.automationTaskAction.actionCode] = cons!CMGT_CFG_TXT_ACT_SEND_EMAIL,
        {
          char(10),
          #"urn:appian:translation-string:v1:444f1eb9-fa68-45ef-9d35-569a389884ff"(
            #"urn:appian:translation-variable:v1:2278bdbc-1c5e-427f-994d-caa131837e1c": local!details.subject
          )
        },
        ""
      )
    ),
    whenTrue: contains(
      {
        cons!CMGT_REFID_EVENT_TYPE_TASK_COMPLETE
      },
      fv!value
    ),
    then: {
      concat(
        #"urn:appian:translation-string:v1:0c505a3f-efa5-4335-8a88-38e6b58fabcf"(
          #"urn:appian:translation-variable:v1:71b17c88-e24f-4229-b8ac-1b067deda73f": local!details.taskBehaviorType
        ),
        char(10),
        a!match(
          value: ri!data[recordType!CMGT_EventHistory_Task.task.taskBehaviorTypeId],
          whenTrue: ri!data[recordType!CMGT_EventHistory_Task.task.taskBehaviorTypeId] = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
          then: local!details.taskResponseOptionLabelKey,
          whenTrue: ri!data[recordType!CMGT_EventHistory_Task.task.taskBehaviorTypeId] = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_CREATE_CASE,
          then: a!localVariables(
            local!case: rule!CMGT_QR_GetCase(
              returnType: "SINGLE_OBJECT",
              executeWhen: a!isNotNullOrEmpty(local!details.newCaseId),
              caseId: local!details.newCaseId,
              fields: {
                recordType!CMGT_Case.friendlyId,
                recordType!CMGT_Case.title
              }
            ),
            {
              local!case[recordType!CMGT_Case.friendlyId],
              " | ",
              local!case[recordType!CMGT_Case.title]
            }
          ),
          default: null
        )
      )
    },
    whenTrue: or(
      and(
        contains(
          {
            cons!CMGT_REFID_EVENT_TYPE_TASK_COMPLETE
          },
          fv!value
        ),
        ri!data[recordType!CMGT_EventHistory_Task.task.taskBehaviorTypeId] = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_SEND_EMAIL
      ),
      fv!value = cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_EMAIL
    ),
    then: concat(
      #"urn:appian:translation-string:v1:0c505a3f-efa5-4335-8a88-38e6b58fabcf"(
        #"urn:appian:translation-variable:v1:71b17c88-e24f-4229-b8ac-1b067deda73f": local!details.taskBehaviorType
      ),
      char(10),
      #"urn:appian:translation-string:v1:444f1eb9-fa68-45ef-9d35-569a389884ff"(
        #"urn:appian:translation-variable:v1:2278bdbc-1c5e-427f-994d-caa131837e1c": local!details.subject
      )
    ),
    whenTrue: contains(
      {
        cons!CMGT_REFID_EVENT_TYPE_TASK_NOT_NEEDED,
        cons!CMGT_REFID_EVENT_TYPE_TASK_MARKED_ACTIVITY_AS_READY,
        cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_ACTIVITY,
        cons!CMGT_REFID_EVENT_TYPE_TASK_ADD_ACTIVITY,
        cons!CMGT_REFID_EVENT_TYPE_TASK_CREATE
      },
      fv!value
    ),
    then: {
      if(
        ri!data[recordType!CMGT_EventHistory_Task.task.taskBehaviorTypeId] = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
        #"urn:appian:translation-string:v1:6ddd8757-400b-4b3e-831d-104bf494c1de"(
          #"urn:appian:translation-variable:v1:6577e335-2c33-4e71-a854-63d183c0ef0d": local!details.automationType
        ),
        #"urn:appian:translation-string:v1:0c505a3f-efa5-4335-8a88-38e6b58fabcf"(
          #"urn:appian:translation-variable:v1:71b17c88-e24f-4229-b8ac-1b067deda73f": local!details.taskBehaviorType
        )
      )
    },
    whenTrue: contains(
      {
        cons!CMGT_REFID_EVENT_TYPE_TASK_ADD_ASSIGNEE,
        cons!CMGT_REFID_EVENT_TYPE_TASK_REMOVED_ASSIGNEE
      },
      fv!value
    ),
    then: if(
      a!isNotNullOrEmpty(local!details.groupId),
      group(
        tointeger(local!details.groupId),
        "groupName"
      ),
      rule!CMGT_UTIL_DisplayUserFullName(username: local!details.user)
    ),
    whenTrue: contains(
      {
        cons!CMGT_REFID_EVENT_TYPE_TASK_ADD_COMMENT,
        cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_COMMENT,
        cons!CMGT_REFID_EVENT_TYPE_TASK_REPLY_TO_COMMENT,
        cons!CMGT_REFID_EVENT_TYPE_TASK_DELETE_COMMENT
      },
      fv!value
    ),
    then: concat(
      #"urn:appian:translation-string:v1:b047f92a-3c3c-4104-9fab-4323d484d78f"(
        #"urn:appian:translation-variable:v1:98579b94-57f7-41ee-8b4e-649f6147ac35": rule!CMGT_UTIL_StripHtmlTags(
          value: local!details.commentText,
          isFormatted: true
        )
      ),
      if(
        rule!CMGT_UTIL_IsBlank(local!details.taggedUsers),
        "",
        concat(
          char(10),
          #"urn:appian:translation-string:v1:1989f3c9-20e5-4be9-822b-7b1dc48f424b"(
            #"urn:appian:translation-variable:v1:0ceb2d10-7e2a-44f9-919d-e51150bb29d3": joinarray(
              #"SYSTEM_SYSRULES_forEach"(
                items: local!details.taggedUsers,
                expression: rule!CMGT_UTIL_DisplayUserFullName(username: fv!item)
              ),
              ", "
            )
          )
        )
      ),
      if(
        rule!CMGT_UTIL_IsBlank(local!details.documents),
        "",
        concat(
          char(10),
          #"urn:appian:translation-string:v1:c0d05eca-625c-4aba-afba-fda052e49cb2"(
            #"urn:appian:translation-variable:v1:7299566d-8206-4962-bb5e-71475e411afb": joinarray(
              #"SYSTEM_SYSRULES_forEach"(
                items: local!details.documents,
                expression: fv!item
              ),
              ", "
            )
          )
        )
      )
    ),
    equals: cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_NAME,
    then: concat(
      #"urn:appian:translation-string:v1:f80429d7-dbf2-4282-9283-8f89899e72d3"(
        #"urn:appian:translation-variable:v1:5c18f69d-9530-42da-847b-130c5c4b6d7e": local!details.originalValue
      ),
      char(10),
      #"urn:appian:translation-string:v1:942a20e1-ba63-496f-9fff-8d1c84bff8df"(
        #"urn:appian:translation-variable:v1:70a6a6c1-297e-430b-b2bd-68be43c4b5d8": local!details.newValue
      ),
      char(10),
      if(
        ri!data[recordType!CMGT_EventHistory_Task.task.taskBehaviorTypeId] = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
        {
          #"urn:appian:translation-string:v1:6ddd8757-400b-4b3e-831d-104bf494c1de"(
            #"urn:appian:translation-variable:v1:6577e335-2c33-4e71-a854-63d183c0ef0d": local!details.automationType
          )
        },
        #"urn:appian:translation-string:v1:0c505a3f-efa5-4335-8a88-38e6b58fabcf"(
          #"urn:appian:translation-variable:v1:71b17c88-e24f-4229-b8ac-1b067deda73f": local!details.taskBehaviorType
        )
      )
    ),
    whenTrue: contains(
      {
        cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_STATUS,
        cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_DUE_DATE
      },
      fv!value
    ),
    then: #"urn:appian:translation-string:v1:0c505a3f-efa5-4335-8a88-38e6b58fabcf"(
      #"urn:appian:translation-variable:v1:71b17c88-e24f-4229-b8ac-1b067deda73f": local!details.taskBehaviorType
    ),
    default: local!details.value
  )
)

