a!localVariables(
  local!allbranches: if(
    ri!context = "MODIFY_WORKFLOW",
    a!flatten(
      ri!tasks[recordType!CMGT_Task.beforeTaskInstanceBranches]
    ),
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!tasks,
        expression: rule!CMGT_QR_GetTaskBranch(
          searchTerm: fv!item[recordType!CMGT_Task.taskInstanceUuid],
          searchFields: {
            recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
            recordType!CMGT_TaskBranch.beforeTaskInstanceUuid
          }
        )
      )
    )
  ),
  rule!CMGT_UTIL_DistinctRecords(
    records: {
    rule!CMGT_UTIL_RejectBlank(
      a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: local!allbranches,
          expression: a!localVariables(
            local!isNewOccurrence: or(
              index(
                index(
                  ri!taskInstanceUuidMapping,
                  wherecontains(
                    touniformstring(
                      fv!item[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid]
                    ),
                    touniformstring(
                      index(
                        ri!taskInstanceUuidMapping,
                        "oldUuid",
                        null()
                      )
                    )
                  ),
                  null()
                ),
                "isNewOccurrenceNeeded",
                null()
              ),
              index(
                index(
                  ri!taskInstanceUuidMapping,
                  wherecontains(
                    touniformstring(
                      fv!item[recordType!CMGT_TaskBranch.afterTaskInstanceUuid]
                    ),
                    touniformstring(
                      index(
                        ri!taskInstanceUuidMapping,
                        "oldUuid",
                        null()
                      )
                    )
                  ),
                  null()
                ),
                "isNewOccurrenceNeeded",
                null()
              )
            ),
            if(
              local!isNewOccurrence,
              {
                #"urn:appian:function:v1:a:update"(
                  data: fv!item,
                  index: recordType!CMGT_TaskBranch.isCurrent,
                  value: false()
                ),
                recordType!CMGT_TaskBranch(
                  recordType!CMGT_TaskBranch.taskBranchId: null,
                  recordType!CMGT_TaskBranch.taskResponseOptionUuid: displayvalue(
                    fv!item[recordType!CMGT_TaskBranch.taskResponseOptionUuid],
                    index(
                      a!flatten(
                        index(
                          ri!taskInstanceUuidMapping,
                          "taskResponseOptionsUuidMapping",
                          {}
                        )
                      ),
                      "taskResponseOptionUUID",
                      null()
                    ),
                    index(
                      a!flatten(
                        index(
                          ri!taskInstanceUuidMapping,
                          "taskResponseOptionsUuidMapping",
                          {}
                        )
                      ),
                      "newUuid",
                      null()
                    ),
                    fv!item[recordType!CMGT_TaskBranch.taskResponseOptionUuid]
                  ),
                  recordType!CMGT_TaskBranch.beforeTaskInstanceUuid: displayvalue(
                    fv!item[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid],
                    index(
                      ri!taskInstanceUuidMapping,
                      "oldUuid",
                      {}
                    ),
                    index(
                      ri!taskInstanceUuidMapping,
                      "newUuid",
                      {}
                    ),
                    fv!item[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid]
                  ),
                  recordType!CMGT_TaskBranch.afterTaskInstanceUuid: displayvalue(
                    fv!item[recordType!CMGT_TaskBranch.afterTaskInstanceUuid],
                    index(
                      ri!taskInstanceUuidMapping,
                      "oldUuid",
                      {}
                    ),
                    index(
                      ri!taskInstanceUuidMapping,
                      "newUuid",
                      {}
                    ),
                    fv!item[recordType!CMGT_TaskBranch.afterTaskInstanceUuid]
                  ),
                  recordType!CMGT_TaskBranch.isCurrent: if(
                    fv!item[recordType!CMGT_TaskBranch.isCurrent] = false(),
                    false(),
                    true()
                  ),
                  recordType!CMGT_TaskBranch.isRequired: fv!item[recordType!CMGT_TaskBranch.isRequired],
                  recordType!CMGT_TaskBranch.isActive: true,
                  recordType!CMGT_TaskBranch.conditionSetUuid: displayvalue(
                    fv!item[recordType!CMGT_TaskBranch.conditionSetUuid],
                    index(
                      a!flatten(
                        index(
                          ri!taskInstanceUuidMapping,
                          "childConditionSetMapping",
                          {}
                        )
                      ),
                      "conditionSetUuid",
                      null()
                    ),
                    index(
                      a!flatten(
                        index(
                          ri!taskInstanceUuidMapping,
                          "childConditionSetMapping",
                          {}
                        )
                      ),
                      "newUuid",
                      null()
                    ),
                    fv!item[recordType!CMGT_TaskBranch.conditionSetUuid]
                  )
                )
              },
              fv!item
            )
          )
        )
      )
    ),
   }
  )
)

