a!localVariables(
  local!originalTasks: if(
    rule!CMGT_UTIL_IsBlank(ri!tasks),
    null,
    rule!CMGT_QR_GetTask(
      taskId: ri!tasks[recordType!CMGT_Task.taskId],
      includeInactive: true,
      executeWhen: rule!CMGT_UTIL_IsNotBlank(
        a!flatten(
          ri!tasks[recordType!CMGT_Task.taskId]
        )
      ),
      includeMilestone: true(),
      fields: {
        recordType!CMGT_Task.conditionSet,
        recordType!CMGT_Task.taskResponseOptions,
        recordType!CMGT_Task.conditionSet,
        recordType!CMGT_Task.createCaseTask,
        recordType!CMGT_Task.sendEmailTask,
        recordType!CMGT_Task.automationTaskAction
      }
    )
  ),
  local!originalAutomationWorkflowTasks:if(
    rule!CMGT_UTIL_IsBlank(ri!tasks),
    null,
    rule!CMGT_QR_GetTask(
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_Task.automationParentWorkflowTaskInstanceUuid,
        operator: "in",
        value: ri!tasks[recordType!CMGT_Task.taskInstanceUuid]
      )
    },
    fields: {
      recordType!CMGT_Task.conditionSet,
      recordType!CMGT_Task.conditionSet,
      recordType!CMGT_Task.conditionSet,
      #"urn:appian:record-relationship:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/5189c3ac-98e2-4a82-8598-2e9561baec9d/64b891e4-359b-48b4-9f47-2a3af4695c34/52813580-ecf3-49c7-be71-568ab452d54a",
      recordType!CMGT_Task.automationTaskAction,
      recordType!CMGT_Task.automationTaskAction,
      #"urn:appian:record-relationship:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/45ed19eb-2545-4c96-9e19-72127f9d27a3/dc83b26a-0034-46f4-8085-5887ad59bc08/86c11a87-1ab5-4e62-93f2-b7d24e412b3a"
    },
    taskIntentionCodes: cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_RUNTIME_CONFIG_TRIGGER
  )),
  local!activeTasks: rule!CMGT_Task_ConstructRelationshipsForActiveTasks(
    tasks: ri!tasks,
    context: ri!context,
    originalTasks: local!originalTasks,
    originalAutomationWorkflowTasks: local!originalAutomationWorkflowTasks
  ),
  local!updatedActiveTasks: rule!CMGT_UTIL_RejectBlank(
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        /*Only Active Tasks are updated*/
        items: local!activeTasks,
        expression: a!localVariables(
          /*The current value of this record in the database*/
          local!orgItem: rule!CMGT_UTIL_Filter(
            records: local!originalTasks,
            field: recordType!CMGT_Task.taskId,
            value: fv!item[recordType!CMGT_Task.taskId]
          ),
          local!thisTaskInstanceUuidMapping: index(
            ri!taskInstanceUuidMapping,
            wherecontains(
              tointeger(
                fv!item[recordType!CMGT_Task.taskId]
              ),
              tointeger(
                index(
                  ri!taskInstanceUuidMapping,
                  "taskId",
                  null()
                )
              )
            ),
            null()
          ),
          local!isNewOccurrenceNeeded: index(
            index(
              local!thisTaskInstanceUuidMapping,
              "isNewOccurrenceNeeded",
              null()
            ),
            1,
            null()
          ),
          if(
            toboolean(local!isNewOccurrenceNeeded) = true(),
            {
              /*Old occurrence update - As new changes should only be reflected in the new instance*/
              rule!CMGT_UTIL_SetRecordFields(
                record: local!orgItem,
                alwaysOverwrite: {
                  a!map(
                    field: recordType!CMGT_Task.modifiedBy,
                    value: cons!CMGT_TXT_PROCESS_AUTOMATION
                  ),
                  a!map(
                    field: recordType!CMGT_Task.isCurrent,
                    value: false()
                  )
                },
                /*Branches will be constructed in a separate rule*/
                childRelationships: {
                  a!map(
                    field: recordType!CMGT_Task.beforeTaskInstanceBranches,
                    value: {}
                  ),
                  /*When the task is reopened the comments added for reopening should be linked
                  to the old case*/
                  a!map(
                    field: recordType!CMGT_Task.comments,
                    value: fv!item[recordType!CMGT_Task.comments],
                    applyWhen: ri!context = "REOPEN"
                  ),
                  a!map(
                    field: recordType!CMGT_Task.documents,
                    value: fv!item[recordType!CMGT_Task.documents],
                    applyWhen: ri!context = "REOPEN"
                  )
                }
              ),
              /*New occurrence*/
              cast(
                recordType!CMGT_Task,
                rule!CMGT_Task_ConstructTaskOccurrence(
                  task: fv!item,
                  taskInstanceUuidMapping: ri!taskInstanceUuidMapping
                )
              )
            },
            rule!CMGT_UTIL_SetRecordFields(
              record: fv!item,
              /*Branches will be constructed in a separate rule*/
              childRelationships: {
                a!map(
                  field: recordType!CMGT_Task.beforeTaskInstanceBranches,
                  value: {}
                )
              }
            )
          )
        )
      )
    )
  ),
  local!archivedTaskIndex: wherecontains(
    false(),
    toboolean(
      local!updatedActiveTasks[recordType!CMGT_Task.isCurrent]
    )
  ),
  local!archivedTask: index(
    local!updatedActiveTasks,
    local!archivedTaskIndex,
    null
  ),
  local!unarchivedTasks: if(
    a!isNotNullOrEmpty(local!archivedTaskIndex),
    remove(
      local!updatedActiveTasks,
      local!archivedTaskIndex
    ),
    local!updatedActiveTasks
  ),
  /*Sort Order update - Done for only Active tasks*/
  /*This is done specifically on the Modify Workflow*/
  local!sortedTasks: if(
    ri!isSortTasks,
    #"SYSTEM_SYSRULES_forEach"(
      local!unarchivedTasks,
      #"urn:appian:function:v1:a:update"(
        fv!item,
        recordType!CMGT_Task.sortOrder,
        fv!index
      )
    ),
    local!unarchivedTasks
  ),
  local!inActiveTasks: rule!CMGT_UTIL_Filter(
    records: ri!tasks,
    field: recordType!CMGT_Task.isActive,
    value: false()
  ),
  local!inActivePreviousInstances: if(
    rule!CMGT_UTIL_IsBlank(local!inActiveTasks),
    {},
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!inActiveTasks,
        expression: a!localVariables(
          local!previousInstances: rule!CMGT_QR_GetTask(
            returnType: "OBJECT_ARRAY",
            additionalFilters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!CMGT_Task.taskUuid,
                operator: "=",
                value: fv!item[recordType!CMGT_Task.taskUuid]
              )
            },
            isCurrentTask: false
          ),
          if(
            rule!CMGT_UTIL_IsNotBlank(local!previousInstances),
            #"urn:appian:function:v1:a:update"(
              local!previousInstances,
              recordType!CMGT_Task.isActive,
              false
            ),
            {}
          )
        )
      )
    )
  ),
  rule!CMGT_UTIL_RejectBlank(
    {
      local!sortedTasks,
      local!archivedTask,
      local!inActiveTasks,
      local!inActivePreviousInstances
    }
  )
)

