_a-0000ea26-2ced-8000-9bf1-011c48011c48_.sail_code:
/*Using the user tags for a comment, 
this is the save logic used to identify document maps that have been kept, added, and deleted
*/
a!localVariables(
  local!temp,
  {
    /*Decipher document maps to keep active and which to delete*/
    a!save(
      local!temp,
      a!localVariables(
        /*Get the unique selected values from the picker fields*/
        local!uniqueSelectedValues: rule!CMGT_UTIL_Distinct(save!value),
        local!allExistingDocumentMapsForThisComment: rule!CMGT_UTIL_Filter(
          records: ri!commentDocumentMaps,
          field: recordType!CMGT_CommentDocumentMap.documentId,
          value: null,
          filterType: "REJECT"
        ),
        local!newDocumentCommentDocumentMapRecords:rule!CMGT_UTIL_Filter(
          records: ri!commentDocumentMaps,
          field:recordType!CMGT_CommentDocumentMap.documentId,
          value: null
        ),
        local!newCommentDocumentMapRecords: #"SYSTEM_SYSRULES_forEach"(
          items: local!uniqueSelectedValues,
          expression: displayvalue(
            tostring(fv!item),
            touniformstring(
              local!allExistingDocumentMapsForThisComment[recordType!CMGT_CommentDocumentMap.documentId]
            ),
            local!allExistingDocumentMapsForThisComment,
            recordType!CMGT_CommentDocumentMap(
              recordType!CMGT_CommentDocumentMap.commentId: ri!commentId,
              recordType!CMGT_CommentDocumentMap.documentId: fv!item,
              recordType!CMGT_CommentDocumentMap.isActive: true,
              recordType!CMGT_CommentDocumentMap.commentAction: "LINK"
            )
          )
        ),
        local!deletedCommentDocumentMapRecords: a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!allExistingDocumentMapsForThisComment,
            expression: if(
              and(
                not(
                  contains(
                    touniformstring(local!uniqueSelectedValues),
                    tostring(
                      fv!item[recordType!CMGT_CommentDocumentMap.documentId]
                    )
                  )
                ),
                a!isNotNullOrEmpty(
                  fv!item[recordType!CMGT_CommentDocumentMap.commentDocumentMapId]
                )
              ),
              #"urn:appian:function:v1:a:update"(
                fv!item,
                recordType!CMGT_CommentDocumentMap.isActive,
                false
              ),
              {}
            )
          )
        ),
        /*Flatten back the maps we modified*/
        a!flatten(
          {
            local!newCommentDocumentMapRecords,
            local!newDocumentCommentDocumentMapRecords,
            local!deletedCommentDocumentMapRecords
          }
        )
      )
    ),
    /*Finally, save the map values back into their dedicated variables*/
    a!save(
      ri!commentDocumentMaps,
      local!temp
    )
  }
)

