rule!CMGT_UTIL_QueryRecord(
  recordType: recordType!CMGT_EML_TRI_Thread,
  returnType: ri!returnType,
  executeWhen: ri!executeWhen,
  triggerRefresh: ri!triggerRefresh,
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_EML_TRI_Thread.threadId,
      operator: "in",
      value: ri!threadIds
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_EML_TRI_Thread.threadSetId,
      operator: "in",
      value: ri!threadSetIds
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_EML_TRI_Thread.threadStatus,
      operator: "in",
      value: ri!threadStatuses
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_EML_TRI_Thread.subject,
      operator: "in",
      value: ri!subjects
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_EML_TRI_Thread.messages.messageId,
      operator: "in",
      value: ri!messageIds
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_EML_TRI_Thread.isActive,
      operator: "in",
      value: { a!defaultValue(ri!isActive, true) }
    ),
    ri!additionalFilter
  },
  logicalExpressions: {
    if(
      a!isNotNullOrEmpty(ri!searchTerm),
      {
        #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "AND",
          logicalExpressions: #"SYSTEM_SYSRULES_forEach"(
            items: split(trim(ri!searchTerm), " "),
            expression: a!localVariables(
              local!splitSearchTerm: fv!item,
              #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "OR",
                filters: #"SYSTEM_SYSRULES_forEach"(
                  items: ri!searchFields,
                  expression: #"SYSTEM_SYSRULES_queryFilter"(
                    field: fv!item,
                    operator: "includes",
                    value: local!splitSearchTerm
                  )
                )
              )
            )
          )
        )
      },
      {}
    ),
    ri!additionalLogicalExpressions
  },
  fields: ri!fields,
  relatedRecordData: ri!relatedRecordData,
  pagingInfo: ri!pagingInfo,
  sort: ri!sort,
  groupByFields: ri!groupByFields,
)

