    /*Created On*/"4db722a4-2e1f-4d17-a168-0ae254a83407",
    /*Modified On */"12a54b35-6aad-4cf0-b04a-700cc54a4917",

  },
  local!contextFieldsToReject: a!match(
    value: ri!context,
    /*If no context is passed, then it's assumed to be used when configuring a condition, which rejects Due On & Completed On    */
    whenTrue: rule!CMGT_UTIL_IsBlank(ri!context),
    then: {
      /*Due On*/"dd6a7d01-9f60-4e65-b322-da301c36bcbb",
      /*Completed On*/"71e2da70-92e0-4ace-93b2-67247ad82b96"
    },
    default: a!flatten(
      {
        local!defaultContextFieldsToReject,
        a!match(
          value: ri!context,
          /*If the context is blank then should    */
          whenTrue: rule!CMGT_UTIL_IsBlank(ri!context),
          /*fields to hide within the fields dropdown of the case field is updated trigger*/
