_a-0000ed24-7935-8000-9d2d-011c48011c48_.sail_code:
if(
  a!defaultValue(value: ri!executeWhen, default: true()),
  a!localVariables(
    local!statuses: {
      "ACTIVE",
      "COMPLETED",
      "PAUSED",
      "CANCELLED",
      "PAUSED_BY_EXCEPTION"
    },
    local!fieldMap: {
      a!map(column: "c0", field: "processName"),
      a!map(column: "c1", field: "status"),
      a!map(column: "c2", field: "startTime"),
      a!map(column: "c4", field: "caseId"),
      a!map(column: "c5", field: "processId")
    },
    #"SYSTEM_SYSRULES_forEach"(
      items: a!queryProcessAnalytics(
        report: cons!CMGT_WORKFLOW_PATH_MAPPING_PROCESSES_BY_PROCESS_MODEL,
        contextProcessModels: a!defaultValue(
          ri!contextProcessModels,
          {
            cons!CMGT_PM_WORKFLOW_PATH_CREATE_UPDATE,
            cons!CMGT_PM_PROCESS_UNCHOSEN_PATH_AND_LOOPBACK,
            cons!CMGT_PM_WRITE_TASK_DETAILS
          }
        ),
        query: #"SYSTEM_SYSRULES_query"(
          pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: - 1),
          logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: "c4",
                operator: "=",
                value: ri!caseId,
                applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!caseId)
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: "c1",
                operator: "in",
                value: wherecontains(ri!status, local!statuses) - 1,
                applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!status)
              )
            }
          )
        )
      ).data,
      expression: reduce(
        #"urn:appian:function:v1:a:update",
        a!map(),
        merge(
          local!fieldMap.field,
          apply(
            index(fv!item, _, null()),
            local!fieldMap.column
          )
        )
      )
    )
  ),
  {}
)

