_a-0000ecbe-e702-8000-9cf8-011c48011c48_.sail_code:
/*This rule groups the task branches as how they should be displayed in the dependents dropdown 
based on response option, requiredness, requiredness valid, conditionset and returns the result in a map format*/
a!localVariables(
  local!activeBranches: rule!CMGT_UTIL_Filter(
    records: ri!taskBranches,
    field: recordType!CMGT_TaskBranch.isActive,
    value: false,
    filterType: "REJECT"
  ),
  local!automationInstanceUUIDs: rule!CMGT_UTIL_Distinct(
    rule!CMGT_UTIL_Filter(
      records: ri!case[recordType!CMGT_Case.tasks],
      field: recordType!CMGT_Task.taskBehaviorTypeId,
      value: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
    )[recordType!CMGT_Task.taskInstanceUuid]
  ),
  local!automationBranches: rule!CMGT_UTIL_Filter(
    records: local!activeBranches,
    fieldValuePairs: {
      a!map(
        field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
        value: local!automationInstanceUUIDs
      )
    }
  ),
  /*  Grouping automation task branches*/
  local!automationGroupMaps: rule!CMGT_GroupTaskBranches_Automations(
    taskBranch: local!automationBranches,
    case: ri!case
  ),
  local!activeTaskBranches: rule!CMGT_UTIL_Filter(
    records: local!activeBranches,
    fieldValuePairs: {
      a!map(
        field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
        value: local!automationInstanceUUIDs
      )
    },
    filterType: "REJECT",

  ),
  local!distinctBeforeTaskInstanceUUID: rule!CMGT_UTIL_Distinct(
    local!activeTaskBranches[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid]
  ),
  /*Grouping task based on response option  */
  local!distinctResponseOptions: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!distinctBeforeTaskInstanceUUID,
      expression: a!localVariables(
        local!thisInstanceUUID: fv!item,
        #"SYSTEM_SYSRULES_forEach"(
          items: rule!CMGT_UTIL_Distinct(
            rule!CMGT_UTIL_Filter(
              records: local!activeBranches,
              field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
              value: local!thisInstanceUUID
            )[recordType!CMGT_TaskBranch.taskResponseOptionUuid]
          ),
          expression: rule!CMGT_ConstructTaskBranchMap(
            taskInstanceUUID: local!thisInstanceUUID,
            taskResponseOptionUUID: fv!item
          )
        )
      )
    )
  ),
  /* Grouping local!distinctResponseOptions based on requiredness */
  local!distinctionRequiredness: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!distinctResponseOptions,
      expression: a!localVariables(
        local!thisItem: fv!item,
        local!thisTaskBranches: rule!CMGT_UTIL_Filter(
          records: local!activeBranches,
          fieldValuePairs: {
            a!map(
              field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
              value: local!thisItem.taskInstanceUUID
            ),
            a!map(
              field: recordType!CMGT_TaskBranch.taskResponseOptionUuid,
              value: local!thisItem.taskResponseOptionUUID
            ),
            a!map(
              field: recordType!CMGT_TaskBranch.conditionSetUuid,
              value: local!thisItem.conditionSetUUID
            )
          }
        ),
        #"SYSTEM_SYSRULES_forEach"(
          items: rule!CMGT_UTIL_Distinct(
            local!thisTaskBranches[recordType!CMGT_TaskBranch.isRequired]
          ),
          expression: rule!CMGT_ConstructTaskBranchMap(
            taskInstanceUUID: local!thisItem.taskInstanceUUID,
            taskResponseOptionUUID: local!thisItem.taskResponseOptionUUID,
            isRequired: fv!item,
            conditionSetUUID: local!thisItem.conditionSetUUID,
            afterTaskBranches: rule!CMGT_UTIL_Filter(
              records: local!thisTaskBranches,
              field: recordType!CMGT_TaskBranch.isRequired,
              value: fv!item
            ),
            isActive: true
          )
        )
      )
    )
  ),
  /* Grouping local!distinctionRequiredness based on requiredness valid */
  local!distinctRequirednessValid: #"SYSTEM_SYSRULES_forEach"(
    items: local!distinctionRequiredness,
    expression: a!localVariables(
      local!thisMap: fv!item,
      local!startsByDefault_afterTaskBranches: cast(
        #"urn:appian:function:v1:a:listtype?okey=a!listType"(
          recordType!CMGT_TaskBranch
        ),
        rule!CMGT_UTIL_Filter(
          records: local!thisMap.afterTaskBranches,
          field: recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
          value: index(
            rule!CMGT_UTIL_Filter(
              records: ri!case[recordType!CMGT_Case.tasks],
              field: recordType!CMGT_Task.startsByDefault,
              value: true
            ),
            recordType!CMGT_Task.taskInstanceUuid,
            {}
          )
        )
      ),
      local!loopBackTasks_afterTaskBranches: cast(
        #"urn:appian:function:v1:a:listtype?okey=a!listType"(
          recordType!CMGT_TaskBranch
        ),
        rule!CMGT_UTIL_Filter(
          records: local!thisMap.afterTaskBranches,
          field: recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
          value: rule!CMGT_Tasks_FindPreviousTasksInTree(
            allTasks: ri!case[recordType!CMGT_Case.tasks],
            taskInstanceUUID: local!thisMap.taskInstanceUUID,
            taskBranches: local!activeBranches
          )[recordType!CMGT_Task.taskInstanceUuid]
        )
      ),
      {
        /*isRequirednessValid = true         */
        if(
          rule!CMGT_UTIL_IsNotBlank(
            difference(
              cast(
                #"urn:appian:function:v1:a:listtype?okey=a!listType"(
                  recordType!CMGT_TaskBranch
                ),
                fv!item.afterTaskBranches
              ),
              a!flatten(
                {
                  local!startsByDefault_afterTaskBranches,
                  local!loopBackTasks_afterTaskBranches
                }
              )
            )
          ),
          rule!CMGT_ConstructTaskBranchMap(
            taskInstanceUUID: local!thisMap.taskInstanceUUID,
            taskResponseOptionUUID: local!thisMap.taskResponseOptionUUID,
            isRequired: local!thisMap.isRequired,
            isRequirednessValid: true,
            conditionSetUUID: fv!item.conditionSetUUID,
            afterTaskBranches: difference(
              cast(
                #"urn:appian:function:v1:a:listtype?okey=a!listType"(
                  recordType!CMGT_TaskBranch
                ),
                fv!item.afterTaskBranches
              ),
              a!flatten(
                {
                  local!startsByDefault_afterTaskBranches,
                  local!loopBackTasks_afterTaskBranches
                }
              )
            ),
            isActive: fv!item.isActive
          ),
          null
        ),
        /*isRequirednessValid = false         */
        if(
          rule!CMGT_UTIL_IsNotBlank(
            union(
              local!startsByDefault_afterTaskBranches,
              local!loopBackTasks_afterTaskBranches
            )
          ),
          rule!CMGT_ConstructTaskBranchMap(
            taskInstanceUUID: local!thisMap.taskInstanceUUID,
            taskResponseOptionUUID: local!thisMap.taskResponseOptionUUID,
            isRequired: local!thisMap.isRequired,
            isRequirednessValid: false,
            conditionSetUUID: fv!item.conditionSetUUID,
            afterTaskBranches: union(
              local!startsByDefault_afterTaskBranches,
              local!loopBackTasks_afterTaskBranches
            ),
            isActive: fv!item.isActive
          ),
          null
        )
      }
    )
  ),
  cast(
    #"urn:appian:function:v1:a:listtype?okey=a!listType"(
      'type!{http://www.appian.com/ae/types/2009}Map'
    ),
    rule!CMGT_UTIL_RejectBlank(
      a!flatten(
        merge(
          local!distinctRequirednessValid,
          local!automationGroupMaps
        )
      )
    )
  )
)

