    a!isNullOrEmpty(ri!conditionSet),
    ri!conditionSet[recordType!CMGT_ConditionSet.isActive] = false()
  ),
  false,
  a!localVariables(
    local!conditions: cast(
      #"urn:appian:function:v1:a:listtype?okey=a!listType"(
        recordType!CMGT_Condition
      ),
      rule!CMGT_UTIL_Filter(
        records: ri!conditionSet[recordType!CMGT_ConditionSet.condition],
        field: recordType!CMGT_Condition.isActive,
        value: false(),
        filterType: "REJECT"
      )
    ),
    local!evaluatedConditions: #"SYSTEM_SYSRULES_forEach"(
      items: local!conditions,
      expression: rule!CMGT_EVAL_EvaluateConditionDynamic(
        condition: fv!item,
        data: ri!data,
        availableFields: ri!availableFields
      )
    ),
    a!map(
      result: a!match(
        value: ri!conditionSet[recordType!CMGT_ConditionSet.operatorCode],
        equals: cons!CMGT_TXT_SET_OPERATOR_AND,
        then: and(
          local!evaluatedConditions[recordType!CMGT_Condition.isMet]
        ),
        equals: cons!CMGT_TXT_SET_OPERATOR_OR,
        then: or(
          local!evaluatedConditions[recordType!CMGT_Condition.isMet]
        ),
        default: error(fv!value)
      ),
      updatedConditionOutcomes: local!evaluatedConditions
    )
  )
)

