                  operator: "search",
                  value: ri!searchTerm,
                  applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!searchTerm)
                ),
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: recordType!CMGT_Task.description,
                  operator: "search",
                  value: ri!searchTerm,
                  applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!searchTerm)
                ),
                /*Comment*/
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: recordType!CMGT_Task.comments.commentText,
                  operator: "search",
                  value: ri!searchTerm,
                  applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!searchTerm)
                )
              }
            ),
            #"SYSTEM_SYSRULES_queryLogicalExpression"(
              operator: "AND",
              filters: {
                /*Assignee*/
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: recordType!CMGT_Task.roles.username,
                  operator: "in",
                  value: ri!assignee,
                  applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!assignee)
                ),
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: recordType!CMGT_Task.roles.roleTypeId,
                  operator: "=",
                  value: cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE,
                  applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!assignee)
                )
              }
            ),
            #"SYSTEM_SYSRULES_queryLogicalExpression"(
              operator: "AND",
              filters: {
                /*Assigned Group*/
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: recordType!CMGT_Task.roles.groupId,
                  operator: "in",
                  value: ri!assignedGroup,
                  applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!assignedGroup)
                ),
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: recordType!CMGT_Task.roles.roleTypeId,
                  operator: "=",
                  value: cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE,
                  applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!assignedGroup)
                )
              }
            )
          },
          filters: {
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!CMGT_Task.taskBehaviorType.taskBehaviorTypeId,
              operator: "in",
              value: ri!taskTypesIds,
              applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!taskTypesIds)
            ),
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!CMGT_Task.status.statusId,
              operator: "in",
              value: ri!statusIds,
              applyWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!statusIds)
            ),
            #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!CMGT_Task.taskIntentionCode,
              operator: "=",
              value: cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_WORKFLOW
            )
          }
        ),
        relatedRecordData: {
          #"SYSTEM_SYSRULES_relatedRecordData"(
            relationship: recordType!CMGT_Task.roles,
