              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!CMGT_Role.roleTypeId,
                operator: "in",
                value: {
                  cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE,
                  cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE
                }
              )
            }
          )
        },
        pagingInfo: a!defaultValue(
          value: ri!pagingInfo,
          default: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: 10, )
        ),
        fetchTotalCount: true()
      ),
      refreshOnVarChange: ri!triggerRefresh
    ),
    local!data
  ),
  {}
)

