  /*Pre-existing comments have no summary*/
  or(
    ri!commentCharCount = 0,
    and(
      a!isNullOrEmpty(ri!activeProcesses),
      a!isNullOrEmpty(ri!commentSummary),
      ri!commentCharCount <> 0
    )
  ),
  "HIDE",
  if(
    ri!commentCharCount < cons!CMGT_CT_AI_MIN_LENGTH_TO_SUMMARIZE_COMMENT,
    "NOT_ENOUGH_INFO",
    a!match(
      /*Summarization in process?*/
      value: a!isNotNullOrEmpty(ri!activeProcesses),
      equals: true,
      then: "GENERATING_SUMMARY",
      default: a!match(
        /*Summarization completed. Error or summary available*/
        value: a!isNotNullOrEmpty(
          ri!commentSummary[recordType!CMGT_CMT_AI_CaseCommentSummary.summary]
        ),
        equals: true,
        then: "DISPLAY_SUMMARY",
        default: "INVALID"
      )
    )
  )
)

