_a-0000e9fd-9441-8000-9bdc-011c48011c48_.sail_code:
a!localVariables(
  /*Get the other related tasks for the case*/
  local!otherTasksToCheckAgainst: rule!CMGT_QR_GetTask(
    caseId: ri!createdTasks[recordType!CMGT_Task.caseId]
  ),
  /*Determine which tasks to open*/
  /*If there are already tasks open, then there's no need to start new tasks  */
  local!tasksToOpen: if(
    rule!CMGT_UTIL_IsNotBlank(
      rule!CMGT_UTIL_Filter(
        records: ri!createdTasks,
        field: recordType!CMGT_Task.statusId,
        value: {
          cons!CMGT_REFID_STATUS_TASK_READY,
          cons!CMGT_REFID_STATUS_TASK_IN_PROGRESS
        }
      )
    ),
    null,
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        /*Loop over each task that could be opened (only ones in Queued state)*/
        items: rule!CMGT_UTIL_Filter(
          records: ri!createdTasks,
          field: recordType!CMGT_Task.statusId,
          value: cons!CMGT_REFID_STATUS_TASK_QUEUED
        ),
        expression: if(
          /*Early exit if the task should start by default*/
          fv!item[recordType!CMGT_Task.startsByDefault],
          fv!item,
          a!localVariables(
            local!thisTask: fv!item,
            /*Get the branches leaading into this task*/
            local!beforeBranches: rule!CMGT_UTIL_Filter(
              records: local!thisTask[recordType!CMGT_Task.beforeTaskInstanceBranches],
              field: recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
              value: local!thisTask[recordType!CMGT_Task.taskInstanceUuid]
            ),
            /*Determine which branches have yet to be satisfied*/
            local!unsatisfiedBeforeBranches: a!flatten(
              #"SYSTEM_SYSRULES_forEach"(
                items: local!beforeBranches,
                expression: a!localVariables(
                  local!thisBranch: fv!item,
                  /*Get the specific task that leads into this branch (the previous task)*/
                  local!beforeTaskForBranch: displayvalue(
                    local!thisBranch[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid],
                    local!otherTasksToCheckAgainst[recordType!CMGT_Task.taskInstanceUuid],
                    local!otherTasksToCheckAgainst,
                    null
                  ),
                  if(
                    tointeger(
                      local!beforeTaskForBranch[recordType!CMGT_Task.taskBehaviorTypeId]
                    ) = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
                    fv!item,
                    if(
                      /*If there is no before task for this branch, consider it satisfied*/
                      a!isNullOrEmpty(local!beforeTaskForBranch),
                      {},
                      if(
                        /*If the task is not complete, or it's complete but the response option is incorrect, consider it unsatsified*/
                        or(
                          not(
                            local!beforeTaskForBranch[recordType!CMGT_Task.isCompleted]
                          ),
                          local!thisBranch[recordType!CMGT_TaskBranch.taskResponseOptionUuid] <> local!beforeTaskForBranch[recordType!CMGT_Task.selectedTaskResponseOption.taskResponseOptionUuid]
                        ),
                        fv!item,
                        {}
                      )
                    )
                  )
                )
              )
            ),
            /*If there are no unsatisfied branches, it means this task can be opened*/
            if(
              a!isNullOrEmpty(local!unsatisfiedBeforeBranches),
              fv!item,
              {}
            )
          )
        )
      )
    )
  ),
  if(
    a!isNullOrEmpty(local!tasksToOpen),
    {},
    /*All initial tasks are opened irrespective of milestone    */
    #"urn:appian:function:v1:a:update"(
      local!tasksToOpen,
      recordType!CMGT_Task.statusId,
      cons!CMGT_REFID_STATUS_TASK_READY
    )
  )
)

