    value: rule!CMGT_DOC_AI_QPA_GetProcessesByDocumentId(
      contextProcessModels: {
        cons!CMGT_DOC_AI_PM_CASE_DOCUMENT_SUMMARY
      },
      documentId: ri!selectedDocument[recordType!CMGT_Document.documentId],
      executeWhen: rule!CMGT_UTIL_IsNotBlank(
        input: ri!selectedDocument[recordType!CMGT_Document.documentId]
      ),
      status: "ACTIVE"
    ),
    /*refreshAfter: "RECORD_ACTION",*/
    refreshInterval: local!refreshInterval,
    refreshAlways: true
  ),
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshOnVarChange: local!activeProcesses
  ),
  local!documentSummary: rule!CMGT_DOC_AI_QR_GetDocumentSummary(
    documentId: ri!selectedDocument[recordType!CMGT_Document.documentId],
    returnType: "SINGLE_OBJECT",
