    array: {
      union(
        tointeger(
          index(
            pv!tasksToOpen,
            recordType!CMGT_Task.caseId,
            null
          )
        ),
        tointeger(
          index(
            pv!createdTasks,
            recordType!CMGT_Task.caseId,
            null
          )
        ),
        tointeger(
          index(
            pv!completedTasks,
            recordType!CMGT_Task.caseId,
            null
          )
        )
      )
    }
  ),
  1,
  null()
)

// Input: ?
"UPDATE"

// Output: ?
rule!CMGT_Task_SetMetaData(
