    rule!CMGT_UTIL_Filter(
      records: pv!tasksToOpen,
      field: recordType!CMGT_Task.taskBehaviorTypeId,
      value: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
    )[recordType!CMGT_Task.taskUuid]
  ),
  automationEvaluationLoopExecutions: pv!automationEvaluationLoopExecutions
)

// Input: ?
=pv!automationEvaluationLoopExecutions

