=if(
  rule!CMGT_UTIL_IsNotBlank(pv!completedTasks),
  rule!CMGT_QR_GetTask(
    taskId: pv!completedTasks[recordType!CMGT_Task.taskId],
    includeBranches: true()
  ),
  null
)

// Input: ?
=if(
  rule!CMGT_UTIL_IsNotBlank(pv!completedTasks),
  index(
    pv!completedTasks[recordType!CMGT_Task.caseId],
    1,
    null
  ),
  null
