_a-0000eb57-0ad2-8000-9c6b-011c48011c48_.sail_code:
a!localVariables(
  local!precedentEdges: a!refreshVariable(
    value: rule!CMGT_UTIL_Filter(
      records: ri!allEdges,
      fieldValuePairs: {
        a!map(field: "to", value: ri!currentNode.id)
      }
    ),
    refreshAlways: true
  ),
  local!requiredPrecedentEdges: a!refreshVariable(
    value: rule!CMGT_UTIL_Filter(
      records: local!precedentEdges,
      fieldValuePairs: { a!map(field: "dashes", value: false) }
    ),
    refreshAlways: true
  ),
  local!precedentEdgesFromSortedNodes: a!refreshVariable(
    value: rule!CMGT_UTIL_Filter(
      records: ri!edgesToUnorderedNodes,
      field: "to",
      value: ri!currentNode.id
    ),
    refreshAlways: true
  ),
  if(
    /*since the currentNode is sourced from dependentUnorderedNodes, it will always have at least one precedent*/
    /*if there are no required precedents and the non-required precedents are not sorted, return nothing*/
    and(
      a!isNullOrEmpty(local!requiredPrecedentEdges),
      a!isNullOrEmpty(local!precedentEdgesFromSortedNodes)
    ),
    {},
    or(
      /*if there are no required precedents and any of the non-required precedents are sorted, give it an order */
      and(
        a!isNullOrEmpty(local!requiredPrecedentEdges),
        a!isNotNullOrEmpty(local!precedentEdgesFromSortedNodes)
      ),
      /*if all required precedents are already sorted, give it an order*/
      a!isNullOrEmpty(
        difference(
          local!requiredPrecedentEdges,
          local!precedentEdgesFromSortedNodes
        )
      )
    ),
    #"urn:appian:function:v1:a:update"(
      data: ri!currentNode,
      index: "verticalOrder",
      value: tointeger(
        max(ri!orderedNodes.verticalOrder) + 1
      )
    ),
    /*if the node has required precedents which are not already sorted, return nothing*/
    {}
  )
)

