rule!CMGT_UTIL_QueryRecord(
  recordType: recordType!CMGT_Task,
  returnType: ri!returnType,
  triggerRefresh: ri!triggerRefresh,
  executeWhen: ri!executeWhen,
  groupByFields: ri!groupByFields,
  filters: {
    ri!additionalFilters,
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Task.taskId,
      operator: "in",
      value: ri!taskId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Task.caseId,
      operator: "in",
      value: ri!caseId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Task.statusId,
      operator: "in",
      value: ri!statusId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Task.dueOn,
      operator: ">",
      value: rule!CMGT_UTIL_ConvertDatetoDatetime(
        date: ri!dueOnDate - 1,
        useSystemTimeZone: a!defaultValue(ri!useSystemTimeZoneForDueOn, true)
      )
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Task.dueOn,
      operator: "<=",
      value: rule!CMGT_UTIL_ConvertDatetoDatetime(
        date: ri!dueOnDate,
        useSystemTimeZone: a!defaultValue(ri!useSystemTimeZoneForDueOn, true)
      )
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Task.isActive,
      operator: "=",
      value: true,
      applyWhen: ri!includeInactive <> true
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Task.isCurrent,
      operator: "=",
      value: a!defaultValue(ri!isCurrentTask, true),
      applyWhen: a!defaultValue(ri!isCurrentTaskApplyWhen, true),

    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Task.taskIntentionCode,
      operator: "in",
      value: a!defaultValue(
        ri!taskIntentionCodes,
        {
          cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_WORKFLOW
        }
      )
    ),

  },
  fields: {
    ri!fields,
    if(
      ri!includeRoles,
      {
        recordType!CMGT_Task.roles
      },
      {}
    ),
    if(
      ri!includeMilestone,
      {
        recordType!CMGT_Task.milestone
      },
      {}
    ),
    if(
      ri!includeCase,
      {
        recordType!CMGT_Task.case
      },
      {}
    ),
    if(
      ri!includeBranches,
      {
        recordType!CMGT_Task.beforeTaskInstanceBranches,
        recordType!CMGT_Task.beforeTaskInstanceBranches,
        recordType!CMGT_Task.beforeTaskInstanceBranches,
        #"urn:appian:record-relationship:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/73a323c9-ce12-4212-97aa-f89df7d14c09/9644c5ba-af85-48a3-9cda-fcfb028d6113/3280de96-3518-4a9e-b8ae-854a84562f07"
      },
      {}
    ),
    recordType!CMGT_Task.taskBehaviorType,
    recordType!CMGT_Task.status,
    recordType!CMGT_Task.taskResponseOptions,
    recordType!CMGT_Task.selectedTaskResponseOption,
    recordType!CMGT_Task.conditionSet,
    recordType!CMGT_Task.conditionSet,
    #"urn:appian:record-relationship:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/5189c3ac-98e2-4a82-8598-2e9561baec9d/64b891e4-359b-48b4-9f47-2a3af4695c34/52813580-ecf3-49c7-be71-568ab452d54a",
    recordType!CMGT_Task.createCaseTask,
    recordType!CMGT_Task.sendEmailTask,
    recordType!CMGT_Task.sendEmailTask,
    recordType!CMGT_Task.automationTaskAction
  },
  logicalExpressions: {
    ri!additionalLogicalExpression,
    rule!CMGT_Task_AutomationRuleGlobalLogicalExpression(
      taskIntentionCodes: ri!taskIntentionCodes
    )
  },
  pagingInfo: ri!pagingInfo,
  relatedRecordData: ri!relatedRecordData,
  sort: ri!sort,
  searchTerm: ri!searchTerm,
  searchFields: ri!searchFields
)

