    value: cons!CMGT_UUID_RECORD_TYPE_TASK
  ),
  local!originalData: ri!originalTask,
  local!newData: ri!task,
  rule!CMGT_UTIL_RejectBlank(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!filteredAvailableFields,
      expression: a!localVariables(
        local!originalRuntimeData: rule!CMGT_AUTO_EVAL_returnRuntimeValue(data: local!originalData, field: fv!item),
        local!newRuntimeData: rule!CMGT_AUTO_EVAL_returnRuntimeValue(data: local!newData, field: fv!item),
        local!fieldJson: #"SYSTEM_SYSRULES_toJson_v1"(
          rule!CMGT_Automation_constructSavedFieldMapping(selectedField: fv!item)
        ),
        a!match(
          value: fv!item,
          /*one to many fields on the case*/
          whenTrue: and(
            rule!CMGT_UTIL_IsListType(local!originalRuntimeData),
            rule!CMGT_UTIL_IsListType(local!newRuntimeData)
          ),
          then: if(
            rule!CMGT_UTIL_IsBlank(
              symmetricdifference(
                cast(
                  runtimetypeof(fv!item.fieldType),
                  local!originalRuntimeData
                ),
                cast(
                  runtimetypeof(fv!item.fieldType),
                  local!newRuntimeData
                )
              )
            ),
            null,
            local!fieldJson
          ),
          whenTrue: or(
            and(
              rule!CMGT_UTIL_IsBlank(local!originalRuntimeData),
              rule!CMGT_UTIL_IsNotBlank(local!newRuntimeData)
            ),
            and(
              rule!CMGT_UTIL_IsNotBlank(local!originalRuntimeData),
              rule!CMGT_UTIL_IsBlank(local!newRuntimeData)
            )
          ),
          then:local!fieldJson,
          /*one to one fields on the case*/
          default: if(
            local!newRuntimeData <> local!originalRuntimeData,
            local!fieldJson,
            null
          )
        )
      )
    )
  )
)

