_a-0000e99c-1217-8000-9ba9-011c48011c48_.sail_code:
if(
  a!isNullOrEmpty(ri!dateTime),
  "",
  a!localVariables(
    /*Converting dateTime to system time zone if  convertToSystemTimeZone is passed true */
    local!dateTimeToFormat: if(
      ri!convertToSystemTimeZone,
      rule!CMGT_UTIL_ConvertDateTimeToTimezone(
        dateTime: ri!dateTime,
        timezone: rule!CMGT_UTIL_SystemTimezone()
      ),
      ri!dateTime
    ),
    if(
      ri!returnTime,
      text(local!dateTimeToFormat, "h:mm a"),
      if(
        ri!includeTime,
        text(
          local!dateTimeToFormat,
          "mmm d, yyyy h:mm a"
        ),
        if(
          ri!returnMonthAndDay,
          text(local!dateTimeToFormat, "mmm.d"),
          text(local!dateTimeToFormat, "mmm d, yyyy")
        )
      )
    )
  )
)

