a!localVariables(
  local!activeProcesses: a!refreshVariable(
    value: rule!CMGT_CMT_AI_QPA_GetProcessesByCaseId(
      contextProcessModels: {
        cons!CMGT_CMT_AI_PM_CASE_COMMENT_SUMMARY
      },
      caseId: ri!caseId,
      executeWhen: rule!CMGT_UTIL_IsNotBlank(input: ri!caseId),
      status: "ACTIVE"
    ),
    refreshAfter: "RECORD_ACTION",
    refreshInterval: .5,
    refreshOnVarChange: { ri!commentRefresh, ri!triggerRefresh },
    refreshAlways: true
  ),
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshOnVarChange: local!activeProcesses
  ),
  local!commentCharCount: a!refreshVariable(
    value: rule!CMGT_CMT_AI_GetTotalCommentCharacterCount(
      caseId: ri!caseId,
      commentRefresh: ri!commentRefresh
    ),
    refreshOnVarChange: {
      ri!commentRefresh,
      ri!triggerRefresh,
      local!activeProcesses
    }
  ),
  local!caseCommentSummary: a!refreshVariable(
    value: rule!CMGT_CMT_AI_QR_getCaseCommentSummary(
      caseId: ri!caseId,
      executeWhen: a!isNotNullOrEmpty(ri!caseId),
      returnType: "SINGLE_OBJECT",
      triggerRefresh: local!triggerRefresh,
      additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_CMT_AI_CaseCommentSummary.isActive,
        operator: "=",
        value: true
      )
    ),
    refreshOnVarChange: {
      ri!commentRefresh,
      ri!triggerRefresh,
      local!activeProcesses,
      local!commentCharCount
    }
  ),
  local!displayDecision: rule!CMGT_CMT_AI_ShowDisplayLogic(
      commentCharCount: local!commentCharCount,
      commentSummary: local!caseCommentSummary,
      activeProcesses: local!activeProcesses
    ),

  #"SYSTEM_SYSRULES_cardLayout"(
    contents: {
      a!match(
        value: local!displayDecision,
        /*Displaying banner if the summary is being generated*/
        equals: "GENERATING_SUMMARY",
        then: #"SYSTEM_SYSRULES_richTextDisplayField"(
          labelPosition: "COLLAPSED",
          value: {
            #"SYSTEM_SYSRULES_richTextIcon"(icon: "spinner", color: "ACCENT"),
            " ",
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: #"urn:appian:translation-string:v1:d85e99f0-16e0-4826-98d8-892e3c3aa45d",
              style: "PLAIN"
            )
          }
        ),
        equals: "NOT_ENOUGH_INFO",
        then: /*Displaying banner if there not enough info for summarization*/
        #"SYSTEM_SYSRULES_richTextDisplayField"(
          labelPosition: "COLLAPSED",
          value: {
            #"SYSTEM_SYSRULES_richTextIcon"(icon: "magic", color: "ACCENT"),
            " ",
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: #"urn:appian:translation-string:v1:83ea2067-f458-4519-a2ff-6fc509a651ef",
              style: "PLAIN"
            )
          }
        ),
        equals: "INVALID",
        then: /*Displaying banner if the summary is invalid */
        #"SYSTEM_SYSRULES_richTextDisplayField"(
          labelPosition: "COLLAPSED",
          value: {
            #"SYSTEM_SYSRULES_richTextIcon"(icon: "magic", color: "ACCENT"),
            " ",
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: local!caseCommentSummary[recordType!CMGT_CMT_AI_CaseCommentSummary.invalidSummaryReasoning],
              style: "PLAIN"
            )
          }
        ),
        equals: "DISPLAY_SUMMARY",
        then: #"SYSTEM_SYSRULES_sectionLayout_v1"(
          label: #"urn:appian:translation-string:v1:9cb7dcac-6ab4-4976-bae9-315aa86d908b",
          labelIcon: "magic",
          labelSize: "EXTRA_SMALL",
          labelColor: ri!brandingMap.InfoTextColor,
          contents: {
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: #"urn:appian:translation-string:v1:d8b19f23-2238-4227-85fa-dfd5d2f8ca56",
                color: ri!brandingMap.SecondaryTextColor
              ),
              marginBelow: "EVEN_LESS"
            ),
            #"SYSTEM_SYSRULES_cardLayout"(
              contents: {
                rule!CMGT_UTIL_RichTextField(
                  value: if(
                    a!isNullOrEmpty(
                      local!caseCommentSummary[recordType!CMGT_CMT_AI_CaseCommentSummary.summary]
                    ),
                    null,
                    stripwith(
                      local!caseCommentSummary[recordType!CMGT_CMT_AI_CaseCommentSummary.summary],
                      char(10)
                    )
                  ),
                  readOnly: true()
                )
              },
              padding: "LESS",
              showBorder: false
            )
          },
          isCollapsible: true,
          marginAbove: "STANDARD",
          marginBelow: "EVEN_LESS"
        ),
        default: {}
      )
    },
    showWhen: and(
      a!defaultValue(ri!showWhen, true),
      local!displayDecision <> "HIDE"
    ),
    style: ri!brandingMap.ConfigBackgroundColor,
    padding: "LESS",
    marginBelow: "STANDARD",
    showBorder: false
  )
)

