a!localVariables(
  /*
  Since the chart will graph dependent on the actual order of nodes
  this logic will just reorder the nodes based on what don't have edges, what those point to, etc.
  */
  /*since nodes are passed in in a parent-child pattern, this pulls all of the nodes out and flattens*/
  local!allNodes: rule!CMGT_UTIL_cast(
    typeOf: { a!map() },
    value: rule!CMGT_UTIL_RejectBlank(
      a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!nodes,
          expression: {
            remove(fv!item, "children"),
            fv!item.children
          }
        )
      )
    )
  ),
  /*sorting nodes vertically so nodes which are closest to the start node are first in line*/
  local!nodesOrderedVertically: reduce(
    rule!CMGT_NCF_orderNodesBasedOnDistanceFromStart(
      orderedNodes: _,
      index: _,
      allNodes: local!allNodes,
      allEdges: ri!edges
    ),
    /*start with "start" node*/
    #"urn:appian:function:v1:a:update"(
      data: rule!CMGT_UTIL_Filter(
        records: local!allNodes,
        field: "id",
        value: ri!nodes[1].id
      ),
      index: "verticalOrder",
      value: 1
    ),
    enumerate(length(local!allNodes))
  ),
  /*stray nodes are nodes with no precedents or nodes that are unreachable*/
  local!strayNodes: rule!CMGT_UTIL_Filter(
    records: local!allNodes,
    field: "id",
    value: local!nodesOrderedVertically.id,
    filterType: "REJECT"
  ),
  local!nodesSortedVertically: rule!CMGT_UTIL_sortList(
    records: {
      local!nodesOrderedVertically,
      #"urn:appian:function:v1:a:update"(
        local!strayNodes,
        "verticalOrder",
        repeat(length(local!strayNodes), 2)
      )
    },
    field: "verticalOrder",
    ascending: true
  ),
  /*using the "verticalOrder" property given to each node, goes through each "level" and sorts nodes*/
  /*horizontally based on the horizontalOrder of their dependents to help prevent edge intersections*/
  /*sorting from bottom first helps alleviate crossing lines that form from arbitrary sort from top*/
  local!nodesSortedHorizontallyFromBottom: rule!CMGT_UTIL_sortList(
    records: rule!CMGT_UTIL_sortList(
      records: reduce(
        rule!CMGT_NCF_orderNodesHorizontallyBottomUp(
          orderedNodes: _,
          level: _,
          allNodes: local!nodesSortedVertically,
          allEdges: ri!edges
        ),
        {},
        rule!CMGT_UTIL_Distinct(
          local!nodesSortedVertically.verticalOrder
        )
      ),
      field: "horizontalOrder",
      ascending: true
    ),
    field: "verticalOrder",
    ascending: true
  ),
  /*using the "verticalOrder" property given to each node, goes through each "level" and sorts nodes*/
  /*horizontally based on the horizontalOrder of their precedents to help prevent edge intersections*/
  /*doing this sort last provides the best visual results*/
  local!nodesSortedHorizontallyFromTop: rule!CMGT_UTIL_sortList(
    records: rule!CMGT_UTIL_sortList(
      records: reduce(
        rule!CMGT_NCF_orderNodesHorizontallyTopDown(
          orderedNodes: _,
          level: _,
          allNodes: local!nodesSortedHorizontallyFromBottom,
          allEdges: ri!edges
        ),
        {},
        rule!CMGT_UTIL_Distinct(
          local!nodesSortedHorizontallyFromBottom.verticalOrder
        )
      ),
      field: "horizontalOrder",
      ascending: true
    ),
    field: "verticalOrder",
    ascending: true
  ),
  local!fixNodesOrder: local!nodesSortedHorizontallyFromTop,
  local!maxVerticalSort: todecimal(
    rule!CMGT_UTIL_returnLastIndexOfArray(array: local!fixNodesOrder).verticalOrder
  ),
  /*
  Since the chart will inaccurately graph if edges point back to previous nodes,
  this logic will update the ordering of edges so that they always point to "next" nodes

  If the edge "from" points to a "to" that is before it, it just swaps the "from" and "to" of both the object and the arrow
  */
  local!fixEdgesOrder: #"SYSTEM_SYSRULES_forEach"(
    items: ri!edges,
    expression: a!localVariables(
      /*to help "fix" direction of loopback edges*/
      local!fromNodeIndex: cast(
        1,
        wherecontains(
          tostring(fv!item.from),
          touniformstring(local!fixNodesOrder.id)
        )
      ),
      local!toNodeIndex: cast(
        1,
        wherecontains(
          tostring(fv!item.to),
          touniformstring(local!fixNodesOrder.id)
        )
      ),
      local!switchToAndFromIds: and(
        a!isNotNullOrEmpty(local!fromNodeIndex),
        a!isNotNullOrEmpty(local!toNodeIndex),
        local!toNodeIndex < local!fromNodeIndex
      ),
      /*to allow for curved edges between distant nodes*/
      local!fromNode: local!fixNodesOrder[local!fromNodeIndex],
      local!toNode: local!fixNodesOrder[local!toNodeIndex],
      local!horizontalOrderSign: sign(
        local!fromNode.horizontalOrder - local!toNode.horizontalOrder
      ),
      /*to allow for multiple curved edges between two nodes when there are multiple branches*/
      local!matchingEdgeIndices: rule!CMGT_UTIL_Filter(
        records: ri!edges,
        fieldValuePairs: {
          a!map(field: "from", value: fv!item.from),
          a!map(field: "to", value: fv!item.to)
        },
        filterType: "MATCH_INDICES"
      ),
      #"urn:appian:function:v1:a:update"(
        fv!item,
        { "from", "to", "arrows", "smooth", "font" },
        {
          /*from*/
          if(
            local!switchToAndFromIds,
            fv!item.to,
            fv!item.from
          ),
          /*to*/
          if(
            local!switchToAndFromIds,
            fv!item.from,
            fv!item.to
          ),
          /*arrows*/
          cast(
            252,
            if(
              and(
                local!switchToAndFromIds,
                a!isNotNullOrEmpty(fv!item.arrows)
              ),
              #"urn:appian:function:v1:a:update"(
                fv!item.arrows,
                { "from", "to" },
                { fv!item.arrows.to, fv!item.arrows.from }
              ),
              fv!item.arrows
            )
          ),
          /*smooth*/
          a!match(
            value: true,
            /*allow for multiple curved edges between two nodes when there are multiple branches*/
            whenTrue: length(local!matchingEdgeIndices) > 1,
            then: a!map(
              enabled: true,
              type: "curvedCW",
              roundness: a!localVariables(
                local!normalizedMatchingEdgeIndices: enumerate(count(local!matchingEdgeIndices)),
                local!normalizedCurrentIndex: cast(
                  1,
                  wherecontains(fv!index, local!matchingEdgeIndices)
                ) - 1,
                local!unadjustedCurve: (
                  local!normalizedCurrentIndex - median(local!normalizedMatchingEdgeIndices)
                ) / count(local!matchingEdgeIndices),
                /*for some reason, negative curve values need to start at -2*/
                local!adjustedCurve: if(
                  local!unadjustedCurve < 0,
                  local!unadjustedCurve - 2,
                  local!unadjustedCurve
                ),
                local!adjustedCurve
              )
            ),
            /*allow for curved edges between distant nodes*/
            whenTrue: (
              local!toNode.verticalOrder - local!fromNode.verticalOrder
            ) <> 1,
            then: a!map(
              enabled: true,
              type: "curvedCW",
              roundness: (
                local!toNode.verticalOrder - local!fromNode.verticalOrder
              ) / (local!maxVerticalSort - 1) * 0.5 * a!match(
                value: 0,
                whenTrue: local!horizontalOrderSign <> 0,
                then: local!horizontalOrderSign,
                whenTrue: sign(local!fromNode.horizontalOrder - 50.0) <> 0,
                then: sign(local!fromNode.horizontalOrder - 50.0),
                default: 1
              )
            ),
            default: a!map(enabled: false)
          ),
          /*font*/
          cast(
            252,
            /*if there are multiple branches between two nodes update label alignment to handle crowdedness*/
            if(
              length(local!matchingEdgeIndices) > 1,
              #"urn:appian:function:v1:a:update"(fv!item.font, "align", "middle"),
              fv!item.font
            )
          )
        }
      )
    )
  ),
  a!map(
    nodes: rule!CMGT_UTIL_DistinctRecords(
      records: rule!CMGT_UTIL_RejectBlank(local!fixNodesOrder),
      fields: "id"
    ),
    edges: rule!CMGT_UTIL_DistinctRecords(
      records: rule!CMGT_UTIL_RejectBlank(local!fixEdgesOrder),
      fields: { "from", "to", "arrows", "title" }
    )
  )
)

