_a-0000eca3-381d-8000-9cf0-011c48011c48_.sail_code:
/*This rule groups the task branches as how they should be displayed in the dependents dropdown 
based on response option, requiredness, requiredness valid, conditionset and returns the result in a map format*/
a!localVariables(
  local!activeBranches: rule!CMGT_UTIL_Filter(
    records: ri!caseTypeWorkflowTaskBranch,
    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.isActive,
    value: false,
    filterType: "REJECT"
  ),
  local!automationUUIDs: rule!CMGT_UTIL_Distinct(
    rule!CMGT_UTIL_Filter(
      records: ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
      field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.taskBehaviorTypeId,
      value: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
    )[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
  ),
  local!automationBranches: rule!CMGT_UTIL_Filter(
    records: local!activeBranches,
    fieldValuePairs: {
      a!map(
        field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
        value: local!automationUUIDs
      )
    }
  ),
  local!automationGroupMaps: rule!CMGT_WFL_GroupCaseTypeWorkflowTaskBranches_Automations(
    caseTypeWorkflowTaskBranch: local!automationBranches,
    caseType: ri!caseType
  ),
  local!activeTaskBranches: rule!CMGT_UTIL_Filter(
    records: local!activeBranches,
    fieldValuePairs: {
      a!map(
        field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
        value: local!automationUUIDs
      )
    },
    filterType: "REJECT",

  ),
  local!distinctBeforeTaskUUID: rule!CMGT_UTIL_Distinct(
    local!activeTaskBranches[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid]
  ),
  /*Grouping task based on response option  */
  local!distinctResponseOptions: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!distinctBeforeTaskUUID,
      expression: a!localVariables(
        local!thisUUID: fv!item,
        #"SYSTEM_SYSRULES_forEach"(
          items: rule!CMGT_UTIL_Distinct(
            rule!CMGT_UTIL_Filter(
              records: local!activeBranches,
              field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
              value: local!thisUUID
            )[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.caseTypeTaskResponseOptionUuid]
          ),
          expression: rule!CMGT_WFL_ConstructCaseTypeWorkflowTaskBranchMap(
            taskUUID: local!thisUUID,
            taskResponseOptionUUID: fv!item
          )
        )
      )
    )
  ),
  /* Grouping local!distinctResponseOptions based on requiredness */
  local!distinctionRequiredness: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!distinctResponseOptions,
      expression: a!localVariables(
        local!thisItem: fv!item,
        local!thisTaskBranches: rule!CMGT_UTIL_Filter(
          records: local!activeBranches,
          fieldValuePairs: {
            a!map(
              field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
              value: local!thisItem.taskUUID
            ),
            a!map(
              field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.caseTypeTaskResponseOptionUuid,
              value: local!thisItem.taskResponseOptionUUID
            ),
            a!map(
              field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.conditionSetUuid,
              value: local!thisItem.conditionSetUUID
            )
          }
        ),
        #"SYSTEM_SYSRULES_forEach"(
          items: rule!CMGT_UTIL_Distinct(
            local!thisTaskBranches[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.isRequired]
          ),
          expression: rule!CMGT_WFL_ConstructCaseTypeWorkflowTaskBranchMap(
            taskUUID: local!thisItem.taskUUID,
            taskResponseOptionUUID: local!thisItem.taskResponseOptionUUID,
            isRequired: fv!item,
            conditionSetUUID: local!thisItem.conditionSetUUID,
            afterCaseTypeWorkflowTaskBranches: rule!CMGT_UTIL_Filter(
              records: local!thisTaskBranches,
              field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.isRequired,
              value: fv!item
            ),
            isActive: true
          )
        )
      )
    )
  ),
  /* Grouping local!distinctionRequiredness based on requiredness valid */
  local!distinctRequirednessValid: #"SYSTEM_SYSRULES_forEach"(
    items: local!distinctionRequiredness,
    expression: a!localVariables(
      local!thisMap: fv!item,
      local!startsByDefault_afterTaskBranches: cast(
        #"urn:appian:function:v1:a:listtype?okey=a!listType"(
          recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch
        ),
        rule!CMGT_UTIL_Filter(
          records: local!thisMap.afterCaseTypeWorkflowTaskBranches,
          field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.afterCaseTypeWorkflowTaskUuid,
          value: index(
            rule!CMGT_UTIL_Filter(
              records: ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
              field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.startsByDefault,
              value: true
            ),
            recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid,
            {}
          )
        )
      ),
      local!loopBackTasks_afterTaskBranches: cast(
        #"urn:appian:function:v1:a:listtype?okey=a!listType"(
          recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch
        ),
        rule!CMGT_UTIL_Filter(
          records: local!thisMap.afterCaseTypeWorkflowTaskBranches,
          field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.afterCaseTypeWorkflowTaskUuid,
          value: rule!CMGT_WFL_CaseTypeWorkflowTasks_FindPreviousTasksInTree(
            caseType: ri!caseType,
            taskUuId: local!thisMap.taskUUID,
            caseTypeWorkflowTaskBranches: local!activeBranches
          )[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
        )
      ),
      {
        /*isRequirednessValid = true         */
        if(
          rule!CMGT_UTIL_IsNotBlank(
            difference(
              cast(
                #"urn:appian:function:v1:a:listtype?okey=a!listType"(
                  recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch
                ),
                fv!item.afterCaseTypeWorkflowTaskBranches
              ),
              a!flatten(
                {
                  local!startsByDefault_afterTaskBranches,
                  local!loopBackTasks_afterTaskBranches
                }
              )
            )
          ),
          rule!CMGT_WFL_ConstructCaseTypeWorkflowTaskBranchMap(
            taskUUID: local!thisMap.taskUUID,
            taskResponseOptionUUID: local!thisMap.taskResponseOptionUUID,
            isRequired: local!thisMap.isRequired,
            isRequirednessValid: true,
            conditionSetUUID: fv!item.conditionSetUUID,
            afterCaseTypeWorkflowTaskBranches: difference(
              cast(
                #"urn:appian:function:v1:a:listtype?okey=a!listType"(
                  recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch
                ),
                fv!item.afterCaseTypeWorkflowTaskBranches
              ),
              a!flatten(
                {
                  local!startsByDefault_afterTaskBranches,
                  local!loopBackTasks_afterTaskBranches
                }
              )
            ),
            isActive: fv!item.isActive
          ),
          null
        ),
        /*isRequirednessValid = false         */
        if(
          rule!CMGT_UTIL_IsNotBlank(
            union(
              local!startsByDefault_afterTaskBranches,
              local!loopBackTasks_afterTaskBranches
            )
          ),
          rule!CMGT_WFL_ConstructCaseTypeWorkflowTaskBranchMap(
            taskUUID: local!thisMap.taskUUID,
            taskResponseOptionUUID: local!thisMap.taskResponseOptionUUID,
            isRequired: local!thisMap.isRequired,
            isRequirednessValid: false,
            conditionSetUUID: fv!item.conditionSetUUID,
            afterCaseTypeWorkflowTaskBranches: union(
              local!startsByDefault_afterTaskBranches,
              local!loopBackTasks_afterTaskBranches
            ),
            isActive: fv!item.isActive
          ),
          null
        )
      }
    )
  ),
  cast(
    #"urn:appian:function:v1:a:listtype?okey=a!listType"(
      'type!{http://www.appian.com/ae/types/2009}Map'
    ),
    rule!CMGT_UTIL_RejectBlank(
      a!flatten(
        merge(
          local!distinctRequirednessValid,
          local!automationGroupMaps
        )
      )
    )
  )
)

