a!localVariables(
  local!originalTasks: if(
    rule!CMGT_UTIL_IsBlank(ri!originalTasks),
    rule!CMGT_QR_GetTask(
      taskId: ri!tasks[recordType!CMGT_Task.taskId],
      includeInactive: true,
      executeWhen: rule!CMGT_UTIL_IsNotBlank(
        a!flatten(
          ri!tasks[recordType!CMGT_Task.taskId]
        )
      ),
      includeMilestone: true(),
      fields: {
        recordType!CMGT_Task.conditionSet,
        recordType!CMGT_Task.taskResponseOptions,
        recordType!CMGT_Task.conditionSet,
        recordType!CMGT_Task.createCaseTask,
        recordType!CMGT_Task.sendEmailTask,
        recordType!CMGT_Task.automationTaskAction
      }
    ),
    ri!originalTasks
  ),
  local!originalAutomationWorkflowTasks: if(
    rule!CMGT_UTIL_IsBlank(ri!originalAutomationWorkflowTasks),
    rule!CMGT_QR_GetTask(
      additionalFilters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_Task.automationParentWorkflowTaskInstanceUuid,
          operator: "in",
          value: ri!tasks[recordType!CMGT_Task.taskInstanceUuid]
        )
      },
      fields: {
        recordType!CMGT_Task.conditionSet,
        recordType!CMGT_Task.conditionSet,
        recordType!CMGT_Task.conditionSet,
        #"urn:appian:record-relationship:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/5189c3ac-98e2-4a82-8598-2e9561baec9d/64b891e4-359b-48b4-9f47-2a3af4695c34/52813580-ecf3-49c7-be71-568ab452d54a",
        recordType!CMGT_Task.automationTaskAction,
        recordType!CMGT_Task.automationTaskAction,
        #"urn:appian:record-relationship:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/45ed19eb-2545-4c96-9e19-72127f9d27a3/dc83b26a-0034-46f4-8085-5887ad59bc08/86c11a87-1ab5-4e62-93f2-b7d24e412b3a"
      },
      taskIntentionCodes: cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_RUNTIME_CONFIG_TRIGGER
    ),
    ri!originalAutomationWorkflowTasks
  ),
  local!filteredActiveTasks: rule!CMGT_UTIL_Filter(
    records: ri!tasks,
    field: recordType!CMGT_Task.isActive,
    value: true()
  ),
  local!activeTasks: #"SYSTEM_SYSRULES_forEach"(
    local!filteredActiveTasks,
    a!localVariables(
      local!orgItem: rule!CMGT_UTIL_Filter(
        records: local!originalTasks,
        field: recordType!CMGT_Task.taskId,
        value: fv!item[recordType!CMGT_Task.taskId]
      ),
      local!orgAutomationTasks: rule!CMGT_UTIL_Filter(
        records: local!originalAutomationWorkflowTasks,
        field: recordType!CMGT_Task.automationParentWorkflowTaskInstanceUuid,
        value: fv!item[recordType!CMGT_Task.taskInstanceUuid]
      ),
      /*Constructing Task Response Options, Condition Sets*/
      /*Note: Branches will be handled separately*/
      rule!CMGT_UTIL_SetRecordFields(
        record: fv!item,
        /*Always set these fields with each update*/
        alwaysOverwrite: {},
        /*Only modify these fields if they are currently null*/
        neverOverwrite: {},
        /*Update Child Relationships*/
        childRelationships: {
          if(
            ri!context = "MODIFY_WORKFLOW",
            {},
            {
              a!map(
                field: recordType!CMGT_Task.taskResponseOptions,
                value: local!orgItem[recordType!CMGT_Task.taskResponseOptions]
              ),
              a!map(
                field: recordType!CMGT_Task.conditionSet,
                value: local!orgItem[recordType!CMGT_Task.conditionSet]
              ),
              a!map(
                field: recordType!CMGT_Task.milestone,
                value: local!orgItem[recordType!CMGT_Task.milestone]
              ),
              a!map(
                field: recordType!CMGT_Task.createCaseTask,
                value: local!orgItem[recordType!CMGT_Task.createCaseTask]
              ),
              a!map(
                field: recordType!CMGT_Task.sendEmailTask,
                value: local!orgItem[recordType!CMGT_Task.sendEmailTask]
              ),
              /*Branches will be constructed in a separate rule*/
              a!map(
                field: recordType!CMGT_Task.beforeTaskInstanceBranches,
                value: {}
              )
            }
          ),
          a!map(
            field: recordType!CMGT_Task.automationWorkflowTasks,
            value: local!orgAutomationTasks
          )
        }
      )
    )
  ),
  local!activeTasks
)

