rule!CMGT_UTIL_SetRecordFields(
  record: ri!message,
  childRelationships: a!map(
    field: recordType!CMGT_EML_TRI_Message.messageRecipients,
    value: a!flatten(
      {
        /*message recipient entry for sender */
        recordType!CMGT_EML_TRI_MessageRecipient(
          recordType!CMGT_EML_TRI_MessageRecipient.recipient: concat(
            user(ri!loggedInUser, "firstName"),
            char(32),
            user(ri!loggedInUser, "lastName")
          ),
          recordType!CMGT_EML_TRI_MessageRecipient.recipientEmail: cons!CMGT_EML_TRI_MAILBOX_EMAIL_ADDRESS,
          recordType!CMGT_EML_TRI_MessageRecipient.isSender: true
        ),
        /*message recipient entry for receiver*/
        a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: rule!CMGT_UTIL_RejectBlank(
              array: union(
                ri!emailAddresses,
                if(
                  a!isNotNullOrEmpty(ri!caseAssociatedEmails),
                  #"SYSTEM_SYSRULES_forEach"(
                    ri!caseAssociatedEmails,
                    index(split(fv!item, "#"), 2, null)
                  ),
                  null
                )
              )
            ),
            expression: recordType!CMGT_EML_TRI_MessageRecipient(
              recordType!CMGT_EML_TRI_MessageRecipient.recipient: null(),
              recordType!CMGT_EML_TRI_MessageRecipient.recipientEmail: fv!item,
              recordType!CMGT_EML_TRI_MessageRecipient.isSender: false
            )
          )
        )
      }
    )
  )
)

