a!localVariables(
  local!distinctBeforeTaskUUID: rule!CMGT_UTIL_Distinct(
    ri!taskBranch[recordType!CMGT_TaskBranch.beforeTaskInstanceUuid]
  ),
  cast(
    #"urn:appian:function:v1:a:listtype?okey=a!listType"(
      'type!{http://www.appian.com/ae/types/2009}Map'
    ),
    rule!CMGT_UTIL_RejectBlank(
      a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: local!distinctBeforeTaskUUID,
          expression: a!localVariables(
            local!thisUUID: fv!item,
            local!thisTaskBranches: rule!CMGT_UTIL_Filter(
              records: ri!taskBranch,
              fieldValuePairs: {
                a!map(
                  field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
                  value: local!thisUUID
                ),

              }
            ),
            {
              a!match(
                value: tostring(
                  rule!CMGT_UTIL_Filter(
                    records: ri!case[recordType!CMGT_Case.tasks],
                    field: recordType!CMGT_Task.taskInstanceUuid,
                    value: local!thisUUID
                  )[recordType!CMGT_Task.automationTaskAction.actionCode]
                ),
                equals: cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING,
                /* Grouping the branches with same Condition Set Uuids */
                then: #"SYSTEM_SYSRULES_forEach"(
                  items: rule!CMGT_UTIL_Distinct(
                    local!thisTaskBranches[recordType!CMGT_TaskBranch.conditionSetUuid]
                  ),
                  expression: rule!CMGT_ConstructTaskBranchMap(
                    taskInstanceUUID: local!thisUUID,
                    conditionSetUUID: fv!item,
                    afterTaskBranches: rule!CMGT_UTIL_Filter(
                      records: local!thisTaskBranches,
                      field: recordType!CMGT_TaskBranch.conditionSetUuid,
                      value: fv!item,

                    ),
                    isRequired: true,
                    isActive: true,
                    isRequirednessValid: true
                  )
                ),
                equals: cons!CMGT_CFG_TXT_ACT_SEND_EMAIL,
                /*   Grouping all the branches as the send email dependents are always required and valid      */
                then: rule!CMGT_ConstructTaskBranchMap(
                  taskInstanceUUID: local!thisUUID,
                  afterTaskBranches: local!thisTaskBranches,
                  isRequired: true,
                  isActive: true,
                  isRequirednessValid: true
                ),
                default: {}
              )
            }
          )
        )
      )
    )
  )
)

