_a-0001ed77-88a5-8000-9d49-011c48011c48_.sail_code:
/*This rule will return all the tasks in the traversed path from the task that has been looped back*/
/*Logic : 1. Get the previously traversed afterTaskUuids of the passed initialTaskInstanceUuid 
2. For the next iteraion pass the afterTaskUuids that have been returned above
3. Continue this logic until there or no afterTaskUuids or initialTaskInstanceUuid is returned in the final result(Loopback)

Note : If initialTaskInstanceUuid is present in reopenedTasks then the task has been reopened already.So to get the traversed path,
1. Take the previous instance of the task 2. Query the afterTaskUuids of the task 3. Get the latest taskInstanceUuids of the returned 
afterTaskUuids.
*/
a!localVariables(
  /*Filter the current task*/
  local!filteredTask: rule!CMGT_UTIL_Filter(
    records: ri!tasks,
    field: recordType!CMGT_Task.taskInstanceUuid,
    value: ri!initialTaskInstanceUuid
  ),
  /*If the initialTaskInstanceUuid is present in reopenedTasks to get the afterTaskUuids we need to process with
  previous instance of the task*/
  local!currentTask: if(
    contains(
      ri!reopenedTasks[recordType!CMGT_Task.taskUuid],
      local!filteredTask[recordType!CMGT_Task.taskUuid]
    ),
    rule!CMGT_UTIL_Filter(
      records: ri!reopenedTasks,
      field: recordType!CMGT_Task.taskUuid,
      value: local!filteredTask[recordType!CMGT_Task.taskUuid]
    ),
    local!filteredTask
  ),
  /*Construct the response map*/
  local!responseMap: a!flatten(
    if(
      local!currentTask[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_COMPLETED,
      rule!CMGT_Task_ConstructResponseMapForCompletedTasks(completedTasks: local!currentTask),
      null
    )
  ),
  /*Filter after task instance uuid of the current task*/
  local!afterTaskInstanceUuids: if(
    contains(
      ri!reopenedTasks[recordType!CMGT_Task.taskUuid],
      local!filteredTask[recordType!CMGT_Task.taskUuid]
    ),
    rule!CMGT_UTIL_Distinct(
      rule!CMGT_UTIL_Filter(
        records: ri!tasks,
        field: recordType!CMGT_Task.taskUuid,
        value: rule!CMGT_QR_GetTaskBranch(
          isCurrent: false,
          fields: recordType!CMGT_TaskBranch.afterTaskInstance,
          additionalLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
                operator: "in",
                value: local!currentTask[recordType!CMGT_Task.taskInstanceUuid]
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!CMGT_TaskBranch.isActive,
                operator: "=",
                value: true
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!CMGT_TaskBranch.taskResponseOptionUuid,
                operator: "in",
                value: index(
                  local!responseMap,
                  "selectedResponseOptionUuid",
                  null
                ),
                applyWhen: rule!CMGT_UTIL_IsNotBlank(
                  index(
                    local!responseMap,
                    "selectedResponseOptionUuid",
                    null
                  )
                )
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!CMGT_TaskBranch.conditionSetUuid,
                operator: "in",
                value: a!flatten(
                  index(
                    local!responseMap,
                    "satisfiedConditionSetUuid",
                    null
                  )
                ),
                applyWhen: and(
                  contains(
                    local!currentTask[recordType!CMGT_Task.taskBehaviorTypeId],
                    cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
                  ),
                  rule!CMGT_UTIL_IsNotBlank(
                    input: index(
                      local!responseMap,
                      "satisfiedConditionSetUuid",
                      null
                    )
                  )
                )
              )
            }
          )
        )[recordType!CMGT_TaskBranch.afterTaskInstance.taskUuid],

      )[recordType!CMGT_Task.taskInstanceUuid]
    ),
    rule!CMGT_UTIL_Distinct(
      rule!CMGT_UTIL_Filter(
        records: ri!taskBranches,
        fieldValuePairs: {
          a!map(
            field: recordType!CMGT_TaskBranch.beforeTaskInstanceUuid,
            value: ri!initialTaskInstanceUuid
          ),
          a!map(
            field: recordType!CMGT_TaskBranch.isActive,
            value: true
          ),
          a!map(
            field: recordType!CMGT_TaskBranch.isCurrent,
            value: true
          ),
          if(
            rule!CMGT_UTIL_IsNotBlank(
              index(
                local!responseMap,
                "selectedResponseOptionUuid",
                null
              )
            ),
            a!map(
              field: recordType!CMGT_TaskBranch.taskResponseOptionUuid,
              value: index(
                local!responseMap,
                "selectedResponseOptionUuid",
                null
              )
            ),
            {}
          ),
          if(
            contains(
              local!currentTask[recordType!CMGT_Task.taskBehaviorTypeId],
              cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
            ),
            a!map(
              field: recordType!CMGT_TaskBranch.conditionSetUuid,
              value: a!flatten(
                index(
                  local!responseMap,
                  "satisfiedConditionSetUuid",
                  null
                )
              ),

            ),
            {}
          )
        }
      )[recordType!CMGT_TaskBranch.afterTaskInstanceUuid]
    )
  ),
  /*Append the current task instance UUID with the final path mapping tasks*/
  local!finalPathMappingTasks: rule!CMGT_UTIL_Distinct(
    append(
      ri!finalWorkflowPathMappingTasks,
      ri!initialTaskInstanceUuid
    )
  ),
  if(
    /*Recursion END Condition*/
    /*Check if there are any after task for current task or the current task*/
    /*leads to loopback (Task present in final output of workflow path mapping)*/
    or(
      rule!CMGT_UTIL_IsBlank(local!afterTaskInstanceUuids),
      contains(
        ri!finalWorkflowPathMappingTasks,
        ri!initialTaskInstanceUuid
      )
    ),
    /*Return the result if recursion end condition is met*/
    local!finalPathMappingTasks,
    /*Call the recursion logic again with the filtered afterTaskUUIDs*/
    reduce(
      rule!CMGT_Tasks_GetTraversedPathOfTask_Reducer(
        finalWorkflowPathMappingTasks: _,
        initialTaskInstanceUuid: _,
        taskBranches: ri!taskBranches,
        tasks: ri!tasks,
        reopenedTasks: ri!reopenedTasks
      ),
      local!finalPathMappingTasks,
      local!afterTaskInstanceUuids
    )
  )
)

_a-0000ecb5-acf8-8000-9cf6-011c48011c48_.sail_code:
/*By default all automation type tasks should use the OOB images. 
Any additional or task behavior type specific images should be added here*/
a!match(
  value: ri!task[recordType!CMGT_Task.taskBehaviorTypeId],
  equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
  then: a!match(
    value: tostring(
      ri!task[recordType!CMGT_Task.automationTaskAction.actionCode]
    ),
    equals: cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING,
    then: document(
      rule!CMGT_Automation_WorkflowRouting_ChooseVisualizationImage(task: ri!task, isSelected: ri!isSelected),
      "url"
    ),
    equals: cons!CMGT_CFG_TXT_ACT_SEND_EMAIL,
    then: document(
      rule!CMGT_Automation_SendEmail_ChooseVisualizationImage(task: ri!task, isSelected: ri!isSelected),
      "url"
    ),
    /*Add additional automation type specific images here*/
    default: document(
      rule!CMGT_Automation_WorkflowRouting_ChooseVisualizationImage(task: ri!task, isSelected: ri!isSelected),
      "url"
    )
  ),
  /*Add new task behaviour type specific images here*/
  default: null
)

