a!localVariables(
  local!loggedInUser: loggedInUser(),
  local!messageDataSubset: rule!CMGT_EML_TRI_QR_getMessages(
    threadIds: ri!thread[recordType!CMGT_EML_TRI_Thread.threadId],
    returnType: "DATA_SUBSET",
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: 1000,
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!CMGT_EML_TRI_Message.modifiedOn
      )
    ),
    triggerRefresh: ri!triggerMessageRefresh
  ),
  local!totalCount: index(
    local!messageDataSubset,
    "totalCount",
    null()
  ),
  local!messages: a!flatten(
    index(local!messageDataSubset, "data", null())
  ),
  /*we want to check if the thread has been action before updating the record on clicking edit anyways*/
  local!unlockThread: if(
    rule!CMGT_EML_TRI_QR_GetThreads(
      threadIds: ri!thread[recordType!CMGT_EML_TRI_Thread.threadId],
      fields: recordType!CMGT_EML_TRI_Thread.threadStatus
    )[recordType!CMGT_EML_TRI_Thread.threadStatus] <> cons!CMGT_EML_TRI_TRIAGE_STATUS_IN_PROGRESS,
    {},
    recordType!CMGT_EML_TRI_Thread(
      recordType!CMGT_EML_TRI_Thread.threadId: ri!thread[recordType!CMGT_EML_TRI_Thread.threadId],
      recordType!CMGT_EML_TRI_Thread.owner: local!loggedInUser,
      recordType!CMGT_EML_TRI_Thread.modifiedBy: local!loggedInUser,
      recordType!CMGT_EML_TRI_Thread.modifiedOn: now()
    )
  ),
  /*local to control the text shown in the banner*/
  local!showTriagedTextInBanner: false,
  local!isViewAllAttachments: #"SYSTEM_SYSRULES_forEach"(
    items: local!messages,
    expression: a!map(
      messageId: fv!item[recordType!CMGT_EML_TRI_Message.messageId],
      viewAll: false()
    )
  ),
  local!replyToMessage: recordType!CMGT_EML_TRI_Message(
    recordType!CMGT_EML_TRI_Message.threadId: ri!thread[recordType!CMGT_EML_TRI_Thread.threadId],
    recordType!CMGT_EML_TRI_Message.messageText: {}
  ),
  local!allMessageRecipients: rule!CMGT_EML_TRI_QR_GetMessageRecipients(
    returnType: "OBJECT_ARRAY",
    messageIds: local!messages[recordType!CMGT_EML_TRI_Message.messageId],
    sort: {
      #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!CMGT_EML_TRI_MessageRecipient.messageId,
        ascending: true()
      )
    }
  ),
  local!action,
  local!messageRecipientsEmail: a!refreshVariable(
    value: if(
      rule!CMGT_UTIL_IsNotBlank(local!allMessageRecipients),
      rule!CMGT_UTIL_Distinct(
        rule!CMGT_UTIL_Filter(
          records: local!allMessageRecipients,
          field: recordType!CMGT_EML_TRI_MessageRecipient.recipientEmail,
          value: cons!CMGT_EML_TRI_MAILBOX_EMAIL_ADDRESS,
          filterType: "REJECT"
        )[recordType!CMGT_EML_TRI_MessageRecipient.recipientEmail]
      ),
      null
    ),
    refreshOnVarChange: local!action
  ),
  local!allSenders: rule!CMGT_UTIL_Distinct(
    touniformstring(
      rule!CMGT_UTIL_Filter(
        records: local!allMessageRecipients,
        field: recordType!CMGT_EML_TRI_MessageRecipient.isSender,
        value: true
      )[recordType!CMGT_EML_TRI_MessageRecipient.recipient]
    )
  ),
  local!sendersUserRecords: rule!CMGT_QR_GetUser(username: local!allSenders),
  local!allMessageAttachments: rule!CMGT_EML_TRI_QR_GetMessageAttachments(
    returnType: "OBJECT_ARRAY",
    messageIds: tointeger(
      local!messages[recordType!CMGT_EML_TRI_Message.messageId]
    )
  ),
  local!firstMessage: ldrop(local!messages, local!totalCount - 1),
  local!latestMessageRecipients: rule!CMGT_UTIL_Filter(
    records: local!allMessageRecipients,
    field: recordType!CMGT_EML_TRI_MessageRecipient.messageId,
