_a-0000ea1d-36f5-8000-9bef-011c48011c48_.sail_code:
/*Using the user tags for a comment, 
this is the save logic used to identify tags that have been kept, added, and deleted*/
a!localVariables(
  local!temp,
  {
    /*Decipher user tags to keep active and which to delete*/
    a!save(
      local!temp,
      a!localVariables(
        /*Get the unique selected values from the picker fields*/
        local!uniqueSelectedValues: rule!CMGT_UTIL_Distinct(save!value),
        local!allExistingCommentTagsForThisComment: cast(
          typeof(ri!commentUserTags),
          a!flatten(
            {
              ri!commentUserTags
            }
          )
        ),
        local!newCommentUserTagRecords: #"SYSTEM_SYSRULES_forEach"(
          items: local!uniqueSelectedValues,
          expression: displayvalue(
            tostring(fv!item),
            touniformstring(
              local!allExistingCommentTagsForThisComment[recordType!CMGT_CommentUserTag.username]
            ),
            local!allExistingCommentTagsForThisComment,
            recordType!CMGT_CommentUserTag(
              recordType!CMGT_CommentUserTag.commentId: ri!commentId,
              recordType!CMGT_CommentUserTag.username: fv!item,
              recordType!CMGT_CommentUserTag.isActive: true
            )
          )
        ),
        local!deletedCommentUserTagRecords: a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!allExistingCommentTagsForThisComment,
            expression: if(
              and(
                not(
                  contains(
                    touniformstring(local!uniqueSelectedValues),
                    tostring(
                      fv!item[recordType!CMGT_CommentUserTag.username]
                    )
                  )
                ),
                a!isNotNullOrEmpty(
                  fv!item[recordType!CMGT_CommentUserTag.commentUserTagId]
                )
              ),
              #"urn:appian:function:v1:a:update"(
                fv!item,
                recordType!CMGT_CommentUserTag.isActive,
                false
              )
              ,
              {}
            )
          )
        ),
        a!flatten(
          {
            local!newCommentUserTagRecords,
            local!deletedCommentUserTagRecords
          }
        )
      )
    ),
    /*Finally, save the map values back into their dedicated variables*/
    a!save(ri!commentUserTags, local!temp)
  }
)

_a-0000ed12-0330-8000-9d27-011c48011c48_.sail_code:
a!localVariables(
  local!sortToUse: if(
    /*SINGLE_OBJECT*/
    ri!returnType = cons!CMGT_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    ri!sort,

    /*AGGREGATION*/
    ri!returnType = cons!CMGT_ENUM_QE_RETURN_TYPE_AGGREGATION,
    ri!sort,

    /*TOTAL_COUNT*/
    ri!returnType = cons!CMGT_ENUM_QE_RETURN_TYPE_TOTAL_COUNT,
    null,

    /*DATA_SUBSET or OBJECT_ARRAY*/
    a!localVariables(
      /*Guess what the primary key is by using the first primitive field in the CDT*/
      local!primaryKeyField_guess: index(rule!CMGT_UTIL_ReturnCdtFields(
        type: ri!entityType,
        onlyPrimitiveFields: true
      ), 1, null),

      /*Grab the appropriate input sort by the returnType*/
      local!inputSort: if(
        ri!returnType = cons!CMGT_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
        ri!pagingInfo.sort,

        ri!returnType = cons!CMGT_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
        ri!sort,

        null
      ),

      /*If the primaryKeyField_guess is not null, build a secondary sort by the PK field*/
      local!secondaryPkSortToAppend: if(
        isnull(local!primaryKeyField_guess),
        {},
        #"SYSTEM_SYSRULES_sortInfo"(field: local!primaryKeyField_guess, ascending: true)
      ),

      /*Append the secondary PK sort to the sort*/
      local!sortWithSecondaryPkSortApplied: if(
        rule!CMGT_UTIL_IsBlank(local!inputSort),
        local!secondaryPkSortToAppend,
        append(local!inputSort, local!secondaryPkSortToAppend)
      ),

      local!sortWithSecondaryPkSortApplied
    )
  ),

  local!sortToUse
)

_a-0000ea42-2109-8000-9bfa-011c48011c48_.sail_code:
if(
  rule!CMGT_UTIL_IsNotBlank(ri!roles),
  a!localVariables(
    local!taskIds: rule!CMGT_UTIL_Distinct(
      ri!roles[recordType!CMGT_Role.taskId]
    ),
    local!allTasks: rule!CMGT_QR_GetTask(
      taskId: local!taskIds,
      executeWhen: a!isNotNullOrEmpty(local!taskIds)
    ),
    /*If the task was just opened, we want to send an email to those assignees even if they already exist    */
    local!newRoles: if(
      a!defaultValue(ri!isTaskOpened, false),
      /*
    When task is opened, the email will be sent to the assigned group unless the user assignee is present.
    */
      a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: local!taskIds,
          expression: a!defaultValue(
            value: rule!CMGT_UTIL_Filter(
              records: ri!roles,
              fieldValuePairs: {
                a!map(
                  field: recordType!CMGT_Role.roleTypeId,
                  value: cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE
                ),
                a!map(
                  field: recordType!CMGT_Role.taskId,
                  value: fv!item
                )
              }
            ),
            default: rule!CMGT_UTIL_Filter(
              records: ri!roles,
              fieldValuePairs: {
                a!map(
                  field: recordType!CMGT_Role.roleTypeId,
                  value: cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE,

                ),
                a!map(
                  field: recordType!CMGT_Role.taskId,
                  value: fv!item
                )
              }
            )
          )
        )
      ),
      rule!CMGT_Email_NewRolesToNotify(
        roles: ri!roles,
        roleTypeIds: rule!CMGT_UTIL_Distinct(
          ri!roles[recordType!CMGT_Role.roleTypeId]
        )
      )
    ),
    /* Assumption: Case List passed in is the full Case record */
    local!allCases: rule!CMGT_QR_GetCase(
      caseId: if(
        a!isNotNullOrEmpty(value: local!allTasks),
        local!allTasks[recordType!CMGT_Task.caseId],
        ri!roles[recordType!CMGT_Role.caseId]
      ),
      executeWhen: or(
        a!isNotNullOrEmpty(local!allTasks),
        a!isNotNullOrEmpty(
          ri!roles[recordType!CMGT_Role.caseId]
        )
      ),
      fields: recordType!CMGT_Case.caseType
    ),
    local!brandingMap: rule!CMGT_UTIL_LoadBrandingMap(),
    #"SYSTEM_SYSRULES_forEach"(
      items: local!newRoles,
      expression: a!localVariables(
        local!task: rule!CMGT_UTIL_Filter(
          records: local!allTasks,
          field: recordType!CMGT_Task.taskId,
          value: fv!item[recordType!CMGT_Role.taskId]
        ),
        local!case: a!defaultValue(
          rule!CMGT_UTIL_Filter(
            records: local!allCases,
            field: recordType!CMGT_Case.caseId,
            value: fv!item[recordType!CMGT_Role.caseId]
          ),
          rule!CMGT_UTIL_Filter(
            records: local!allCases,
            field: recordType!CMGT_Case.caseId,
            value: local!task[recordType!CMGT_Task.caseId]
          )
        ),
        local!isTask: rule!CMGT_UTIL_IsNotBlank(local!task),
        local!otherRoles: rule!CMGT_UTIL_Filter(
          records: ri!roles,
          fieldValuePairs: {
            a!map(
              field: recordType!CMGT_Role.roleTypeId,
              value: fv!item[recordType!CMGT_Role.roleTypeId]
            ),
            if(
              local!isTask,
              a!map(
                field: recordType!CMGT_Role.taskId,
                value: local!task[recordType!CMGT_Task.taskId]
              ),
              a!map(
                field: recordType!CMGT_Role.caseId,
                value: local!case[recordType!CMGT_Case.caseId]
              )
            )
          }
        ),
        local!groupUsersCount: if(
          fv!item[recordType!CMGT_Role.roleTypeId] = cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE,
          a!groupMembers(
            group: togroup(
              fv!item[recordType!CMGT_Role.groupId]
            ),
            memberType: "USER",
            pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 0)
          ).totalCount,
          null
        ),
        local!newRoleTypeCount: if(
          a!isNotNullOrEmpty(fv!item),
          length(local!otherRoles),
          {}
        ),
        local!rolesToDelete: if(
          a!isNotNullOrEmpty(fv!item),
          rule!CMGT_UTIL_Filter(
            records: ri!rolesToDelete,
            field: recordType!CMGT_Role.roleTypeId,
            value: fv!item[recordType!CMGT_Role.roleTypeId]
          ),
          {}
        ),
        local!subjectAndRecipients: a!match(
          value: fv!item[recordType!CMGT_Role.roleTypeId],
          /* When a user is assigned with a case/task   */
          equals: cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE,
          then: a!map(
            subject: if(
              local!isTask,
              #"urn:appian:translation-string:v1:2bf9ef92-dcca-46f0-b010-7f124cf09634"(
                #"urn:appian:translation-variable:v1:722f3dc9-8d87-42ec-89b4-5d93b351e4a4": local!case[recordType!CMGT_Case.friendlyId]
              ),
              #"urn:appian:translation-string:v1:e8a8f6f2-4ef7-4bf7-8081-f50b992d09c6"(
                #"urn:appian:translation-variable:v1:262ab45e-35fe-4153-8a03-52bad342dd31": local!case[recordType!CMGT_Case.friendlyId]
              )
            ),
            recipients: if(
              isusernametaken(
                fv!item[recordType!CMGT_Role.username]
              ),
              toemailrecipient(
                fv!item[recordType!CMGT_Role.username]
              ),
              null
            ),
            instruction: if(
              local!isTask,
              #"urn:appian:translation-string:v1:c2b035da-18b8-4188-bcaa-7f330e66a3cb",
              #"urn:appian:translation-string:v1:7d9521c8-978b-4276-9e00-572b3ccb9b84"
            ),
            fieldName: #"urn:appian:translation-string:v1:7bcb0001-ac03-470f-a26b-be3786709f7e",
            oldValue: index(
              local!rolesToDelete,
              #"urn:appian:record-field:v1:6799d796-883e-49ff-8975-297d87e40756/6f0c8dbc-95d2-4ba7-9fb5-f0916efa0f08/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName",
              ""
            ),
            newValue: {
              rule!CMGT_UTIL_DisplayUserFullName(
                username: fv!item[recordType!CMGT_Role.username]
              ),
              if(
                local!newRoleTypeCount > 1,
                concat(
                  "+",
                  #"urn:appian:translation-string:v1:138e2717-f77d-4cb5-9d38-6e79f12039c7"(
                    #"urn:appian:translation-variable:v1:706bfb1d-813b-4e07-adb1-b21bb5d5018b": local!newRoleTypeCount - 1
                  )
                ),
                {}
              )
            }
          ),
          /* When a group is assigned with a case/task   */
          equals: cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE,
          then: a!map(
            subject: if(
              local!isTask,
              #"urn:appian:translation-string:v1:a4f90062-5f59-4230-abd7-a23edb69184a"(
                #"urn:appian:translation-variable:v1:4d33305e-5a95-468f-b1af-b0d5001c1f80": local!case[recordType!CMGT_Case.friendlyId]
              ),
              #"urn:appian:translation-string:v1:44a08933-d187-4cf7-ab63-5621a7ff737a"(
                #"urn:appian:translation-variable:v1:57948676-df93-4426-8e27-70b75f463a79": local!case[recordType!CMGT_Case.friendlyId]
              )
            ),
            recipients: if(
              /* Checking if the group assigned with a case/task contains a user */
              local!groupUsersCount > 0,
              toemailrecipient(
                togroup(
                  fv!item[recordType!CMGT_Role.groupId]
                )
              ),
              null
            ),
            instruction: if(
              local!isTask,
              #"urn:appian:translation-string:v1:45dbbe9e-1593-473f-9786-cd5986eeb227",
              #"urn:appian:translation-string:v1:045ec8ed-8424-46ab-b76e-038a66ce0ea8"
            ),
            fieldName: #"urn:appian:translation-string:v1:559ce3f6-e852-4bee-b5b2-ca315e1ebbef",
            oldValue: #"SYSTEM_SYSRULES_forEach"(
              items: local!rolesToDelete[recordType!CMGT_Role.groupId],
              expression: group(fv!item, "groupName")
            ),
            newValue: {
              group(
                fv!item[recordType!CMGT_Role.groupId],
                "groupName"
              ),
              if(
                local!newRoleTypeCount > 1,
                concat(
                  "+",
                  #"urn:appian:translation-string:v1:138e2717-f77d-4cb5-9d38-6e79f12039c7"(
                    #"urn:appian:translation-variable:v1:706bfb1d-813b-4e07-adb1-b21bb5d5018b": local!newRoleTypeCount - 1
                  )
                ),
                {}
              )
            }
          ),
          /* When a user is added as a watcher to a case   */
          equals: cons!CMGT_REFID_ROLE_TYPE_WATCHER,
          then: a!map(
            subject: #"urn:appian:translation-string:v1:b8f7e21f-13f7-4577-9a78-eb1dc79ccd8a"(
              #"urn:appian:translation-variable:v1:7c8cc9b2-d792-4678-9266-5ab462d6a393": local!case[recordType!CMGT_Case.friendlyId]
            ),
            recipients: toemailrecipient(
              fv!item[recordType!CMGT_Role.username]
            ),
            instruction: #"urn:appian:translation-string:v1:1b43a1aa-b7d9-4c19-b9e9-bd31b6501e98",
            fieldName: #"urn:appian:translation-string:v1:4aab3312-5d55-4912-8919-d1e6f10fff37",
            oldValue: "",
            newValue: rule!CMGT_UTIL_DisplayUserFullName(
              username: fv!item[recordType!CMGT_Role.username]
            )
          ),
          /*Add new conditions below this to support other email types    */
          default: {}
        ),
        a!map(
          recipients: local!subjectAndRecipients.recipients,
          subject: local!subjectAndRecipients.subject,
          recipientEmailAddress: null,
          replacementParams: if(
            local!isTask,
            rule!CMGT_Email_TaskDetails_ReplacementParamsMap(
              task: local!task,
              case: local!case,
              brandingMap: local!brandingMap,
              instruction: local!subjectAndRecipients.instruction,
              fieldName: local!subjectAndRecipients.fieldName,
              oldValue: local!subjectAndRecipients.oldValue,
              newValue: local!subjectAndRecipients.newValue
            ),
            rule!CMGT_Email_CaseDetails_ReplacementParamsMap(
              case: local!case,
              brandingMap: local!brandingMap,
              instruction: local!subjectAndRecipients.instruction,
              fieldName: local!subjectAndRecipients.fieldName,
              oldValue: local!subjectAndRecipients.oldValue,
              newValue: local!subjectAndRecipients.newValue
            )
          )
        )
      )
    )
  ),
  null
)

