      "ACTIVE",
      "COMPLETED",
      "PAUSED",
      "CANCELLED",
      "PAUSED_BY_EXCEPTION"
    },
    local!fieldMap: {
      a!map(column: "c0", field: "processName"),
      a!map(column: "c1", field: "status"),
      a!map(column: "c2", field: "startTime"),
