                      marginBelow: "LESS",
                      accessibilityText: #"urn:appian:translation-string:v1:875e881c-49e7-47bb-bc16-c44d329f1b3d"(
                        #"urn:appian:translation-variable:v1:a9524717-9864-4132-8427-a938548d507d": ri!comment[#"urn:appian:record-field:v1:e57250b0-f216-4a23-afd9-39e7d532d302-cmgt/f1bcb252-9f3e-40ce-bb79-5a8359cef95d-cmgt/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName"],
                        #"urn:appian:translation-variable:v1:2f293bbd-41bd-4f88-ba66-8c5b1fa04c5c": ri!comment[recordType!CMGT_Comment.modifiedOn]
                      )
                    ),
                    #"SYSTEM_SYSRULES_sectionLayout_v1"(showWhen: local!isAddingComment),
                    /*In-line comment box shown if reply is clicked */
                    rule!CMGT_Comment_Sub_CommentInput(
                      comment: local!newComment,
                      required: true,
                      brandingMap: ri!brandingMap,
                      showWhen: local!isAddingComment,
                      includeExtraInput: true,
                      showInlineSaveButton: true,
                      onSubmitSaves: {
                        a!save(
                          local!newComment,
                          local!defaultNewComment
                        ),
                        a!save(local!isAddingComment, false),
                        a!save(ri!commentRefresh, now())
                      },
                      caseId: ri!comment[recordType!CMGT_Comment.caseId]
                    )
                  },
                  style: "TRANSPARENT",
                  shape: "SQUARED",
                  padding: if(
                    local!isChildComment,
                    "EVEN_LESS",
                    "NONE"
                  ),
                  marginAbove: if(
                    local!isChildComment,
                    "EVEN_LESS",
                    "NONE"
                  ),
                  showBorder: false,
                  decorativeBarPosition: if(local!isChildComment, "START", null),
                  decorativeBarColor: if(
                    local!isChildComment,
                    ri!brandingMap.TertiaryBackgroundColor,
                    null
                  )
                )
              }
            )
          }
        )
      },
      dividerColor: ri!brandingMap.DividerColor,
      divider: if(local!isChildComment, "NONE", "BELOW"),
      marginBelow: if(
        local!isChildComment,
        "EVEN_LESS",
        "LESS"
      )
    )
  }
)

