  local!isCreateProcessDone: a!refreshVariable(
    value: rule!CMGT_CheckIfCaseCreateProcessIsDone(
      caseId: ri!case[recordType!CMGT_Case.caseId]
    ),
    refreshInterval: if(fv!value, null, 0.5)
  ),
  /*Retrieve the process details for the context process models*/
  local!processStatusOfWorkflowMappingPM: a!refreshVariable(
    value: rule!CMGT_QPA_GetWorkflowPathAndMappingProcessesByCaseId(
      caseId: ri!case[recordType!CMGT_Case.caseId],
      executeWhen: rule!CMGT_UTIL_IsNotBlank(
        input: ri!case[recordType!CMGT_Case.caseId]
      )
    ),
    refreshInterval: if(
      rule!CMGT_UTIL_IsBlank(value: fv!value),
      null,
      0.5
    )
  ),
  local!allWorkflowMappingProcessDone: a!refreshVariable(
    value: and(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!processStatusOfWorkflowMappingPM,
        expression: if(fv!item.status = 1, true, false)
      )
    ),
    refreshOnReferencedVarChange: true()
  ),
  /*Refresh After is required as the above processes will not be triggered if a task is manually marked as not needed*/
  local!interfaceRefresh: a!refreshVariable(
    value: now(),
    refreshAfter: "RECORD_ACTION",
    refreshInterval: if(local!isCreateProcessDone, null, 0.5)
  ),
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshOnVarChange: {
      local!allWorkflowMappingProcessDone,
      local!interfaceRefresh
    }
  ),
  local!reopenTaskOnLoopbackProcessStatus: a!refreshVariable(
    value: rule!CMGT_QPA_GetWorkflowPathAndMappingProcessesByCaseId(
      caseId: ri!case[recordType!CMGT_Case.caseId],
      executeWhen: rule!CMGT_UTIL_IsNotBlank(
        input: ri!case[recordType!CMGT_Case.caseId]
      ),
      contextProcessModels: cons!CMGT_PM_WRITE_TASK_DETAILS
    ),
    refreshOnVarChange: local!triggerRefresh
  ),
  local!reopenTaskOnLoopbackProcessDone: a!refreshVariable(
    value: if(
      or(
        and(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!reopenTaskOnLoopbackProcessStatus,
            expression: if(fv!item.status = 1, true, false)
          )
        ),
        a!isNullOrEmpty(
          local!reopenTaskOnLoopbackProcessStatus
        )
      ),
      true,
      false
    ),
    refreshOnReferencedVarChange: true(),
    refreshOnVarChange: local!triggerRefresh
  ),
  local!layouts: {
    a!map(
      layout: "SPLIT",
      icon: "columns",
      labelKey: #"urn:appian:translation-string:v1:f4d103ee-179b-4cd5-8296-a1532b122729",
      tooltip: #"urn:appian:translation-string:v1:f4d103ee-179b-4cd5-8296-a1532b122729"
    ),
