      layout: "LIST",
      icon: "list-ul",
      labelKey: #"urn:appian:translation-string:v1:ba7699e6-3c2e-4086-86fc-4f4ca0b73096",
      tooltip: #"urn:appian:translation-string:v1:ba7699e6-3c2e-4086-86fc-4f4ca0b73096"
    ),
    a!map(
      layout: "WORKFLOW",
      icon: "network-wired",
      labelKey: #"urn:appian:translation-string:v1:5d803e4f-7706-4b83-8460-9eae2a6a629a",
      tooltip: #"urn:appian:translation-string:v1:7b752a23-d163-4150-906e-0bbef19479fb"
    )
  },
  local!selectedLayout: if(
    #"SYSTEM_SYSRULES_isPageWidth"({ "PHONE", "TABLET_PORTRAIT" }),
    local!layouts[2].layout,
    local!layouts[1].layout
  ),
  local!tasks:rule!CMGT_QR_GetTask(
    caseId: ri!case[recordType!CMGT_Case.caseId],
