a!localVariables(
  local!showVisbilityFields: if(
    rule!CMGT_LA_UTIL_IsLimitedAccessOrPortal(),
    false,
    #"urn:appian:function:v1:a:isusermemberofgroup?okey=a!isUserMemberOfGroup"(
      loggedInUser(),
      cons!CMGT_GRP_INTERNAL_USERS
    )
  ),
  #"SYSTEM_SYSRULES_sectionLayout_v1"(
    contents: {
      #"SYSTEM_SYSRULES_fileUploadField_v1"(
        label: ri!label,
        labelPosition: ri!labelPosition,
        target: cons!CMGT_FLD_TEMPORARY_DOCUMENTS,
        /*value is always null since once a document is uploaded, it moves to the file upload field of the card field*/
        value: {},
        /*Note that this save logic is complicated, so buried in its own rule*/
        saveInto: {
          rule!CMGT_Document_RecordAction_CaseUpload_SaveLogic(
            documents: ri!documents,
            case: ri!case,
            task: ri!task
          )
        },
        buttonDisplay: "LABEL_AND_ICON",
        buttonStyle: "STANDARD",
        accessibilityText: #"urn:appian:translation-string:v1:e98b5eac-30d7-47c5-a2a7-9533261bf298"
      ),
      #"SYSTEM_SYSRULES_cardLayout"(
        contents: #"SYSTEM_SYSRULES_forEach"(
          items: ri!documents,
          expression: {
            #"SYSTEM_SYSRULES_sideBySideLayout"(
              items: {
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: #"SYSTEM_SYSRULES_fileUploadField_v1"(
                    target: if(
                      rule!CMGT_LA_UTIL_IsLimitedAccessOrPortal(),
                      cons!CMGT_FLD_TEMPORARY_DOCUMENTS,
                      rule!CMGT_Document_DetermineFolderForDocument(document: fv!item)
                    ),
                    maxSelections: 1,
                    value: fv!item[recordType!CMGT_Document.appianDocId],
                    saveInto: if(
                      a!isNotNullOrEmpty(
                        fv!item[recordType!CMGT_Document.documentId]
                      ),
                      a!save(
                        fv!item[recordType!CMGT_Document.isActive],
                        false
                      ),
                      a!save(
                        ri!documents,
                        remove(ri!documents, fv!index)
                      )
                    ),
                    validations: {}
                  )
                ),
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: #"SYSTEM_SYSRULES_radioButtonField_v1"(
                    choiceLabels: {
                      #"urn:appian:translation-string:v1:4a009516-21f5-456d-8e55-a2cb910c7043",
                      #"urn:appian:translation-string:v1:56bad845-4e97-4a3b-bd64-1961f9e625d7"
                    },
                    choiceValues: { false, true },
                    label: #"urn:appian:translation-string:v1:0c034ac7-0df2-40dd-bb7f-2d02ba7d615b",
                    helpTooltip: #"urn:appian:translation-string:v1:da9b3f7a-10f9-4210-bb75-cc488b8a2f62",
                    value: fv!item[recordType!CMGT_Document.isPrivate],
                    saveInto: fv!item[recordType!CMGT_Document.isPrivate],
                    showWhen: local!showVisbilityFields,
                    required: true,
                    choiceLayout: "COMPACT",
                    accessibilityText: extract(
                      getcontentobjectdetailsbyid(
                        fv!item[recordType!CMGT_Document.appianDocId]
                      ),
                      "Name:",
                      ","
                    )
                  )
                ),
                #"SYSTEM_SYSRULES_sideBySideItem"(),

              },
              stackWhen: { "PHONE", "TABLET_PORTRAIT" },
              marginBelow: "STANDARD",
              marginAbove: "STANDARD"
            )
          }
        ),
        style: ri!brandingMap.TertiaryBackgroundColor,
        marginBelow: "NONE",
        showBorder: false(),
        showWhen: if(
          a!isNullOrEmpty(ri!documents),
          false(),
          and(
            rule!CMGT_UTIL_IsNotBlank(
              ri!documents[recordType!CMGT_Document.appianDocId]
            ),
            rule!CMGT_UTIL_IsBlank(
              ri!documents[recordType!CMGT_Document.documentId]
            )
          )
        )
      )
    },
    validations: {
      #"SYSTEM_SYSRULES_validationMessage"(
        message: #"urn:appian:translation-string:v1:fe3fcd09-5ed5-4f1e-9594-c09c19f59809",
        validateAfter: "SUBMIT",
        showWhen: and(
          a!defaultValue(ri!required, false),
          rule!CMGT_UTIL_IsBlank(
