a!localVariables(
  local!searchTerm,
  local!searchTermTemp,
  local!showUnpublished: true(),
  local!isCurrentLevelAllCases: tostring(ri!currentLevelSelected) = #"urn:appian:translation-string:v1:3807a409-6086-4767-8e1b-dd26b980ff5f",
  /*Query to Handle the BatchSize of Custom Paging*/
  local!allAvailableResources: rule!CMGT_KB_QR_GetResources(
    returnType: "OBJECT_ARRAY",
    triggerRefresh: ri!triggerRefresh,
    searchTerm: local!searchTerm,
    fields: {
      recordType!CMGT_KB_Resource.document,
      recordType!CMGT_KB_Resource.caseType
    },
    searchFields: {
      recordType!CMGT_KB_Resource.title,
      recordType!CMGT_KB_Resource.resourceText.plainText,
      recordType!CMGT_KB_Resource.modifiedBy,
      #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/20381804-6324-459c-8688-d93a850ce2ab/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName",
      #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/07608b0d-8089-4216-a3e1-a3cb646c086f/9b6a045d-e2da-4963-a8c7-f7833625e811/SYSTEM_RECORD_TYPE_DOCUMENT_FIELD_name",
      #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/07608b0d-8089-4216-a3e1-a3cb646c086f/9b6a045d-e2da-4963-a8c7-f7833625e811/SYSTEM_RECORD_TYPE_DOCUMENT_FIELD_description"
    },
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_KB_Resource.status,
        operator: "=",
        value: #"urn:appian:translation-string:v1:c46d69c3-d638-48ac-abee-bfc4414a30cf",
        applyWhen: toboolean(local!showUnpublished) <> true()
      )
    },
    additionalLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
      operator: "OR",
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_KB_Resource.caseTypeUuid,
          operator: "is null"
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_KB_Resource.caseTypeUuid,
          operator: "in",
          value: {
            ri!selectedCaseTypeUuid,
            ri!selectedCaseType[recordType!CMGT_CFG_CaseType.parentUuid]
          }
        )
      }
    ),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: cons!CMGT_INT_BATCH_SIZE_MAX,
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!CMGT_KB_Resource.modifiedOn,
        ascending: false
      )
    )
  ),
  local!numberOfAllCaseResources: length(
    {
      rule!CMGT_UTIL_Filter(
        records: local!allAvailableResources,
        field: recordType!CMGT_KB_Resource.caseType.isCategory,
        value: { true, false },
        filterType: "REJECT"
      )
    }
  ),
  local!numberOfCaseCategoryResources: length(
    {
      rule!CMGT_UTIL_Filter(
        records: local!allAvailableResources,
        field: recordType!CMGT_KB_Resource.caseType.isCategory,
        value: true
      )
    }
  ),
  local!numberOfCaseTypeResources: length(
    {
      rule!CMGT_UTIL_Filter(
        records: local!allAvailableResources,
        field: recordType!CMGT_KB_Resource.caseType.isCategory,
        value: false
      )
    }
  ),
  local!allCasesBatchSize: if(
    or(
      local!isCurrentLevelAllCases,
      and(
        ri!currentLevelSelected = ri!displayConfigs.hierarchyLevel[2],
        local!numberOfCaseCategoryResources = 0
      ),
      and(
        ri!currentLevelSelected = ri!displayConfigs.hierarchyLevel[3],
        local!numberOfCaseCategoryResources = 0,
        local!numberOfCaseTypeResources = 0
      )
    ),
    cons!CMGT_KB_INT_BATCH_SIZE_MEDIUM_PLUS,
    cons!CMGT_KB_INT_BATCH_SIZE_SMALL
  ),
  local!allCasesPagingInfo: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: local!allCasesBatchSize,
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!CMGT_KB_Resource.modifiedOn,
        ascending: false
      )
    ),
    refreshOnVarChange: local!searchTerm
  ),
  local!allCasesLevelResources: rule!CMGT_KB_QR_GetResources(
    returnType: "DATA_SUBSET",
    triggerRefresh: ri!triggerRefresh,
    searchTerm: local!searchTerm,
    fields: {
      recordType!CMGT_KB_Resource.document,
      recordType!CMGT_KB_Resource.caseType
    },
    searchFields: {
      recordType!CMGT_KB_Resource.title,
      recordType!CMGT_KB_Resource.resourceText.plainText,
      recordType!CMGT_KB_Resource.modifiedBy,
      #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/20381804-6324-459c-8688-d93a850ce2ab/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName",
      #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/07608b0d-8089-4216-a3e1-a3cb646c086f/9b6a045d-e2da-4963-a8c7-f7833625e811/SYSTEM_RECORD_TYPE_DOCUMENT_FIELD_name",
      #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/07608b0d-8089-4216-a3e1-a3cb646c086f/9b6a045d-e2da-4963-a8c7-f7833625e811/SYSTEM_RECORD_TYPE_DOCUMENT_FIELD_description"
    },
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_KB_Resource.status,
        operator: "=",
        value: #"urn:appian:translation-string:v1:c46d69c3-d638-48ac-abee-bfc4414a30cf",
        applyWhen: toboolean(local!showUnpublished) <> true()
      )
    },
    additionalLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
      operator: "AND",
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_KB_Resource.caseTypeUuid,
          operator: "is null"
        )
      }
    ),
    pagingInfo: local!allCasesPagingInfo
  ),
  local!categoryLevelBatchSize: if(
    or(
      and(
        ri!currentLevelSelected = ri!displayConfigs.hierarchyLevel[2],
        local!numberOfAllCaseResources = 0
      ),
      and(
        ri!currentLevelSelected = ri!displayConfigs.hierarchyLevel[3],
        local!numberOfAllCaseResources = 0,
        local!numberOfCaseTypeResources = 0
      )
    ),
    cons!CMGT_KB_INT_BATCH_SIZE_MEDIUM_PLUS,
    cons!CMGT_KB_INT_BATCH_SIZE_SMALL
  ),
  local!categoryLevelPagingInfo: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: local!categoryLevelBatchSize,
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!CMGT_KB_Resource.modifiedOn,
        ascending: false
      )
    ),
    refreshOnVarChange: local!searchTerm
  ),
  local!categoryLevelResources: if(
    rule!CMGT_UTIL_IsNotBlank(ri!selectedCaseTypeUuid),
    rule!CMGT_KB_QR_GetResources(
      returnType: "DATA_SUBSET",
      triggerRefresh: ri!triggerRefresh,
      searchTerm: local!searchTerm,
      fields: {
        recordType!CMGT_KB_Resource.document,
        recordType!CMGT_KB_Resource.caseType
      },
      searchFields: {
        recordType!CMGT_KB_Resource.title,
        recordType!CMGT_KB_Resource.resourceText.plainText,
        recordType!CMGT_KB_Resource.modifiedBy,
        #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/20381804-6324-459c-8688-d93a850ce2ab/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName",
        #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/07608b0d-8089-4216-a3e1-a3cb646c086f/9b6a045d-e2da-4963-a8c7-f7833625e811/SYSTEM_RECORD_TYPE_DOCUMENT_FIELD_name",
        #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/07608b0d-8089-4216-a3e1-a3cb646c086f/9b6a045d-e2da-4963-a8c7-f7833625e811/SYSTEM_RECORD_TYPE_DOCUMENT_FIELD_description"
      },
      additionalFilters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_KB_Resource.status,
          operator: "=",
          value: #"urn:appian:translation-string:v1:c46d69c3-d638-48ac-abee-bfc4414a30cf",
          applyWhen: toboolean(local!showUnpublished) <> true()
        )
      },
      additionalLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!CMGT_KB_Resource.caseTypeUuid,
            operator: "not null",
            applyWhen: rule!CMGT_UTIL_IsNotBlank(ri!selectedCaseTypeUuid)
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!CMGT_KB_Resource.caseTypeUuid,
            operator: "in",
            value: {
              ri!selectedCaseTypeUuid,
              ri!selectedCaseType[recordType!CMGT_CFG_CaseType.parentUuid]
            },
            applyWhen: rule!CMGT_UTIL_IsNotBlank(ri!selectedCaseTypeUuid)
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!CMGT_KB_Resource.caseType.isCategory,
            operator: "=",
            value: true(),
            applyWhen: rule!CMGT_UTIL_IsNotBlank(ri!selectedCaseTypeUuid)
          )
        }
      ),
      pagingInfo: local!categoryLevelPagingInfo
    ),
    null()
  ),
  local!caseTypeLevelBatchSize: if(
    and(
      ri!currentLevelSelected = ri!displayConfigs.hierarchyLevel[3],
      local!numberOfAllCaseResources = 0,
      local!numberOfCaseCategoryResources = 0
    ),
    cons!CMGT_KB_INT_BATCH_SIZE_MEDIUM_PLUS,
    cons!CMGT_KB_INT_BATCH_SIZE_SMALL
  ),
  local!caseTypeLevelPagingInfo: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: local!caseTypeLevelBatchSize,
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!CMGT_KB_Resource.modifiedOn,
        ascending: false
      )
    ),
    refreshOnVarChange: local!searchTerm
  ),
  local!caseTypeLevelResources: if(
    rule!CMGT_UTIL_IsNotBlank(ri!selectedCaseTypeUuid),
    rule!CMGT_KB_QR_GetResources(
      returnType: "DATA_SUBSET",
      triggerRefresh: ri!triggerRefresh,
      searchTerm: local!searchTerm,
      fields: {
        recordType!CMGT_KB_Resource.document,
        recordType!CMGT_KB_Resource.caseType
      },
      searchFields: {
        recordType!CMGT_KB_Resource.title,
        recordType!CMGT_KB_Resource.resourceText.plainText,
        recordType!CMGT_KB_Resource.modifiedBy,
        #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/20381804-6324-459c-8688-d93a850ce2ab/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName",
        #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/07608b0d-8089-4216-a3e1-a3cb646c086f/9b6a045d-e2da-4963-a8c7-f7833625e811/SYSTEM_RECORD_TYPE_DOCUMENT_FIELD_name",
        #"urn:appian:record-field:v1:462e1408-1c19-4591-b946-fce9233909ce/07608b0d-8089-4216-a3e1-a3cb646c086f/9b6a045d-e2da-4963-a8c7-f7833625e811/SYSTEM_RECORD_TYPE_DOCUMENT_FIELD_description"
      },
      additionalFilters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_KB_Resource.status,
          operator: "=",
          value: #"urn:appian:translation-string:v1:c46d69c3-d638-48ac-abee-bfc4414a30cf",
          applyWhen: toboolean(local!showUnpublished) <> true()
        )
      },
      additionalLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!CMGT_KB_Resource.caseTypeUuid,
            operator: "not null"
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!CMGT_KB_Resource.caseTypeUuid,
            operator: "in",
            value: { ri!selectedCaseTypeUuid },
            applyWhen: rule!CMGT_UTIL_IsNotBlank(ri!selectedCaseTypeUuid)
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!CMGT_KB_Resource.caseType.isCategory,
            operator: "=",
            value: false()
          )
        }
      ),
      pagingInfo: local!caseTypeLevelPagingInfo
    ),
    null()
  ),
  local!totalCountOfAllCasesResources: index(
    local!allCasesLevelResources,
    "totalCount",
    null()
  ),
  local!totalCountOfCategoryLevelResources: index(
    local!categoryLevelResources,
    "totalCount",
    null()
  ),
  local!totalCountOfCaseTypeLevelResources: index(
    local!caseTypeLevelResources,
    "totalCount",
    null()
  ),
  #"SYSTEM_SYSRULES_cardLayout"(
    contents: {
      #"SYSTEM_SYSRULES_sideBySideLayout"(
        items: {
          /*Search*/
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_textField"(
              label: #"urn:appian:translation-string:v1:7607e9f5-1e18-4ff8-883a-700b1b6ed238",
              labelPosition: "COLLAPSED",
              placeholder: "Search",
              value: local!searchTermTemp,
              saveInto: {
                a!save(local!searchTermTemp, trim(save!value)),
                if(
                  a!isNullOrEmpty(local!searchTermTemp),
                  a!save(local!searchTerm, local!searchTermTemp),
                  {}
                )
              },
              refreshAfter: "KEYPRESS",
              marginAbove: "EVEN_LESS",
              marginBelow: "NONE"
            ),
            width: "1X"
          ),
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_ButtonArrayLayout"(
              buttons: {
                #"SYSTEM_SYSRULES_buttonWidget_v1"(
                  label: #"urn:appian:translation-string:v1:7607e9f5-1e18-4ff8-883a-700b1b6ed238",
                  saveInto: a!save(local!searchTerm, local!searchTermTemp),
                  style: "OUTLINE",
                  size: "SMALL"
                )
              },
              align: "START",
              marginBelow: "NONE",
              marginAbove: "EVEN_LESS"
            ),
            width: "MINIMIZE"
          ),
          /*Add resource record action*/
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_recordActionField_v1"(
              actions: if(
                a!isNotNullOrEmpty(ri!selectedCaseTypeUuid),
                #"SYSTEM_SYSRULES_recordActionItem"(
                  action: #"urn:appian:record-action:v1:82aa3b28-94c2-4d3c-a403-bb813bd76155/faa27aab-0728-4c54-a932-daa98dc7da5b",
                  identifier: ri!selectedCaseTypeUuid
                ),
                #"SYSTEM_SYSRULES_recordActionItem"(
                  action: #"urn:appian:record-action:v1:462e1408-1c19-4591-b946-fce9233909ce/42eaf4ed-12b3-4919-88b9-8f34e6a65d3d"
                )
              ),
              style: "TOOLBAR_PRIMARY",
              display: "LABEL_AND_ICON",
              align: "END"
            ),
            width: "2X"
          )
        },
        marginBelow: "STANDARD"
