      /*Show Unnpublished Toggle*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        labelPosition: "COLLAPSED",
        value: {
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: if(
              local!showUnpublished,
              "toggle-on",
              "toggle-off"
            ),
            color: if(
              local!showUnpublished,
              ri!brandingMap.AccentColor,
              ri!brandingMap.NavigationHighlightColor
            ),
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              value: local!showUnpublished,
              saveInto: {
                a!save(
                  local!showUnpublished,
                  not(local!showUnpublished)
                ),
                a!save(ri!triggerRefresh, now())
              }
            ),
            linkStyle: "STANDALONE",
            size: "MEDIUM",
            altText: if(
              local!showUnpublished,
              #"urn:appian:translation-string:v1:d1be97ef-5362-4f3b-aee6-542918496309",
              #"urn:appian:translation-string:v1:2912e702-de09-44db-a6d7-12dbc7bd4550"
            )
          ),
          char(32),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: #"urn:appian:translation-string:v1:173f735a-72c6-45fe-b8c7-072109ffb845",
            size: "STANDARD"
          )
        },
        marginBelow: "MORE"
      ),
      a!match(
        value: null,
        whenTrue: and(
          a!match(
            value: ri!currentLevelSelected,
            equals: ri!displayConfigs.hierarchyLevel[1],
            /*No Search results on All Cases level*/
            then: rule!CMGT_UTIL_IsBlank(local!allCasesLevelResources.data),
            equals: ri!displayConfigs.hierarchyLevel[2],
            /*No Search results on Category level*/
            then: and(
              rule!CMGT_UTIL_IsBlank(local!allCasesLevelResources.data),
              rule!CMGT_UTIL_IsBlank(local!categoryLevelResources.data)
            ),
            equals: ri!displayConfigs.hierarchyLevel[3],
            /*No Search results on Case Type level*/
            then: and(
              rule!CMGT_UTIL_IsBlank(local!allCasesLevelResources.data),
              rule!CMGT_UTIL_IsBlank(local!categoryLevelResources.data),
              rule!CMGT_UTIL_IsBlank(local!caseTypeLevelResources.data)
            ),
            default: false()
          )
        ),
        /* Empty state */
        then: #"SYSTEM_SYSRULES_cardLayout"(
          contents: {
            #"SYSTEM_SYSRULES_richTextDisplayField"(marginBelow: "EVEN_MORE"),
            rule!CMGT_UTIL_DisplayEmptyState(
              brandingMap: ri!brandingMap,
              primaryText: #"urn:appian:translation-string:v1:21d9dfbb-38eb-494f-a759-2c575e3f8b2c",
              docImage: cons!CMGT_DOC_IMAGES_NO_RESULTS_ICON,
              imageSize: "SMALL",
              primaryTextSize: "MEDIUM"
            ),
            #"SYSTEM_SYSRULES_richTextDisplayField"(marginBelow: "EVEN_MORE"),

          },
          height: "EXTRA_TALL",
          padding: "EVEN_MORE",
          marginAbove: "EVEN_MORE",
          showBorder: false,
          style: "TRANSPARENT"
        ),
        /*Purposely not iterated using a!foreach to simplify the logics behind Search & Custom Paging*/
        default: {
          /*All Cases Resources*/
          rule!CMGT_KB_SUB_ResourcesCardDisplay(
            hierarchyLevel: ri!displayConfigs.hierarchyLevel[1],
            brandingMap: ri!brandingMap,
            resourcesToDisplay: local!allCasesLevelResources.data,
            selectedResource: ri!selectedResource,
            numberOfResources: local!totalCountOfAllCasesResources,
            pagingInfo: local!allCasesPagingInfo,
            batchSize: local!allCasesBatchSize
          ),
          /*Category Level Resources*/
          rule!CMGT_KB_SUB_ResourcesCardDisplay(
            hierarchyLevel: ri!displayConfigs.hierarchyLevel[2],
            brandingMap: ri!brandingMap,
            resourcesToDisplay: local!categoryLevelResources.data,
            selectedResource: ri!selectedResource,
            numberOfResources: local!totalCountOfCategoryLevelResources,
            pagingInfo: local!categoryLevelPagingInfo,
            batchSize: local!categoryLevelBatchSize
          ),
          /*Case type level Resources*/
          rule!CMGT_KB_SUB_ResourcesCardDisplay(
            hierarchyLevel: ri!displayConfigs.hierarchyLevel[3],
            brandingMap: ri!brandingMap,
            resourcesToDisplay: local!caseTypeLevelResources.data,
            selectedResource: ri!selectedResource,
            numberOfResources: local!totalCountOfCaseTypeLevelResources,
            pagingInfo: local!caseTypeLevelPagingInfo,
            batchSize: local!caseTypeLevelBatchSize
          )
        }
      )
    },
    style: "TRANSPARENT",
    padding: "NONE",
    marginBelow: "MORE",
    showBorder: false,
    shape: ri!brandingMap.CardShape,
    showShadow: ri!brandingMap.CardShowShadow
  )
)

_a-0000e9ae-873e-8000-9baf-011c48011c48_.sail_code:
a!localVariables(
  local!daysOfWeek: {
    #"urn:appian:translation-string:v1:54e17271-ff88-44b6-bdbb-6757ac512213",
    #"urn:appian:translation-string:v1:6496e4b9-1e17-43e5-a14e-74a659193405",
    #"urn:appian:translation-string:v1:ac2f886c-8ad6-4726-8823-8d9c51d84e8e",
    #"urn:appian:translation-string:v1:e67fbbe8-bb1b-4fe4-a68b-0bfc0cb9123b",
    #"urn:appian:translation-string:v1:ef35d9ec-d068-429d-8e8f-6ef947b5ba5f",
    #"urn:appian:translation-string:v1:581a7892-c388-4a0d-b7a9-9b9347700100",
    #"urn:appian:translation-string:v1:bdda4113-30a7-4f47-af99-d71b9d3e5665"
  },
  local!monthDates: rule!CMGT_UTIL_CalendarField_GetMonthDates(startDate: ri!firstDate),
  local!numOfWeeks: if(
    a!isNullOrEmpty(local!monthDates),
    0,
    length(local!monthDates) / 7
  ),
  local!currentYear: year(ri!firstDate),
  local!currentMonth: month(ri!firstDate),
  local!currentDay: day(ri!firstDate),
  local!selectableDays: if(
    rule!CMGT_UTIL_IsNotBlank(ri!selectableDates),
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!selectableDates,
      expression: rule!CMGT_UTIL_GetDateFromDateTime(
        dateTimeValue: fv!item,
        convertToSystemTimeZone: false
      )
    ),
    todate(null)
  ),
  {
    #"SYSTEM_SYSRULES_cardLayout"(
      contents: {
        #"SYSTEM_SYSRULES_sideBySideLayout"(
          items: {
            #"SYSTEM_SYSRULES_sideBySideItem"(width: "MINIMIZE"),
            /*Month and year label*/
            #"SYSTEM_SYSRULES_sideBySideItem"(
              item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: {
                  #"SYSTEM_SYSRULES_richTextHeader"(
                    text: {
                      text(
                        date(
                          local!currentYear,
                          local!currentMonth,
                          local!currentDay
                        ),
                        "mmmm yyyy"
                      )
                    },
                    size: "SMALL"
                  )
                }
              )
            ),
            /*Navigation icon*/
            #"SYSTEM_SYSRULES_sideBySideItem"(
              item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: {
                  #"SYSTEM_SYSRULES_richTextIcon"(
                    icon: "angle-left",
                    altText: #"urn:appian:translation-string:v1:5e14ef93-fcae-46cf-ab0b-2f508681b1d3",
                    /*restrict user navigating to previous month*/
                    link: #"SYSTEM_SYSRULES_dynamicLink"(
                      saveInto: {
                        if(
                          local!currentMonth = 1,
                          /*If current month is January, have to decrease the year, set month to Dec (12), and set day to the 1st (1)*/
                          a!save(
                            ri!firstDate,
                            date(local!currentYear - 1, 12, 1)
                          ),
                          /*If current month is not January, year is the same, have to decrease the month, and set day to the 1st (1)*/
                          a!save(
                            ri!firstDate,
                            date(
                              local!currentYear,
                              local!currentMonth - 1,
                              1
                            )
                          )
                        )
                      }
                    ),
                    linkStyle: "STANDALONE",
                    color: "STANDARD",
                    size: "MEDIUM"
                  ),
                  /*space between paging icons*/
                  " ",
                  #"SYSTEM_SYSRULES_richTextIcon"(
                    icon: "angle-right",
                    altText: #"urn:appian:translation-string:v1:3a18859f-5a01-447e-97a1-b32f15c381ea",
                    /*user shouldn't be able to navigate beyond the max case due date month */
                    /*eg: last case due month is on november, user shouldn't be able to navigate after november*/
                    link: #"SYSTEM_SYSRULES_dynamicLink"(
                      saveInto: {
                        if(
                          local!currentMonth = 12,
                          /*If current month is December, have to increment the year, set month to Jan (1), and set day to the 1st (1)*/
                          a!save(
                            ri!firstDate,
                            date(local!currentYear + 1, 1, 1)
                          ),
                          /*If current month is not December, year is the same, have to increment the month, and set day to the 1st (1)*/
                          a!save(
                            ri!firstDate,
                            date(
                              local!currentYear,
                              local!currentMonth + 1,
                              1
                            )
                          )
                        )
                      }
                    ),
                    linkStyle: "STANDALONE",
                    color: "STANDARD",
                    size: "MEDIUM"
                  )
                }
              ),
              width: "MINIMIZE"
            ),
            #"SYSTEM_SYSRULES_sideBySideItem"(width: "MINIMIZE")
          },
          alignVertical: "MIDDLE"
        ),
        #"SYSTEM_SYSRULES_gridLayout"(
          label: ri!label,
          labelPosition: "COLLAPSED",
          headerCells: #"SYSTEM_SYSRULES_forEach"(
            items: local!daysOfWeek,
            expression: #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: fv!item, align: "CENTER")
          ),
          columnConfigs: {
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON_PLUS"),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON"),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON"),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON"),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON"),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON"),
            #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON")
          },
          rows: #"SYSTEM_SYSRULES_forEach"(
            items: enumerate(local!numOfWeeks) + 1,
            expression: #"SYSTEM_SYSRULES_gridRowLayout"(
              contents: {
                a!localVariables(
                  local!rowNum: fv!item,
                  #"SYSTEM_SYSRULES_forEach"(
                    items: local!daysOfWeek,
                    expression: a!localVariables(
                      local!date: index(
                        index(
                          local!monthDates,
                          enumerate(7) + 7 * (local!rowNum - 1) + 1,
                          null
                        ),
                        fv!index,
                        {}
                      ),
                      local!isSelectable: contains(local!selectableDays, local!date),
                      #"SYSTEM_SYSRULES_richTextDisplayField"(
                        labelPosition: "COLLAPSED",
                        value: {
                          if(
                            or(
                              month(local!date) < local!currentMonth,
                              month(local!date) > local!currentMonth
                            ),
                            {
                              #"SYSTEM_SYSRULES_richTextItem_v1"(text: " "),
                              char(10),
                              #"SYSTEM_SYSRULES_richTextItem_v1"()
                            },
                            #"SYSTEM_SYSRULES_richTextItem_v1"(
                              text: {
                                day(local!date),
                                char(10),
                                if(
                                  local!isSelectable,
                                  #"SYSTEM_SYSRULES_richTextIcon"(
                                    icon: if(
                                      local!date = ri!selectedDate,
                                      "circle",
                                      "circle-o"
                                    ),
                                    size: "SMALL",
                                    altText: a!match(
                                      value: local!date,
                                      equals: ri!selectedDate,
                                      then: #"urn:appian:translation-string:v1:bba99ad4-c088-4129-aaa7-bd8561f12981",
                                      whenTrue: local!isSelectable,
                                      then: #"urn:appian:translation-string:v1:56face77-2026-4ac3-b5db-fce74bc46165",
                                      default: null()
                                    )
                                  ),
                                  #"SYSTEM_SYSRULES_richTextItem_v1"(text: " ")
                                )
                              },
                              link: #"SYSTEM_SYSRULES_dynamicLink"(
                                saveInto: {
                                  a!save(
                                    ri!selectedDate,
                                    if(
                                      local!date = ri!selectedDate,
                                      null,
                                      local!date
                                    )
                                  )
                                },
                                showWhen: local!isSelectable
                              ),
                              linkStyle: "STANDALONE",
                              color: "STANDARD",
                              style: if(
                                local!date = ri!selectedDate,
                                "STRONG",
                                "PLAIN"
                              )
                            )
                          )
                        },
                        align: "CENTER"
                      )
                    )
                  )
                )
              }
            )
          ),
          shadeAlternateRows: true,
          borderStyle: "LIGHT"
        ),
        #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: #"urn:appian:translation-string:v1:bb35dd48-fc7e-4d13-a480-deb04257d520",
              link: #"SYSTEM_SYSRULES_dynamicLink"(saveInto: a!save(ri!selectedDate, null)),
              linkStyle: "STANDALONE"
            ),
            #"SYSTEM_SYSRULES_richTextItem_v1"(text: " ")
          },
          showWhen: a!isNotNullOrEmpty(ri!selectedDate),
          align: "RIGHT",
          marginAbove: "NONE",
          marginBelow: "NONE"
        )
      },
      height: "AUTO",
      style: "TRANSPARENT",
      padding: "NONE",
      marginAbove: "STANDARD",
      marginBelow: "NONE",
      showBorder: false,
      showShadow: false
    )
  }
)

_a-0000ee9e-ca68-8000-9daf-011c48011c48_.sail_code:
a!localVariables(
  local!brandingMap: rule!CMGT_UTIL_LoadBrandingMap(),
  local!searchCategories: rule!CMGT_SMS_AI_QR_GetCategory(returnType: "OBJECT_ARRAY"),
  local!selectedCategoryTemp: local!searchCategories[recordType!CMGT_SMS_AI_CFG_Category.categoryId],
  local!selectedCategory,
  local!searchTextTemp,
  local!searchText,
  local!activeTab: a!refreshVariable(
    value: a!match(
      value: {},
      whenTrue: contains(
        tointeger(local!selectedCategory),
        { 1, 2 }
      ),
      then: 1,
      whenTrue: contains(tointeger(local!selectedCategory), 1),
      then: 2,
      whenTrue: contains(tointeger(local!selectedCategory), 2),
      then: 3,
      default: 1
    ),
    refreshOnVarChange: local!searchText
  ),
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshAfter: "RECORD_ACTION",
    refreshOnVarChange: local!activeTab
  ),
  local!searchHistory: a!refreshVariable(
    value: rule!CMGT_SMS_AI_QR_GetSearchHistory(
      returnType: "OBJECT_ARRAY",
      createdBy: loggedInUser(),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: cons!CMGT_SMS_AI_INT_BATCH_SIZE_EXTRA_SMALL,
        sort: #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!CMGT_SMS_AI_SearchHistory.modifiedOn,
          ascending: false
        )
      ),
      fields: recordType!CMGT_SMS_AI_SearchHistory.searchHistoryCategoryMap,
      triggerRefresh: local!triggerRefresh
    ),
    refreshOnVarChange: local!triggerRefresh
  ),
  /*The first instance of validation should be displayed on the click of submit button.This value will be updated based on the local!showvalidation during a button click */
  local!displayValidation: false,
  /*Adding separate validation for character count check because OOTB validation when displayed causes mismatch in text field and button alignment*/
  local!validationForSearchText: a!refreshVariable(
    value: len(local!searchTextTemp) > cons!CMGT_SMS_AI_INT_MAX_CHAR_LIMIT_LARGE,
    refreshOnVarChange: local!displayValidation
  ),
  local!showValidation: or(
    rule!CMGT_UTIL_IsBlank(local!searchTextTemp),
    rule!CMGT_UTIL_IsBlank(local!selectedCategoryTemp),
    local!validationForSearchText
  ),
  /*To display the space above the card only initially. The displaySpace will be incremented in further saves.*/
  /*Not using boolean here as, the change in value will not display the space properly not even when first proper search is executed*/
  local!displaySpace: 1,
  /*Cases Tab*/
  local!selectedCaseStatus: a!refreshVariable(
    value: null,
    refreshOnVarChange: local!triggerRefresh
  ),
  local!selectedCaseTypes: a!refreshVariable(
    value: null,
    refreshOnVarChange: local!triggerRefresh
  ),
  local!selectedCaseAssignees: a!refreshVariable(
    value: null,
    refreshOnVarChange: local!triggerRefresh
  ),
  local!selectedCaseAssignedGroups: a!refreshVariable(
    value: null,
    refreshOnVarChange: local!triggerRefresh
  ),
  local!pagingInfoForCases: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: cons!CMGT_INT_BATCH_SIZE_MEDIUM/*INCORPORATION STEP - Cases - Add sortInfo() to sort based on similarity score*/
      ,
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: #"urn:appian:record-search-results:v1:d81cdd0e-c54d-4b8e-beec-b7ae37fefe7f/allSearchFields/similarityScore",
        ascending: false
      )
    ),
    refreshOnVarChange: {
      local!triggerRefresh,
      local!selectedCaseStatus,
      local!selectedCaseTypes,
      local!selectedCaseAssignees,
      local!selectedCaseAssignedGroups
    }
  ),
  local!searchResultsForCases: rule!CMGT_SMS_AI_QR_GetSmartSearchResultsOnCases(
    searchTerm: local!searchText,
    pagingInfo: local!pagingInfoForCases,
    caseType: local!selectedCaseTypes,
    status: local!selectedCaseStatus,
    assignee: local!selectedCaseAssignees,
    assignedGroup: local!selectedCaseAssignedGroups,
    triggerRefresh: local!triggerRefresh,
    selectedCategory: local!selectedCategory
  ),
  /*Tasks Tab*/
  local!selectedTaskStatus: a!refreshVariable(
    value: null,
    refreshOnVarChange: local!triggerRefresh
  ),
  local!selectedTaskTypes: a!refreshVariable(
    value: null,
    refreshOnVarChange: local!triggerRefresh
  ),
  local!selectedTaskAssignees: a!refreshVariable(
    value: null,
    refreshOnVarChange: local!triggerRefresh
  ),
  local!selectedTaskAssignedGroups: a!refreshVariable(
    value: null,
    refreshOnVarChange: local!triggerRefresh
  ),
  local!pagingInfoForTasks: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: cons!CMGT_INT_BATCH_SIZE_MEDIUM/*INCORPORATION STEP - Tasks - Add sortInfo() to sort based on similarity score*/
      ,
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: #"urn:appian:record-search-results:v1:ee5aa46d-0228-430f-a794-1134a11fbcc0/allSearchFields/similarityScore",
        ascending: false
      )
    ),
    refreshOnVarChange: {
      local!triggerRefresh,
      local!selectedTaskTypes,
      local!selectedTaskStatus,
      local!selectedTaskAssignedGroups,
      local!selectedTaskAssignees
    }
  ),
  local!searchResultsForTasks: rule!CMGT_SMS_AI_QR_GetSmartSearchResultsOnTasks(
    searchTerm: local!searchText,
    pagingInfo: local!pagingInfoForTasks,
    statusIds: local!selectedTaskStatus,
    taskTypesIds: local!selectedTaskTypes,
    assignee: local!selectedTaskAssignees,
    assignedGroup: local!selectedTaskAssignedGroups,
    triggerRefresh: local!triggerRefresh,
    selectedCategory: local!selectedCategory
  ),
  /*Top Tab*/
  local!topSearchResults: rule!CMGT_SMS_AI_GetTopSmartSearchResults(
    cases: index(
      local!searchResultsForCases,
      "data",
      null
    ),
    tasks: index(
      local!searchResultsForTasks,
      "data",
      null
    )
  ),
  local!tabs: {
    a!map(
      name: #"urn:appian:translation-string:v1:7fb4c23f-a2ce-4572-b4e3-1fac53386e86",
      showTabCount: false(),
      showTab: contains(
        tointeger(local!selectedCategory),
        { 1, 2 }
      )
    ),
    a!map(
      name: #"urn:appian:translation-string:v1:afeebca1-14f6-4395-bf5d-88a61a5bd34e",
      showTabCount: false(),
      showTab: contains(tointeger(local!selectedCategory), 1)
    ),
    a!map(
      name: #"urn:appian:translation-string:v1:ae3d3f86-01e2-4653-a614-ebbd7e9c9cde",
      showTabCount: false(),
      showTab: contains(tointeger(local!selectedCategory), 2)
    )
  },
  #"SYSTEM_SYSRULES_headerContentLayout"(
    contents: {
      /*Empty Space*/
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(contents: {}),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_cardLayout"(
                showBorder: false,
                style: local!brandingMap.DefaultBackgroundColor,
                height: "SHORT_PLUS"
              ),

            },
            width: "WIDE_PLUS"
          ),
          #"SYSTEM_SYSRULES_columnLayout"(contents: {})
        },
        showWhen: local!displaySpace = 1
      ),
      /*Search Box*/
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(contents: {}),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_cardLayout"(
                contents: {
                  #"SYSTEM_SYSRULES_cardLayout"(
                    contents: {
                      #"SYSTEM_SYSRULES_headingField"(
                        text: #"urn:appian:translation-string:v1:77327e89-a5b4-4952-a593-97b2f85f406a",
                        size: "MEDIUM_PLUS",
                        fontWeight: "SEMI_BOLD",
                        marginBelow: "LESS"
                      ),
                      #"SYSTEM_SYSRULES_richTextDisplayField"(
                        labelPosition: "COLLAPSED",
                        value: {
                          #"SYSTEM_SYSRULES_richTextItem_v1"(
                            text: #"urn:appian:translation-string:v1:0f395d2d-e6f2-48b6-afcd-900352d84720",
                            color: local!brandingMap.SecondaryTextColor,
                            size: "MEDIUM"
                          )
                        }
                      )
                    },
                    style: local!brandingMap.ConfigBackgroundColor,
                    shape: "ROUNDED",
                    padding: "STANDARD",
                    showBorder: false,
                    showWhen: local!displaySpace = 1
                  ),
                  #"SYSTEM_SYSRULES_cardLayout"(
                    contents: {
                      #"SYSTEM_SYSRULES_sideBySideLayout"(
                        items: {
                          #"SYSTEM_SYSRULES_sideBySideItem"(
                            item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                              labelPosition: "COLLAPSED",
                              value: {
                                #"SYSTEM_SYSRULES_richTextItem_v1"(
                                  text: #"urn:appian:translation-string:v1:aa37a376-8cad-4919-9632-6d34b1c0943f",
                                  color: local!brandingMap.StandardTextColor,
                                  style: "STRONG"
                                )
                              }
                            ),
                            width: "MINIMIZE"
                          ),
                          #"SYSTEM_SYSRULES_sideBySideItem"(
                            item: #"SYSTEM_SYSRULES_checkboxField_v1"(
                              choiceLabels: local!searchCategories[recordType!CMGT_SMS_AI_CFG_Category.label],
                              choiceValues: local!searchCategories[recordType!CMGT_SMS_AI_CFG_Category.categoryId],
                              labelPosition: "COLLAPSED",
                              value: tointeger(local!selectedCategoryTemp),
                              saveInto: local!selectedCategoryTemp,
                              choiceLayout: "COMPACT",
                              marginBelow: "NONE"
                            )
                          )
                        },
                        alignVertical: "MIDDLE"
                      ),
                      #"SYSTEM_SYSRULES_sideBySideLayout"(
                        items: {
                          #"SYSTEM_SYSRULES_sideBySideItem"(
                            item: #"SYSTEM_SYSRULES_textField"(
                              label: "",
                              labelPosition: "COLLAPSED",
                              value: local!searchTextTemp,
                              saveInto: a!save(local!searchTextTemp, trim(save!value)),
                              refreshAfter: "UNFOCUS",
                              marginBelow: "NONE"
                            )
                          ),
                          #"SYSTEM_SYSRULES_sideBySideItem"(
                            item: #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                              buttons: {
                                #"SYSTEM_SYSRULES_buttonWidget_v1"(
                                  label: #"urn:appian:translation-string:v1:e3c04bbf-3297-4625-b89f-be0595880761",
                                  size: "SMALL",
                                  style: "SOLID",
                                  submit: not(local!showValidation),
                                  validate: true,
                                  /*To not display the initial space at the top when search is executed for the first time*/
                                  saveInto: {
                                    a!save(local!searchText, local!searchTextTemp),
                                    a!save(
                                      local!selectedCategory,
                                      local!selectedCategoryTemp
                                    ),
                                    if(
                                      local!showValidation,
                                      a!save(local!displayValidation, true),
                                      {
                                        a!save(
                                          local!displaySpace,
                                          local!displaySpace + 1
                                        ),
                                        a!save(local!displayValidation, false),
                                        /* Write to search history */
                                        #"SYSTEM_SYSRULES_startProcess3"(
                                          processModel: cons!CMGT_SMS_AI_PM_WRITE_SEARCH_HISTORY,
                                          processParameters: {
                                            searchCategory: local!selectedCategory,
                                            searchText: local!searchText
                                          }
                                        )
                                      }
                                    ),
                                    a!save(local!triggerRefresh, now()),
                                    a!save(
                                      {
                                        local!searchResultsForCases,
                                        local!searchResultsForTasks
                                      },
                                      null()
                                    )
                                  }
                                )
                              },
                              align: "START",
                              marginBelow: "NONE"
                            ),
                            width: "MINIMIZE"
                          )
                        },
                        alignVertical: "MIDDLE",
                        spacing: "NONE",
                        marginBelow: if(
                          and(
                            local!displayValidation,
                            local!showValidation
                          ),
                          "EVEN_LESS",
                          "STANDARD"
                        )
                      ),
                      /* Validation Message*/
                      #"SYSTEM_SYSRULES_richTextDisplayField"(
                        labelPosition: "COLLAPSED",
                        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                          text: if(
                            local!validationForSearchText,
                            #"urn:appian:translation-string:v1:db3b8c2d-1618-4dfc-a379-53320d7445ed"(
                              #"urn:appian:translation-variable:v1:9048b1ec-e0c0-498d-bf63-aee30fb0d59c": len(local!searchTextTemp)
                            ),
                            #"urn:appian:translation-string:v1:d2fbb041-7b3e-46cc-8b26-1c230ddba933"
                          ),
