                        brandingMap: local!brandingMap,
                        triggerRefresh: local!triggerRefresh
                      ),
                      /* Delete search history */
                      #"SYSTEM_SYSRULES_recordActionField_v1"(
                        actions: #"SYSTEM_SYSRULES_recordActionItem"(
                          action: #"urn:appian:record-action:v1:53049395-2855-4537-8bc6-f1a7d80fd2df/595eac66-3d52-4f57-a7bf-e122352cac16"
                        ),
