        }
      ),
      /*Search Results*/
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(contents: {}),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: #"SYSTEM_SYSRULES_cardLayout"(
              contents: {
                rule!CMGT_SMS_UTIL_TabLayout(
                  tabs: local!tabs,
                  activeTab: local!activeTab,
                  brandingMap: local!brandingMap
                ),
                a!match(
                  value: local!activeTab,
                  equals: 1,
                  then: rule!CMGT_SMS_AI_SmartSearchResultsTab_Top(
                    topResults: local!topSearchResults.data,
                    brandingMap: local!brandingMap
                  ),
                  equals: 2,
                  then: {
                    rule!CMGT_SMS_AI_SmartSearchResultsTab_Cases(
                      searchResult: local!searchResultsForCases,
                      pagingInfo: local!pagingInfoForCases,
                      selectedCaseTypes: local!selectedCaseTypes,
                      selectedStatus: local!selectedCaseStatus,
                      selectedAssignees: local!selectedCaseAssignees,
                      selectedAssignedGroups: local!selectedCaseAssignedGroups,
                      brandingMap: local!brandingMap,
                      totalCount: index(
                        local!searchResultsForCases,
                        "totalCount",
                        null
                      )
                    )
                  },
                  equals: 3,
                  then: {
                    rule!CMGT_SMS_AI_SmartSearchResultsTab_Tasks(
                      searchResult: local!searchResultsForTasks.data,
                      selectedStatus: local!selectedTaskStatus,
                      selectedTaskTypes: local!selectedTaskTypes,
                      selectedAssignees: local!selectedTaskAssignees,
                      selectedAssignedGroups: local!selectedTaskAssignedGroups,
                      pagingInfo: local!pagingInfoForTasks,
                      brandingMap: local!brandingMap,
                      totalCount: index(
                        local!searchResultsForTasks,
                        "totalCount",
                        null
                      )
                    )
                  },
                  default: {}
                )
              },
              style: local!brandingMap.ConfigBackgroundColor,
              padding: "NONE",
              showBorder: true,
              shape: "ROUNDED",

            ),
            width: "WIDE_PLUS"
          ),
          #"SYSTEM_SYSRULES_columnLayout"(contents: {})
        },
        showWhen: any(
          rule!CMGT_UTIL_IsNotBlank,
          {
            local!searchResultsForCases,
            local!searchResultsForTasks,

          }
        )
      ),
      /*Search Results - No Result found*/
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(contents: {}),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_stampField"(
                labelPosition: "COLLAPSED",
                icon: "search-minus",
                backgroundColor: local!brandingMap.WhiteColor,
                contentColor: local!brandingMap.SecondaryTextColor,
                size: "MEDIUM",
                align: "CENTER",
                marginBelow: "STANDARD",
                shape: "ROUNDED"
              ),
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: #"urn:appian:translation-string:v1:6ad92534-fd9d-4c80-b325-728dd1392a18",
