              ri!case[recordType!CMGT_Case.documents.appianDocId]
            )
          ),
          a!save(ri!case, null),
          a!save(ri!task, null),
          a!save(ri!data, null)
        },
        submit: true,
        style: "LINK",
        validate: false,
        width: local!width
      )
    },
    contentsWidth: "MEDIUM",
    showStepHeadings: true,
    showButtonDivider: true(),
    isButtonFooterFixed: true(),
    focusOnFirstInput: true(),
    showTitleBarDivider: if(
      rule!CMGT_UTIL_IsBlank(value: ri!task),
      true(),
      false()
    ),
    isTitleBarFixed: false(),
    style: "DOT_VERTICAL"
  )
)

