if(
  rule!CMGT_UTIL_IsBlank(ri!automationTasks),
  {},
  a!localVariables(
    local!data: rule!CMGT_EVAL_returnDataForConditionEvaluation(case: ri!case, task: ri!task),
    local!caseType: rule!CMGT_QR_GetCaseType(
      returnType: "SINGLE_OBJECT",
      caseTypeUuid: ri!case[recordType!CMGT_Case.caseTypeUuid],
      fields: {
        recordType!CMGT_CFG_CaseType.parentCaseType
      }
    ),
    /*Need to return the available field paths in case the paths have changed.  */
    local!allAvailableFields: rule!CMGT_returnAvailableFields(
      scope: if(
        rule!CMGT_UTIL_IsBlank(ri!task),
        "CASE",
        "TASK"
      ),
      caseTypeRecordTypeUuid: local!caseType[recordType!CMGT_CFG_CaseType.uuid],
      parentCaseTypeRecordTypeUuid: local!caseType[recordType!CMGT_CFG_CaseType.parentUuid]
    ),
    local!filteredAvailableFields: rule!CMGT_UTIL_Filter(
      records: local!allAvailableFields,
      fieldValuePairs: {
        a!map(
          field: "uuid",
          value: rule!CMGT_ENUM_Automation_ootbCaseRecordFieldsToReject(context: null)
        )
      },
      filterType: "REJECT"
    ),
    local!rulesWithPassingConditionSets: ri!automationTasks[where(
      /*loops through each task (automation rule) and checks whether or not it should be run based on the conditions*/
      a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!automationTasks, expression: a!localVariables(
            /* Only want to execute if the condition set is active         */
            local!parentConditionSet: index(
              rule!CMGT_UTIL_Filter(
                records: a!flatten(
                  fv!item[recordType!CMGT_Task.conditionSet]
                ),
                field: recordType!CMGT_ConditionSet.isActive,
                value: true()
              ),
              1,
              null
            ),
            /*loops through each child condition set and checks if the child condition sets associated with each one pass*/
            local!parentConditionSetEvaluation: #"SYSTEM_SYSRULES_forEach"(
              items: local!parentConditionSet[recordType!CMGT_ConditionSet.childConditionSet],
              expression: a!localVariables(
                local!childConditionSet: fv!item,
                /*loops through each condition on the child condition set and checks if the conditions associated pass*/
                #"SYSTEM_SYSRULES_forEach"(
                  items: local!childConditionSet[recordType!CMGT_ConditionSet.condition],
                  expression: rule!CMGT_EVAL_EvaluateConditionSetDynamic(
                    conditionSet: local!childConditionSet,
                    data: local!data,
                    availableFields: local!filteredAvailableFields
                  ).result
                )
              )
            ),
            if(
              a!isNullOrEmpty(local!parentConditionSet),
              true,
              /*checks if all the child condition sets pass depending on the operator selected*/
              a!match(
                value: tostring(
                  local!parentConditionSet[recordType!CMGT_ConditionSet.operatorCode]
                ),
                equals: "OR",
                then: or(local!parentConditionSetEvaluation),
                equals: "AND",
                then: and(local!parentConditionSetEvaluation),
                default: 0
              )
            )
          )
        )
      )
    )],
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!rulesWithPassingConditionSets[recordType!CMGT_Task.automationTaskAction],
        expression: #"urn:appian:function:v1:a:update"(
          data: fv!item,
          index: recordType!CMGT_TaskActions.automationTaskInstance,
          value: rule!CMGT_UTIL_Filter(
            records: ri!automationTasks,
            field: recordType!CMGT_Task.taskInstanceUuid,
            value: fv!item[recordType!CMGT_TaskActions.taskInstanceUuid]
          )
        )
      )
    )
  )
)

_a-0000ecf6-527e-8000-9bbc-011c48011c48_.sail_code:
a!localVariables(
  local!activeConditionSets: rule!CMGT_UTIL_Filter(
    records: ri!caseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.conditionSet],
    field: recordType!CMGT_CFG_ConditionSet.isActive,
    value: true
  ),
  local!containsMultipleChildConditionSets: length(
    a!defaultValue(
      local!activeConditionSets,
      0
    )
  ) > 1,
  #"SYSTEM_SYSRULES_forEach"(
    items: local!activeConditionSets,
    expression: {
      a!localVariables(
        local!currentChildConditionSet: fv!item,
        local!activeConditions:rule!CMGT_UTIL_Filter(
          records:local!currentChildConditionSet[recordType!CMGT_CFG_ConditionSet.condition],
          field: recordType!CMGT_CFG_Condition.isActive,
          value: true
        ),
        local!numberOfConditions: length(
          local!activeConditions
        ),
        local!containsMultipleConditions: local!numberOfConditions > 1,
        {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: "(",
            size: a!defaultValue(ri!textSize,"MEDIUM"),
            showWhen: and(
              local!containsMultipleChildConditionSets,
              local!containsMultipleConditions
            ),
            color: a!defaultValue(ri!textColor, "STANDARD")
          ),
          #"SYSTEM_SYSRULES_forEach"(
            items: local!activeConditions,
            expression: a!localVariables(
              {
                rule!CMGT_Automation_FormatCondition(
                  fieldDetails:ri!availableFields,
                  fieldPathJson:fv!item[recordType!CMGT_CFG_Condition.fieldPathJson],
                  textSize: a!defaultValue(ri!textSize,"MEDIUM"),
                  textColor:ri!textColor,
                  valueJson: fv!item[recordType!CMGT_CFG_Condition.valueJson],
                  operator: fv!item[recordType!CMGT_CFG_Condition.operator]
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: " ",
                  showWhen: and(
                    fv!index < local!numberOfConditions,
                  ),
                  size:a!defaultValue(ri!textSize,"MEDIUM"),
                  color: a!defaultValue(ri!textColor, "STANDARD")
                ),
                /*only show the in between opeartor codes if it is not the last condition within a specific condition set*/
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: UPPER(
                    local!currentChildConditionSet[recordType!CMGT_CFG_ConditionSet.operatorCode]
                  ) & " ",
                  showWhen: fv!index < local!numberOfConditions,
                  size: a!defaultValue(ri!textSize,"MEDIUM"),
                  color: a!defaultValue(ri!textColor, "STANDARD")
                )
              }
            )
          ),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: ")",
            size: a!defaultValue(ri!textSize,"MEDIUM"),
            showWhen: and(
              local!containsMultipleChildConditionSets,
              local!containsMultipleConditions
            ),
            color: a!defaultValue(ri!textColor, "STANDARD")
          ),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: " ",
            size: a!defaultValue(ri!textSize,"MEDIUM"),
            showWhen:
            or(
              and(
                local!containsMultipleChildConditionSets,
                local!containsMultipleConditions
              ),
              fv!index < length(
                a!defaultValue(
                  local!activeConditionSets,
                  {}
                )
              )
            ),
            color: a!defaultValue(ri!textColor, "STANDARD")
          ),
          /*only show the operator code if the current condition set is not the last one*/
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: UPPER(
              ri!caseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.conditionSet.operatorCode]
            ) & " ",
            showWhen: fv!index < length(
              a!defaultValue(
                local!activeConditionSets,
                {}
              )
            ),
            size: a!defaultValue(ri!textSize,"MEDIUM"),
            color: a!defaultValue(ri!textColor, "STANDARD")
          )
        }
      )
    }
  )
)

