#"SYSTEM_SYSRULES_forEach"(
  items: ri!relatedCaseMaps,
  expression: rule!CMGT_UTIL_SetRecordFields(
    record: fv!item,
    alwaysOverwrite: {
      a!map(
        field: recordType!CMGT_RelatedCaseMap.modifiedOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_RelatedCaseMap.modifiedBy,
        value: ri!initiator
      )
    },
    neverOverwrite: {
      a!map(
        field: recordType!CMGT_RelatedCaseMap.createdOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_RelatedCaseMap.createdBy,
        value: ri!initiator
      ),
      a!map(
        field: recordType!CMGT_RelatedCaseMap.isActive,
        value: true
      ),
      /*In case the ID of parent/child is on the object instead of the direct field itself, tie it to the direct field itself*/
      /*e.g. `.parentCase.caseId` is populated, so then we populate `.caseId`*/
      a!map(
        field: recordType!CMGT_RelatedCaseMap.caseId,
        value: fv!item[recordType!CMGT_RelatedCaseMap.parentCase.caseId]
      ),
      a!map(
        field: recordType!CMGT_RelatedCaseMap.relatedCaseId,
        value: fv!item[recordType!CMGT_RelatedCaseMap.childCase.caseId]
      )
    }
  )
)

