_a-0000ecac-72b0-8000-9cf2-011c48011c48_.sail_code:
a!localVariables(
  local!conditionMap: rule!CMGT_UTIL_ReturnFieldEvaluationByType(
    type: ri!condition[recordType!CMGT_Condition.valueType]
  ),
  local!field: rule!CMGT_returnAvailableFieldFromFieldPathJson(
    fieldPathJson: ri!condition[recordType!CMGT_Condition.fieldPathJson],
    availableFields: ri!availableFields
  ),
  local!parentRecordTypeUuid: local!field.parentRecordTypeUuid,
  local!data: a!match(
    value: local!parentRecordTypeUuid,
    equals: cons!CMGT_UUID_RECORD_TYPE_CASE,
    then: ri!data.caseData,
    equals: cons!CMGT_UUID_RECORD_TYPE_TASK,
    then: ri!data.taskData,
    default: a!defaultValue(
      ri!data.studioData[local!field.dataKeyName],
      ri!data.caseData
    )
  ),
  if(
    or(
      rule!CMGT_UTIL_IsBlank(local!field),
      rule!CMGT_UTIL_IsBlank(local!data)
    ),
    #"urn:appian:function:v1:a:update"(
      ri!condition,
      {
        recordType!CMGT_Condition.isMet,
        recordType!CMGT_Condition.isValid
      },
      { false, false }
    ),
    if(
      /*Checking whether the field used in condition is update/deleted in Studio*/
      ri!condition[recordType!CMGT_Condition.isValid],
      if(
        local!conditionMap.evalRule(
          condition: ri!condition,
          data: local!data,
          field: local!field
        ),
        #"urn:appian:function:v1:a:update"(
          ri!condition,
          recordType!CMGT_Condition.isMet,
          true
        ),
        #"urn:appian:function:v1:a:update"(
          ri!condition,
          recordType!CMGT_Condition.isMet,
          false
        )
      ),
      #"urn:appian:function:v1:a:update"(
        ri!condition,
        recordType!CMGT_Condition.isMet,
        false
      )
    )
  )
)

